css:
body {
background: white;
color: #444;
font-family:"Trebuchet MS","Arial";
}

table{
background:#FFF;
border-collapse: collapse;
border: 1px solid #CCC;
border-radius:4px 4px;
margin:0px 0 2px 0;
}

table table{
border:none;
}
table td{
border:none;
border: 1px dotted #EEE;
border-left:none;
border-right:none;
border-bottom: 1px solid #DDD;

padding:3px;
margin:1px;
}

table td td{
border-bottom: 1px dotted #EEE;;
}
table.chattable{
width: 100%;
}

  #curturn {
