/* ==UserStyle==
// @name         LR Vista Statusbar
// @description  LR Vista Statusbar
// @author       LRodman
// ==/UserStyle==*/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

statusbar {
  -moz-appearance: statusbar;
  border-top: 0px solid !important;
  border-left: 0px solid !important;
  border-right: 0px solid !important;
  border-bottom: 0px solid !important;
}

  
#status-bar {
  -moz-appearance: none !important;
  background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAYCAIAAABfmbuOAAAANUlEQVR42sXFMQ2AQBAEwL0//ItBBga+wAGhpSAhDDKYZmrdJqn9vHjruB8s3Y0BjCRJ/u0Dq5kZFa3ue5sAAAAASUVORK5CYII=") !important;
}

.statusbar-resizerpanel {
  -moz-box-align: end;
  -moz-box-pack: end;
  -moz-appearance: none!important;
  min-width:70px;
  background: url("data:image/png;base64,") !important;
  padding: 0px;
  border: none;
}

.statusbarpanel-iconic,  .statusbarpanel-iconic-text, .statusbarpanel-menu-iconic, 
 #statusbar-progresspanel, #security-button, #page-report-button, #statusbar-display, #gm-status, #abp-status, #downbarMini, #flvdownloader-tooltip #googlepreview-status {
  -moz-appearance: none;
  min-width:30px;
  min-height:24px;
  padding: 0px;
  border:none;
}
