/* ==UserStyle==
// @name         LR vista go-button
// @author       LRodman
// ==/UserStyle==*/
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#go-button {
  padding: 0px!important;

  margin-right:-1px;
  list-style-image: url("data:image/png;base64,")!important;
  -moz-image-region: rect(1px 24px 21px 0px)!important;
}

#go-button:hover {
  -moz-image-region: rect(1px 49px 21px 25px)!important;
}

#go-button[disabled="true"] {
  -moz-image-region: rect(1px 74px 21px 50px)!important;
}

#go-button:hover:active {
  -moz-image-region: rect(1px 99px 21px 75px)!important;
}

#urlbar > .autocomplete-history-dropmarker {
  padding: 0px!important;
  margin-left:-1px;  
  margin-bottom:-1px;
  list-style-image: url("data:image/png;base64,")!important;
  -moz-image-region: rect(0px 16px 21px 1px)!important;
}

#urlbar > .autocomplete-history-dropmarker >.dropmarker-icon{
  margin-left:0px!important;
  margin-right:0px!important;
  -moz-margin-start: 0px!important;
}
#urlbar > .autocomplete-history-dropmarker:hover,
#urlbar > .autocomplete-history-dropmarker[open="true"] {
  -moz-appearance: none!important;
  outline:none!important;
  -moz-margin-start: 0px!important;
  -moz-image-region: rect(0px 32px 21px 18px)!important;
}
#urlbar > .autocomplete-history-dropmarker[disabled="true"] {
  -moz-image-region: rect(0px 48px 21px 34px)!important;
}
#urlbar > .autocomplete-history-dropmarker:hover:active {
  -moz-image-region: rect(0px 64px 21px 49px)!important;
  margin-left:-1px!important;
}
