/* ==UserStyle==
// @name         LR Vista search bar
// @author       LRodman
// ==/UserStyle==*/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.search-go-button {
  padding: 0px!important;

  
  list-style-image: url("data:image/png;base64,")!important;
  -moz-image-region: rect(1px 24px 21px 0px)!important;
}

.search-go-button:hover {
  -moz-image-region: rect(1px 49px 21px 25px)!important;
}

.search-go-button[disabled="true"] {
  -moz-image-region: rect(1px, 74px, 21px, 50px)!important;
}

.search-go-button:hover:active {
  -moz-image-region: rect(1px, 99px, 21px, 75px)!important;
}
