css:
@-moz-document url-prefix(http://forum.knuddels)
                {

/* defaults for all links */
a {color: #2E3C9A !important; text-decoration:none !important}
a:hover {color: #6079DE !important; text-decoration:none !important}
#kft .ftoutside .content a {color: #888 !important}
#kft .ftoutside .content a:hover {color: #666 !important}
#khd ul.hdm a {color: #000 !important;
border-bottom: #4671AA solid 3px  !important}
#khd ul.hdm a:hover {color: #4671AA !important; border: none !important}
.tbody {border-color: transparent !important}

/* Stimmungs-Feature ausblenden */
  span[title="Stimmung ändern"] {display: none}

/* Smileyausrichtung in der Vorschau (Bugfix) */
  #preview_area img {vertical-align: bottom}

/* Einrückung des Willkommens-Textes */
  #content div:first-of-type {text-indent: -2.5%}
  table div {text-indent: 0 !important}

 /* Farbverlauf und Anpassung */
 /* Mozilla und Webkit */
  body, html {background:
-moz-linear-gradient(top, #D69F9A, #000) !important; background:
-webkit-linear-gradient(top, #D69F9A, #FFF) !important}
#bd3 {background-color: #D69AB3 !important}
#khd div.content {background: transparent !important}
#khd .r {background: transparent !important}
#khd .l {background: transparent !important}
#kbody {width: 95.9% !important; margin: 16px auto !important;}
#kmain .forum {font-family: Verdana,Arial,Helvetica,sans-serif !important;
font-size :13px !important}

/* ActiveText-Unterstreichung abstellen */
abbr {border-bottom: none !important}

/* Fokus auf aktuelle Seite legen */
  table.pagination td {color: #000000 !important}
  table.pagination td a {color: #4671AA !important}
  table.pagination td a:hover {color: #000000 !important}

/* Themespezifische Eigenschaften */
  .navigation {background: #E4C1BE !important; 
-moz-border-radius:13px; -khtml-border-radius:12px; border-radius:12px !important}
  .t_inner {background: #D69AB3 !important}
  .breadcrumbs, .tdheader,
  .inline_selector {background: #D69AB3 !important; color: #000000 !important;
  text-shadow: 0 1px 0 #CCC !important}
  .subjecttable {background: #D29893 !important}
  .date {color: #505362 !important}
  .footer {background: #BD6B8E !important; -moz-border-radius:13px;
  -khtml-border-radius:12px; border-radius:12px !important}
  .author-content, .markup_panel {background: #D9E0F7 !important;}
  .footer table td { padding: 3px 5px 2px 5px !important;}
  .newinforum {background: #D29893 !important}
  .forumtitle {color: #2E3C9A !important; background: #E4C1BE !important}
  .threadtotal {background: #D29893 !important}
  .posttotal {background: #D29893 !important}
  .posttime {background: #E4C1BE !important}
  .author-content {border-bottom: 1px solid #CCC !important}
  .post-content {border-left: 1px solid #CCC !important;
  background: #E4EBFF !important; text-align: justify !important;
  font-family: Calibri,Verdana,Helvetica,sans-serif !important;
font-size: 11pt !important}
  .forumdescript {text-align: justify !important}
  .post-options {display: none}
  .post_top_link {background: #D69AB3 !important; border: none !important}
  .category {background: #E4C1BE !important;
  border-bottom: 1px solid #CCC !important;
  border: 1px solid #CCC !important;
  -moz-border-radius:13px; -khtml-border-radius:12px; border-radius:12px !important}
  .t_outer {-moz-box-shadow: 0px 0px 1px #000000}
  .alt-1 {background: #DCB0AC  !important}
  .alt-2 {background: #EBD3D0 !important}
  .markup_panel_normal_button {background: #E4C1BE !important}
  .markup_panel_hover_button {background: #E4EBFF !important}
  .newintopic {background: #DDD !important}
  .alt-newintopic {background: #EEE !important}
  .topicicon {background: #DDD !important}
  .alt-topicicon {background: #EEE !important}
  .topicsubject {background: #DDD !important}
  .alt-topicsubject {background: #EEE !important}
  .topicreplies {background: #DDD !important}
  .alt-topicreplies {background: #EEE !important}
  .topicviews {background: #DDD !important}
  .alt-topicviews {background: #EEE !important}
  .topictime {background: #DDD !important}
  .alt-topictime {background: #EEE !important}
  .subjecttable {background: #E4C1BE !important;
text-shadow: 0 1px 0 white !important}
  .subjecttable .post-buttons {background: #ABB3EF !important}
  .subjecttable .post-buttons a {color: #000000 !important}
  .subjecttable  .post-buttons:hover {background: #E4C1BE !important}
  .newsubjecttable {background: #ABB3EF !important;
text-shadow: 0 1px 0 white !important}
  .newsubjecttable .post-buttons {background: #E4C1BE !important}
  .newsubjecttable .post-buttons:hover {background: #ABB3EF !important}
  .newsubjecttable .post-buttons a {color: #000000 !important}
  .new-newintopic, .new-topicicon, .new-topicsubject, .new-topicreplies,
  .new-topicviews, .new-topictime {background: #E5B9B7 !important}
  .new-alt-newintopic, .new-alt-topicicon, .new-alt-topicsubject,
  .new-alt-topicreplies, .new-alt-topicviews,
  .new-alt-topictime {background: #F1C5C3 !important}
  .controlcontainer td {padding-left: 6px !important;
  padding-right: 6px !important;}
  .author-content a {text-shadow: 0px 0px 0px !important}
  .modname {color: #305F34 !important}
 .adminname {color: #A21B11 !important}
 .globalmodname {color: #A26311 !important}
 .standouttext {color: #A21B11 !important}
 .newtotal {color: #000000 !important}
 .profile_career {background: #E4EBFF !important;
-moz-border-radius:13px; -khtml-border-radius:12px; border-radius:12px}
 .ubbcode-block {background: #D9E0F7 !important;
-moz-border-radius:13px; -khtml-border-radius:12px; border-radius:12px;
border-left: 5px solid #2E3C9A !important}
.ubbcode-header {background: #D29893 !important}
h3 {color: #222 !important; text-shadow: 0 1px 0 white !important}
h2 {color: #222 !important; text-shadow: 0 1px 0 white !important}
h1 {color: #17226E !important; text-shadow: 0 1px 0 white !important}
#help {background: #E4C1BE !important}
.contact_page_content {background: #E4C1BE !important;
text-align: justify !important}
#help .help-gray {background: #EEE !important}
table.mypreftable th {background: #E4C1BE !important}
table.mypreftable td {background: #E4EBFF !important}
.tab_grippy_sel {background: #E4C1BE !important;
border: 0px solid #CCC !important}
input{color:#000 !important; }
input:hover {color: #17226E !important;}
input:not([type="submit"]):not([type="reset"]):not([type="button"]
):not([type="checkbox"]):not([type="radio"]):focus, textarea:focus {
-moz-box-shadow: 0px 0px 3px 3px rgba(85,165,243,0.5) !important;}
#kniggewrap .kniggebox {background-color: #E4C1BE !important;
border: 1px solid #CCC !important;
-moz-border-radius:13px; -khtml-border-radius:12px; border-radius:12px}
#kniggewrap .colorhead {color: #505362 !important}
.popup_menu_header  {background-color: #E5B9B7 !important;
border: 1px solid #CCC !important}
.popup_menu_content {background-color: #EEE !important;
border: 1px solid #CCC !important}
.popup_menu_highlight {background-color: #D9E0F7 !important;
border: 1px solid #CCC !important}

/* Neueste Änderungen */
 a:focus {outline: none !important}

  /* Divfixes */
#kft .ftbox .l {width:29px;background: transparent  !important}
#kft .ftbox .r {width:29px;background: transparent !important;
position:absolute; right:0;}
#kft .ftbox div.content{background:transparent !important}


}
