css:
@-moz-document domain(tumblr.com) {
body {



    background: url('http://novelnovice.files.wordpress.com/2011/10/hunger-games-movie-wp_katniss-and-peeta.jpg') center right #fff repeat fixed !important;



  }



ol#posts .post.new_post .new_post_label {

display:none !important;

}


.dashboard_nav_item{border:1px solid #5d5450!important; background:#5d5450!important; margin-bottom:5px;margin-top:5px;}
.dashboard_nav_item a{color:#fff !important;background-image: none!important;border:0px!important;}
.dashboard_nav_item a:hover{color:#5d5450 !important;}

.nav_item{color:#5d5450 !important;background-image: none!important;border:0px!important;}

.dashboard_nav_title{color:#5d5450!important;
font-size:20px!important;
font-family:georgia!important;
font-style:italic;}

#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_footer {

border-top:0px!important;

border-top-width:0px!important;}



#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_header {

border-bottom:0px!important;

border-top-width:0px!important;}



#right_column #dashboard_switch_blog_menu_wrapper #dashboard_switch_blog_menu a#dashboard_switch_blog_menu_header #dashboard_switch_blog_arrow_and_divider {

border-top:0px!important;}


#dashboard_nav_following{
border-left:0px!important;
border-right:0px!important;
border-top:0px!important;
box-shadow:inset 0 0 0px!important ;
-moz-box-shadow:inset 0 0 0px!important; 
background:url('http://i.imgur.com/G5BLT.jpg')!important;
display: inline-block;
padding: 5px 10px 6px;
color: #fff;
text-decoration: none;
font-family: Arial, Helvetica, Helvetica Neue, Verdana, sans-serif;
font-size:12px!important;
font-weight: bold!important;
line-height: 1;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
-moz-box-shadow: 0 1px 3px #999;
-webkit-box-shadow: 0 1px 3px #999;
text-shadow: 0 -1px 1px #5d5450;
border-bottom: 1px solid #5d5450;
position: relative;
cursor: pointer;
}

.post_info{border-bottom: 3px solid #FFf!important;}


#dashboard_nav_following span .icon{display:none;}


#dashboard_nav_following .icon dude span{display:none!important;}

.dashboard_switch_blog_menu_item{color:#5d5450!important; background:#5d5450 !important;}

ul .dashboard_subpages{color:#5d5450 !important;}

ol#posts li blockquote,

body.mceContentBody blockquote {

margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;

border-color: #5d5450 !important;

}

ol#posts li blockquote blockquote,

body.mceContentBody blockquote blockquote {

border-color: #5d5450 !important;

}

ol#posts li blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote {

border-color: #5d5450 !important;
}

ol#posts li blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote {

border-color: #5d5450 !important;
}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i.imgur.com/slCXD.jpg') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}



ol#posts li blockquote blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote blockquote {

border-color: #5d5450!important;
}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: transparent!important;

  }

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: #5d5450 !important;


    -webkit-border-radius: 20px;



    -moz-border-radius: 20px;



    border-radius: 20px;



    margin: auto;



    padding: 20px 20px;



  }

h1 {
color: #fff;
}

.no_posts_found, h1.dashboard_header {

color: #fff;
}

img#content_top, img#content_bottom {

display: none;

}

.notification a {

color:#fff;

border: 0px!important;



}



#auto_pagination_loader img { display: none; }


#auto_pagination_loader {

	text-shadow: #5d5450 2px 1px 0px;


background: transparent!important;



border: 0px!important;



font-weight: bold;



font-family: Georgia, serif;



font-style: italic;



color: #0859bd;



}

#right_column a#dashboard_switch_blog_menu_current  {

color:#fff !important;


  }


#right_column .dashboard_nav_item {

    background-attachment: initial;



    background-position-x: 50%;



    background-position-y: 100%;



    background-origin: initial;



    background-clip: initial;



    background-color: initial;



}


#right_column .dashboard_nav_item #hide_radar {



background-image: none;



background-repeat-x: no-repeat;



background-repeat-y: repeat;



background-attachment: initial;



background-position-x: 0%;



background-position-y: 0%;



background-origin: initial;



background-clip: initial;



background-color: transparent;



}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {


background: #fff;



}

#nav .nav_item.active {

    background: #fff!important;

color:#5d5450!important;

  }

.nav_item_nipple a{color:#5d5450!important;}

#nav .nav_item .nav_item_nipple  {
    background: #5d5450!important;

  }

ol#posts li.notification.alt {

    background-color: #251b19 !important;
}

ol#posts li.notification.first_notification {

    background-color: #5d5450!important;

}
ol#posts li.notification {

    background-color: #5d5450 !important;

    border-bottom: 0px solid #251b19 !important;

}

ol#posts li.notification.last_notification {

color:#fff;
text-decoration:none;

    border-bottom: 1px solid #5d5450;
}

ol#posts li.notification.last_notification {

color:#fff;
text-decoration:none;

    border-bottom: 1px solid #5d5450;
}
ol#posts li.post .permalink {
    background: #5d8ea3;

}
#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: #fff;
border: solid #fff;
}

ol#posts li.post .post_avatar{
border: 2px solid #FFf;

}

#edit_post_controls .advanced_option_divider{
height:0px!important;
background:transparent!important;
border-bottom:0px!important}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {

border-top: 1px solid #5d5450;
border-bottom: 1px solid #5d5450;

}
#right_column a#dashboard_switch_blog_menu_current:hover {
background: transparent!important;
}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {
background: none;

}

form.dashboard_options_form {
background-color: #5d5450;
border-bottom: 1px solid #FFf;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #fff;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;

}
form.dashboard_options_form .option_container {

border-top: 1px solid #fff;

}
.no_posts_found, h1.dashboard_header {

color: #fff;}
}
