css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org")
{
	html
	{
	
		background: black !important;
	}
	body
	{
	
		background: black !important;
		color: #fff !important;
		margin: 0 auto !important;
		padding: 0;

                font-family: Segoe UI !important;
	}
	form
	{
		background: #101010;
		border-left: 1px solid #222;
		border-right: 1px solid #222;
		margin: 0 auto !important;
		max-width: 1000px !important;
		padding: 25px 5px 5px 5px;
		width: 85% !important;
	}
	#recent-images li
	{
		background: #1c1c1c !important;
	}

	#navtop
	{
		background: #101010 !important;
		border-bottom: 1px solid #1c1c1c;
		color: #444 !important;
		font: 12px Calibri !important;
		padding: 0;
		padding: 0px !important;
		position: static;
		text-align: center !important;
		top: 0;
		width:100% !important;
		z-index: 9;
	}


HTML > BODY > TABLE > TBODY{
display:none !important;
}

.replyhl{
background:#111!important;
color: #ccc !important;
border-color:#333 !important;
}
	div.logo
	{
        
		left: -700px;
		position: absolute;
		right: 0;
		visibility: hidden;
	}
	div.logo font span
	{
		color: #fff;
		visibility: visible;
	}
	center font[color="red"]
	{
		display: none !important;
	}

	BODY TABLE[width="100%"]
	{
		margin-bottom: -1px;
		padding: 0 !important;
		padding-left: 10px !important;
		padding-right: 10px !important;
	}
	BODY TABLE[width="100%"] TH
	{
		padding: 8px !important;
	}
	.postarea
	{
		padding: 0 !important;
	}
	.postarea form div
	{
		border: none;
	}

	.postarea table
	{
		margin-bottom: -10px;
		margin-top: -10px;
	}
	input, input[type="submit"], input[type="button"], select, button, textarea
	{
		background: #000 !important;
		border: 1px #222 solid !important;
		color: #aaa !important;
	}


	img#recaptcha_reload
	{
		background-image: url(data:image/png;base64,) !important;
		border: none !important;
		height: 0px !important;
		margin: 0 !important;
		padding-top: 18px !important;
		width: 25px !important;
	}
	img#recaptcha_switch_audio
	{
		background-image: url(data:image/png;base64,) !important;
		border: none !important;
		height: 0px !important;
		margin: 0 !important;
		padding-top: 15px !important;
		width: 25px !important;
	}
	img#recaptcha_whatsthis
	{
		background-image: url(data:image/png;base64,) !important;
		border: none !important;
		height: 0px !important;
		margin: 0 !important;
		padding-top: 16px !important;
		width: 25px !important;
	}
	#recaptcha_logo, #recaptcha_tagline
	{
		display: none !important;
	}
	a
	{
		border: 0 !important;
		color: #ccc !important;
		text-decoration: none !important;
	}
	a:hover
	{
		color: #f00!important;
	}
	a.quotejs
	{
		color: #88aaaa!important;
	}
	a:hover.quotejs
	{
		color: #00dddd!important;
	}
	#navtop a
	{
		color: #ffffff !important;
	}
	#navtop a:hover
	{
		background: #000000 !important;
		color: #999999 !important;
	}
	th
	{
		background: #222 !important;
		padding: 10px !important;
	}
	td.reply
	{
		background-color: #1c1c1c !important;
		border: 0 !important;
		border-color: #222222 !important;
		clear: both;
		color: #FFF !important;
		width: 900px !important;
	}
	td.replyhl
	{
		background-color: #111811 !important;
		border: 0px !important;
		border-color: #ffffff;
		color: #FFF !important;
		width: 900px !important;
	}
	.pages
	{
		background: #222 !important;
		border: 0 !important;
		text-align: center !important;
		width: 100% !important;
	}
	.pages td, .pages td b
	{
		color: #666 !important;
	}

	.postertrip
	{
		color: #ff2222 !important;
	}

	a img
	{
		border: 1px solid #333 !important;
		clear: both !important;
		margin: 0 10px 10px 2px !important;
	}
	.deletebuttons input:not([type="checkbox"])
	{
		display: inline !important;
		width: 130px !important;
	}
	.deletebuttons input[type="checkbox"]
	{
		position: relative;
		top: 3px;
	}
	.quotejs
	{
		visibility: visible;
	}
	.replytitle
	{
		color: #6666ff !important;
	}
	.deletebuttons
	{
		visibility: hidden;
	}

	.omittedposts
	{
		color: #777 !important;
		margin-left: 10px;
		visibility: visible !important;
	}

	body form blockquote
	{
		margin: 8px 2px 8px 20px;
	}
        .filesize{
        font-size: 10px !important;

        }
	td blockquote
	{
		background: none !important;
		margin: 0 !important;
		padding: 5px !important;
	}
	form td
	{
		padding: 2px 6px !important;
	}
	 #hd, #ft, hr, td small, .doubledash, #footer, .rules, center font h3, center font small, center small font
	{
		display: none !important;
	}
	img[class*="4chan_ext"]
	{
		border: 0 !important;
		display: inline;
		margin: 0 !important;
	}
	div[id*="twi_table"]
	{
	}
	.reply
	{
		background: #1C1C1C !important;
		border: 1px solid #444 !important;
		color: #EEE !important;
		font: 12px Calibri !important;
	}
	.postername , .postertrip, .filetitle, .commentpostername
	{
		font: 12px Calibri !important;
	}
	.reply[id*="ext"]
	{
		padding: 1px !important;
	}
	div[id*="twi_table"] .postblock
	{
		font: 12px Calibri !important;
		padding: 3px !important;
	}
	form[id*="4chan_qr_form"]
	{
		padding: 5px;
		width: auto !important;
	}
	span[class*="4chan_win_header_buttons"] img
	{
		display: inline;
		padding: 1px;
	}
	img
	{
		display: block;
		margin: 0 auto;
	}
	img[src*="http://static.4chan.org/"]
	{
		display: none !important;
	}
	#backwash_tooltip
	{
		background-color: #1c1c1c !important;
	}
	

/* Post Form Cleanup */
	.postarea .rules,
	.postarea small,
	.postarea td[style="padding: 4px 7px 12px;"],
	.postarea td[style="padding: 4px 7px 12px 7px;"],
	.postarea td[style="padding: 10px 7px 7px 7px;"],
	.postarea td[style="padding: 18px 7px 18px 7px;"],
	.postarea td[style="padding: 10px 7px 7px;"],
	.postarea td[style="padding: 18px 7px;"],
	.postarea #recaptcha_logo,
	.postarea #recaptcha_tagline,
	.postarea .postblock {
		display: none !important;
	}
/* Post Form */
	.postarea form:not(#newform) {
		max-width: 307px !important;
		min-width: 307px !important;
		padding-left: 5px;
	}

	.postarea form:first-child {
		position: fixed;
		top: auto;
		right: -270px;
		left: auto;
		bottom: 40px;
		z-index: 5;
		margin: 0 !important; 
		-moz-transition: all 1s ease-out .6s !important;
                opacity: 0 !important;
	}

	.postarea form:first-child:hover {
		right: 0;
		-moz-transition: all 1s  !important;
                opacity: 1 !important;
	}

	#com_submit,
	.postarea input[type=file] > input[type=button],
	.postarea input[name="upfile"] {
		font: 200 7pt "Verdana" !important;
	}

	.postarea input[type=text],
	.postarea input[type=password],
	.postarea input[type=file][name="upfile"] > input[type=text],
	.postarea input[type=file] > input[type=button],
	.postarea textarea.inputtext	{
		-moz-appearance: none;
		-webkit-appearance: none;
		border-color: #aaa;
		border-width: 1px !important;
		border-style: solid !important;
		width: 302px !important;
		height: 19px !important;
		padding: 1px 0 1px 0 !important;
		z-index: 1;
	}

	.postarea input[type=file] {
		z-index: 1;
	}

	body .postarea input[name="sub"],
	.postarea input[type=file][name="upfile"] > input[type=text] {
		width: 251px !important;
		margin: 0 1px 0 0 !important;
		vertical-align: middle;
	}

	.postarea #com_submit,
	.postarea input[type=file] > input[type=button] {
		width: 50px !important;
		height: 19px !important;
		margin: 0 0 0 0 !important;
		padding: 0 !important;
		vertical-align: middle;
	}
	
	.postarea input[type="file"]::-webkit-file-upload-button,
	#quickrep input[type="file"]::-webkit-file-upload-button {
		-moz-appearance: none;
		background: transparent;
		border-color: #aaa;
		border-width: 1px !important;
		border-style: solid !important;
		height: 19px !important;
		padding: 0 !important;
	}

	.postarea textarea.inputtext,
	textarea#message {
		height: auto !important;
		font-size: 10pt !important;
	}

	.postarea textarea#message {
		width: 302px !important
	}

	.postarea input[type=submit] {
		-moz-appearance: none;
		border-color: #aaa;
		border-width: 1px !important;
		border-style: solid !important;
	}

	.postarea td, .reply td {
		z-index: 0;
		padding: 0 !important;
	}
/* Spoiler Image Checkbox */
	html>body>.postarea>form>table>tbody>tr:nth-of-type(3)>td:nth-of-type(3) {
		font-size: 0 !important;
	}
	
	html>body>.postarea>form>table>tbody>tr:nth-of-type(3)>td:nth-of-type(3)>label {
		position: fixed;
		font-size: 10pt !important;
		top: auto;
		left: auto;
		bottom: 63px;
	}
	
	html>body>.postarea:hover>form>table>tbody>tr:nth-of-type(3)>td:nth-of-type(3)>label,
	div.postarea:hover #autonoko {
		right: 10px;
		-webkit-transition: right .3s linear;
		-moz-transition: right .3s linear;
		-o-transition: right .3s linear;
	}
	
	html>body>.postarea:not(:hover)>form>table>tbody>tr:nth-of-type(3)>td:nth-of-type(3)>label,
	div.postarea:not(:hover) #autonoko {
		right: -240px;
		-webkit-transition: right .3s ease-in-out 3s;
		-moz-transition: right .3s ease-in-out 3s;
		-o-transition: right .3s ease-in-out 3s;
	}
/* Captcha */
	.postarea td.recaptcha_image_cell,
	td[style="padding-left: 7px;"] {
		padding: 0 !important;
	}

	.postarea .recaptcha_image_cell>center>#recaptcha_image {
		border-color: #aaa;
		border-width: 1px !important;
		border-style: solid !important;
	}

	.postarea .recaptchatable #recaptcha_response_field {
		border-color: #aaa;
		border-width: 1px !important;
		border-style: solid !important;
	}

	.postarea .recaptcha_input_area {
		padding: 2px 0 0 0 !important;
	}

	.postarea tr[height="73"], tr[height="73"] td {
		height: 59px !important;
	}
}
@-moz-document url(http://www.4chan.org/)
{
	HTML > BODY
	{
		color: #dddddd;
		padding: 10px 0px !important;
	}
	#hd, #close-announce
	{
		background: #222222 !important;
		color: #dddddd;
		display: none !important;
	}
	.boxbar, #preview-tooltip-nws H2, #preview-tooltip-ws H2
	{
		background: #222222 !important;
		border-bottom: 1px solid rgba(0,0,0,.2) !important;
		color: #bb0000 !important;
		color: #dddddd;
		text-align: center !important;
	}
	#announce .boxcontent
	{
		background: #222222 !important;
		color: #dddddd;
		text-align: center !important;
	}
	.box-outer, #preview-tooltip-nws, #preview-tooltip-ws
	{
		background: #222222 !important;
		border-width: 12px !important;
		color: #dddddd;
	}
	.menubutton
	{
		background: #222222 !important;
		background-image: none !important;
		color: #dddddd;
	}
	#filter-button, #option-button
	{
		letter-spacing: 0px !important;
		margin: 0 !important;
		padding: 0px !important;
	}
	#filter-button:hover, #option-button:hover
	{
		opacity: .7 !important;
	}
	.yui-menu-shadow
	{
		display: none !important;
	}
	#recent-images LI
	{
		background: #222222 !important;
		background: none !important;
		color: #dddddd;
	}
	#boards .column
	{
		background: #222222 !important;
		color: #dddddd;
		text-transform: lowercase !important;
		width: 12em !important;
	}
	.column > H3, .p_postername
	{
		color: #bb0000 !important;
	}
	.box-outer.right-box, .box-outer.left-box,.box-outer.top-box
	{
		background: #222222 !important;
		color: #dddddd;
	}
	.box-outer, #preview-tooltip-nws, #preview-tooltip-ws
	{
		background: #222222 !important;
		border: 5px solid #101010 !important;
		color: #dddddd;
	}
	#preview-tooltip-nws, #preview-tooltip-ws
	{
		background: #222222 !important;
		color: #dddddd;
		padding: 5px !important;
	}
	.boxcontent img
	{
		background: #222222 !important;
		color: #dddddd;
		margin: 0 auto !important;
	}
	
}
@-moz-document url-prefix(http://www.4chan.org/framesnav)
{
	BODY
	{
		font: 11px verdana !important;
	}
	#logo
	{
		background: none !important;
		height: 0 !important;
		padding-bottom: 5px !important;
	}
	.plus
	{
		background: #DDD !important;
		border: none !important;
	}
	H2
	{
		background: #1C1C1C !important;
		color: #FFF !important;
	}
	LI:hover
	{
		text-decoration: none !important;
	}
	li
	{
		color: #6666ff !important;
		padding-left: 5px !important;
		padding-right: 5px !important;
	}
}
