css:
@-moz-document domain("bing.com") {
/* bing.com */
body { 
	background: none;
}
h3 {
	font-size: 150%;
}
h3 a, .ans2 h2 div span span a:hover, .ans h2 a {
	color: #11c;
	text-decoration: none;
}

.mmrel, .sw_tlif, .sa_uc, .sb_adsWv2, .sp_pss, #sa_bop, .sw_aal, .sw_aar, .sw_aa1, .sw_aa2, .sw_aa3, .sw_aa4,
	#sc_sst, #sidebar, #sw_hdr #sch_scopes, #sw_hdr .sw_tm, #sw_meCtrl .sw_meIc { 
	display: none !important; 
}
.sw_a span, .ss_triangle {
	display: inline-block;
	margin-left: 3px;
}
.sw_a .sw_aa {
	border: none;
	margin: 0;
	padding: inherit;
}
.sw_aat {
	border: none;
	padding-top: 6px;
}
.sw_b input {
	padding: .32em .32em .35em .55em;
}
.sw_b .sw_qbtn {
	position: relative; top: 1px;
}
.sw_logo {
	background-image: url(data:image/jpeg;base64,%3D%3D);
	background-size: auto 100%;
	background-position: 21px -8px;
	margin-right: 64px;
	height: 60px;
	text-indent: -9999px;
}
.sb_ph {
	opacity: .3;
}
.sb_pt {
	border: none;
}
.sb_ptn {
	visibility: hidden;
}
.sb_results p, .sb_results .sb_meta {
	font-size: 110%;
	line-height: 1.5;
	margin: 2px 6px 0 0;
}
.sb_results .sb_meta {
	margin-top: 0;
}
.sw_tc {
	background-color: #333;
	background-image: none;
	height: 2em;
}
.sw_t ul {
	float: none;
	padding-top: 3px;
}
.vs_cont .vs_trd {
	border-left: 1px solid #ccc;
}

#sg_root .sg_u, #sg_root .sg_g { 
	border: none; 
}
#sw_aside { 
	opacity: .4;
	
	-webkit-transition: all .5s ease; /* Safari 3.2+, Chrome */
   	   -moz-transition: all .5s ease; /* FF 4+ */
    	-ms-transition: all .5s ease; /* IE 10 */
     	 -o-transition: all .5s ease; /* Opera 10.5+ */
        	transition: all .5s ease; 
}
#sw_aside:hover, #sw_aside:focus { 
	opacity: .8; 
}
#sw_hdr { 
	z-index: 1; 
}
#sw_hdr .sw_box { 
	padding-top: 70px; 
}
#sw_im { 
	background: none; 
}
}

@-moz-document regexp("bing.com/images") {
.filter {
        margin-top: 7px;
}

#sw_aside { 
	padding-top: 14px;
}
#sw_canvas {
	padding-top: 2.5em;
}
#sw_content {
	margin-top: 100px;
	padding-top: 0;
}
#sw_hdr {
	background: #fff;
	z-index: 5;	
}
#sw_main { 
	margin-top: 21px; 
}
#sw_width .sb_pt {
	border: none; 
}
#sw_width #sw_abar { 
	padding-bottom: 6px; 
	left: -1px;
	top: 113px; 
}
#vm_cb { 
	padding: .7em 1em .6em .6em; 
}
#vm_st { 
	display: none; 
}
}

@-moz-document regexp("bing.com/images/search\\?.*detail") {
#sw_width #sw_abar { 
	border-bottom: 1px solid #ccc; 
	box-shadow: 0 6px 6px rgba(0,0,0,.2); 
}
}
