css:
@-moz-document url-prefix(http://forum.knuddels)
                {

/* Neue Schriftart- und größe fürs Forum (zum Entfernen folgende Zeile löschen)  */
.post-content {font-family: Calibri,Verdana,Helvetica,sans-serif !important;
font-size: 11pt !important}

/* defaults for all links */
a {color: #AB030E !important; text-decoration:none !important}
a:hover {color: #DD0312 !important; text-decoration:none !important}
#kft .ftoutside .content a {color: #757575 !important}
#kft .ftoutside .content a:hover {color: #575757 !important}
#khd ul.hdm a {color: #000 !important;
border-bottom: #AB030E solid 3px  !important}
#khd ul.hdm a:hover {color: #AB030E !important; border: none !important}

/* Stimmungs-Feature ausblenden */
  span[title="Stimmung ändern"] {display: none}
  
/* Stimmungs-Smileys vollständig ausblenden  */
img[src*='confused.gif'] {visibility: hidden}
img[src*='crying.gif'] { visibility: hidden}
img[src*='mad.gif'] { visibility: hidden}
img[src*='happy.gif'] { visibility: hidden}
img[src*='embarassed'] { visibility: hidden}
img[src*='sleepy.gif'] { visibility: hidden}
img[src*='content.gif'] { visibility: hidden}
img[src*='sick.gif'] { visibility: hidden}
img[src*='sad.gif'] { visibility: hidden}
img[src*='shocked.gif'] { visibility: hidden}

/* Smileyausrichtung in der Vorschau (Bugfix) */
  #preview_area img {vertical-align: bottom}

/* Einrückung des Willkommens-Textes */
  #content div:first-of-type {text-indent: -2.5%}
  table div {text-indent: 0 !important}

 /* Layout-Anpassung */
#kbody {width: 98.9% !important; margin: 16px auto !important;}
#kmain .forum {font-family: Verdana,Helvetica,sans-serif !important;
font-size: 13px !important}
.tbody {border-color: transparent !important}
#khd div.hr {background: transparent !important}

/* ActiveText-Unterstreichung abstellen */
abbr {border-bottom: none !important}

/* Fokus auf aktuelle Seite legen */
  table.pagination td {color: #000 !important; font-weight: bold !important}
  table.pagination td a {color: #A80011 !important}
  table.pagination td a:hover {color: #000 !important}

/* Navigation und Pop-up-Menüs */
  .navigation {background: #000 !important; 
border-radius: 0.34em; -khtml-border-radius: 0.34em}
 .navigation a {color: #B3B3B3 !important}
 .navigation a:hover {color: #FFF !important;
 text-shadow: 0 1px 0 #000 !important}
 .popup_menu_header  {background-color: #000 !important;
border: 1px solid #FFF !important; color: #FFF !important}
.popup_menu_content {background-color: #000 !important;
border: 1px solid #FFF !important; width: 165px}
.popup_menu_content a {color: #B3B3B3 !important}
.popup_menu_highlight {background-color: #000 !important;
border: 1px solid #FFF !important}
.popup_menu_highlight a {color: #FFF !important}
 
 /* Footer */
   .footer {background: #000 !important;
  -khtml-border-radius: 0.34em; border-radius: 0.34em !important}
   .footer a {color: #B3B3B3 !important}
  .footer a:hover {color: #FFF !important}
  .footer a:active {color: #FFF !important}
  .footer table td { padding: 3px 5px 2px 5px !important;}
  
  /* Post-Eignschaften */
   .posttotal {background: #DEDEDE !important}
  .posttime {background: #D2D2D2 !important}
  .post-content {border-left: 1px solid #FFF !important;
  background: #FFF !important; text-align: justify !important}
    .post-options {background: #FFF !important; border-left: none !important}
  
  /* Post-Links */
   .post_top_link {background: #E6E6E6 !important; border: none !important}
  .post_top_link a {font-weight: bold !important; color: #242424 !important}
  .post_top_link a:hover {color: #AB030E !important;
  background-color: #FFF; -khtml-border-radius: 0.34em;
  border-radius: 0.34em !important}
  
  /* Thread- und Forum-Eigenschaften */
   .threadtotal {background: #DEDEDE !important}
     .newinforum {background: #DEDEDE !important}
  .forumtitle {color: #000 !important; background: #D2D2D2 !important}
  .forumdescript {text-align: justify !important}
    .category {background: #D2D2D2 !important;
  border-bottom: 1px solid #FFF !important;
  border: 1px solid #FFF !important;
-khtml-border-radius: 0.34em; border-radius: 0.34em !important}
  
 /* Date */
   .date {color: #575757 !important}
   
  /* Alt */
  .alt-1 {background: #DEDEDE  !important}
  .alt-2 {background: #EBEBEB !important}
 
 /* Inner */
  .t_inner {background: transparent !important}
  
 /* Mark-up-Panel */
   .markup_panel_normal_button {background: #D2D2D2 !important}
  .markup_panel_hover_button {background: #FFF !important}
  
 /* Subjecttables */
 .post-buttons {text-shadow: none !important; -khtml-border-radius: 0.34em;
 border-radius: 0.34em !important; color: #000 !important; border-color: #242424 !important}
  .subjecttable {background: #B8B8B8 !important;
text-shadow: 0 1px 0 #D9D9D9 !important}
  .subjecttable .post-buttons {background: #FFF !important}
  .subjecttable .post-buttons a {color: #000 !important}
  .subjecttable  .post-buttons:hover {background: #FFB3B3 !important}
  .newsubjecttable {background: #FFB3B3 !important;
  text-shadow: 0 1px 0 #D9D9D9 !important}
  .newsubjecttable .post-buttons {background: #FFF !important}
  .newsubjecttable .post-buttons:hover {background: #B8B8B8 !important}
  .newsubjecttable .post-buttons a {color: #000 !important}
  
  /* Topics */
    .newintopic {background: #DEDEDE !important}
  .alt-newintopic {background: #D2D2D2 !important}
  .topicicon {background: #DEDEDE !important}
  .alt-topicicon {background: #D2D2D2 !important}
  .topicsubject {background: #DEDEDE !important}
  .alt-topicsubject {background: #D2D2D2 !important}
  .topicreplies {background: #DEDEDE !important}
  .alt-topicreplies {background: #D2D2D2 !important}
  .topicviews {background: #DEDEDE !important}
  .alt-topicviews {background: #D2D2D2 !important}
  .topictime {background: #DEDEDE !important}
  .alt-topictime {background: #D2D2D2 !important}
  .new-newintopic, .new-topicicon, .new-topicsubject, .new-topicreplies,
  .new-topicviews, .new-topictime {background: #FF9999 !important}
  .new-alt-newintopic, .new-alt-topicicon, .new-alt-topicsubject,
  .new-alt-topicreplies, .new-alt-topicviews,
  .new-alt-topictime {background: #FFB3B3 !important}
  
 /* Special User Colours */
  .modname {color: #076001 !important}
  .modname:hover {color: #0B9201 !important}
 .adminname {color: #000 !important}
 .adminname:hover {text-shadow: 0 1px 0 #FFF !important}
 .globalmodname {color: #AC4B02 !important}
 .globalmodname:hover {color: #DE6202 !important}
 .subforum_moderators {font-weight: bold !important}
 div[class="small subforum_moderators"] a {color:#076001 !important}
 div[class="small subforum_moderators"] a:hover {color:#0B9201 !important}
 div.forum_extras a {color: #076001 !important}
 div.forum_extras a:hover {color: #0B9201 !important}
 span[class="subforum_moderators"] a {color:#076001 !important}
 span[class="subforum_moderators"] a:hover {color:#0B9201 !important}
 
 /* Profile Career */
  .profile_career {border-color: #242424 !important; background: #076001 !important;
  color: #FFF; -khtml-border-radius: 0.34em;
  border-radius: 0.34em !important}
  
 /* Breadcrumbs, Selectors and Controlcontainers */
  .breadcrumbs, .tdheader,
  .inline_selector {background: #D2D2D2 !important; color: #000 !important;
  text-shadow: 0 1px 0 #FFF !important}
  .controlcontainer td {padding-left: 6px !important;
  padding-right: 6px !important}
  .controlcontainer .t_standard a, .controlcontainerbottom .t_standard a
{font-weight: bold !important}
  
  /* Standout-Text */
   .standouttext {color: #AB030E !important}
  
  /* Hnumber */
h3 {color: #222 !important; text-shadow: 0 1px 0 #FFF  !important}
h2 {color: #222 !important; text-shadow: 0 1px 0 #FFF  !important}
h1 {color: #AB030E !important; text-shadow: 0 1px 0 #FFF !important}

/* Newtotal, Signature, Pollcolor */
.signature {color: #575757 !important}
.pollcolor {background-color: #AB030E !important}
.newtotal {color: #076001 !important}

  /* Author-Eigenschaften */
.author-content a {text-shadow: 0px 0px 0px !important}
 .author-content, .markup_panel {background: #E6E6E6 !important}

/* Suche-Highlighting */
.search_highlight {background-color: #FDB37C !important}

/* Kontaktseite */
.contact_page_content {background: #D2D2D2 !important;
text-align: justify !important}

/* Help */
#help {background: #D2D2D2 !important}
#help .help-gray {background-color: #E4BFBF !important}

/* Knigge */
#kniggewrap .colorhead {color: #575757 !important}
#kniggewrap .kniggebox {background-color: #E6E6E6 !important;
border: 1px solid #FFF !important; -khtml-border-radius: 0.34em; border-radius: 0.34em !important}

/* Mypreftable */
table.mypreftable th {background: #D2D2D2 !important}
table.mypreftable td {background: #DEDEDE !important}

/* Tabgrip, kmain */
.tab_grippy_sel {background: #D2D2D2 !important;
border: 0px solid #FFF !important}
#kmain, th {text-align: justify !important}

/* Rote Farbe zu Knuddelsrot */
font[color=red] {color: #AB030E !important}

/* Firefox-Focus-Bugfix */
 a:focus {outline: none !important}
 
/* .bbCode */
.bbcodecomment {color: #076001 !important}
.bbcodekeyword {color: #610037 !important}
.bbcodestring {color:  #AD9F00 !important}
.bbcodehtml {color: #0071C7 !important}
.ubbcode-header {background: #DBDBDB !important}
.ubbcode-block {background: #E8E8E8 !important;
-khtml-border-radius: 0.34em; border-radius: 0.34em;
border-left: 4px solid #AB030E !important; border-bottom: 2px solid #DBDBDB !important}
.ubbcode-body {font-family: Georgia, Helvetica, Serif !important; font-style: italic !important; 
background:url(http://s14.directupload.net/images/120326/hm3a4teq.png) no-repeat left top;
padding-left: 50px !important; padding-bottom: 18px !important; color: #242424 !important;
text-shadow: 0 1px 0 #FFF !important}

/* Firefox-Forum-Fade-In */
html {-moz-animation: linear .67s; -moz-animation-name: htmlfadein} 
@-moz-keyframes htmlfadein {from {opacity: .67 !important;} to {opacity: 1 !important;}}

/* FORMULARFELDER */
/* Form-and-Input-Default */
html:root > body input[type="file"], 
html:root > body input[type="text"], 
html:root > body input[type="password"], 
html:root > body input:not([type]), 
html:root > body textarea 
{margin: 1px !important; -moz-appearance: none !important;
background-color: #000 !important; color: #FFF !important;
border: 1px solid #000 !important; border-radius: 0.34em !important;
-webkit-border-radius: 0.34em !important}

html:root > body button, 
html:root > body input[type="button"], 
html:root > body input[type="reset"], 
html:root > body input[type="submit"] 
{margin: 1px !important; -moz-appearance: none !important;
background-color: #242424 !important; color: #FFF !important;
font-weight: bold !important; border: 1px solid #000 !important;
border-radius: 0.34em !important; -webkit-border-radius: 0.34em !important}

html:root > body select 
{-moz-appearance: none !important; background-color: #000 !important;
border: 1px solid #000 !important; font-weight: bold !important;
color: #B3B3B3 !important; border-radius: 0.34em !important;
-webkit-border-radius: 0.34em !important}

html:root > body input:not([type]) 
{-moz-appearance: none !important; border: 1px solid #000 !important;
color: #FFF !important; border-radius: 0.34em !important;
-webkit-border-radius: 0.34em !important}

/* Form-Hover */
html:root > body input[type="file"]:hover, 
html:root > body input[type="text"]:hover, 
html:root > body input[type="password"]:hover, 
html:root > body textarea:hover 
{border: 1px solid #AB030E !important; border-radius: 0.34em !important;
-webkit-border-radius: 0.34em !important}

html:root > body button:hover, 
html:root > body input[type="button"]:hover, 
html:root > body input[type="reset"]:hover, 
html:root > body input[type="submit"]:hover 
{background-color: #000 !important; border: 1px solid #AB030E !important;
border-radius: 0.34em !important;
-webkit-border-radius: 0.34em !important}

html:root > body select:hover {background-color: #000 !important;
color: #FFF !important; border: 1px solid #000 !important;
border-radius: 0.34em !important;
-webkit-border-radius: 0.34em !important}

/* Form-Active */
html:root > body button:active, 
html:root > body input[type="button"]:active, 
html:root > body input[type="reset"]:active, 
html:root > body input[type="submit"]:active 
{background-color: #000 !important; color: #FFF !important;
border: 1px solid #AB030E !important; border-radius: 0.34em !important;
-webkit-border-radius: 0.34em !important}

/* Form-Focus */
html:root > body button:focus, 
html:root > body input[type="button"]:focus, 
html:root > body input[type="file"]:focus, 
html:root > body input[type="password"]:focus, 
html:root > body input[type="reset"]:focus, 
html:root > body input[type="submit"]:focus, 
html:root > body input[type="text"]:focus, 
html:root > body input:not([type]):focus, 
html:root > body texarea:focus 
{background-color: #000 !important; color: #FFF !important;
border: 1px solid #AB030E !important; border-radius: 0.34em !important;
-webkit-border-radius: 0.34em !important}

/* Markierung */
::-moz-selection {background: #FDB37C; color: #000}

/* Option[selected] */
option[selected] {background-color: #AB030E !important}

/* Moderiertes-Forum-Hinweis */
.moderated {padding-left: 6px !important; padding-bottom: 4px !important;
font-weight: bold !important; color: #FFF !important; background: #AB030E !important;
border-radius: 0.34em; -khtml-border-radius: 0.34em; border-radius: 0.34em}

/* Avatare abrunden*/
img.avatar {border-radius: 0.34em;
  -khtml-border-radius: 0.34em; border-radius: 0.34em !important}

}
