css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("popmundo.com") {


/* Quoting */
.quote {
font-style: italic !important;
padding: 1px 2px 3px 4px !important;
margin-bottom: 0.3em !important;
border:solid !important;
border-width:1px 1px 2px 1px !important;
border-color:#ccc !important;
background:#f4f4f4 !important;
}

/* Border around messagebox */
.messageBox {
border: 1px solid #006699;
margin-bottom: -10px !important;
background-color:#f9f9f9;
}

/* Border around left navigation */
.folder {
border: 1px solid #006699;
padding: 2px !important;
}

/* Smaller folder images (red, green and grey) */ 
div.folder>img, div.folder>b>b>img, div.folder>b>img {
width:7px !important;
}

/* Padding 'answer to' */
.messageActions a {
padding: 5px !important;
}

/* Sticky padding */
span.sticky {
padding-left: 2px !important;
}

/* Closed topics padding */
span.closed {
color: #006481 !important;
font-weight: bold !important;
padding-left: 2px !important;
}

/* Foldernames text-alignement centered */
div.folder>div.foldername {
border:1px solid #006699 !important;
text-align:center !important;
/* margin: 0 1px !important; */
}

/* Top navigation border and padding */
div.bordertop, div.bordertop2 {
border-top:1px solid #006699 !important;
padding-bottom:2px !important;
padding-top:2px !important;
}

/* Background if hovering left navigation */
div.folder:hover, div.bordertop:hover, div.bordertop2:hover {
background: #EFF9FF !important;
}

/* Folder titles font weight bold */
div.foldername>a {
font-weight:bold !important; 
}

/* Bigger thread priority images */
div#cn>div.threadPrio>a>img
{width: 13px !important;}

/* Redundant img 'thread priority' hidden */
div#cn>div.threadPrio>img
{visibility:hidden !important;}

/* Messageboxhovering color */
div.messagebox:hover
{background:#F7FCFF !important;}

/* Thread navigation padding */
#cn>div.threadNav {
padding-top: 10px !important;
}


/* HR transparent */
img[src="images/conference/FMHR.gif"] {
padding-top: 10px !important;  
background-image: url(http://i35.tinypic.com/15pgahz.gif) !important; 
background-repeat: no-repeat !important; 
height: 0 !important; 
}


/* Deleted Messages */
html>body>div#cn>div.box0 {
background-color:#FFEFEF !important;
border:1px solid #ff0000 !important;
margin-top:10px !important;
margin-bottom:-10px !important;
text-align:center !important;
padding: 7px 0 7px !important;
}


/* Color of closed threads */
span.closed + a {
color:#888 !important;
font-weight: normal !important;
}

/* Highlights the Sticky */
span.sticky + a {
background-color:#FAFFCF !important;
}

/* Highlights red threads */
div.folder img[src="/Common/Images/Conference/high10.gif"] +a {
background-color: #FFE7E7 !important
}

/* Color of grey threads */
div.folder img[src="/Common/Images/Conference/low10.gif"] +a {
color:#888 !important;
}

/*
 Folder width 
div.folder {
width: 115% !important; 
padding-left:0px !important; 
padding-right:0px !important; 
margin-left:-11px !important;
}
*/

}
