css:
@-moz-document url-prefix("http://forum.blockland.us") {
body{
	background:#DDEEFF url('http://dl.dropbox.com/u/8005323/webs/stripes%20bg.png') !important;
	padding-top:16px !important;
	margin:0 auto !important;
/*	max-width:1200px !important; */
}

.post img{
	max-width:100%;
}

body>table:not(.tborder)>tbody>tr>td{
	border-radius:1em;
	background:rgba(255,255,255,0.4) !important;
	padding:20px !important;
}

.tborder[cellpadding='0']{
/*	position:absolute !important; */
/*	margin-top:0; */
	border:none !important;
/*	width:100% !important; */
	background:#222 !important;
	box-shadow:0px 0px 8px #555 !important;
	z-index: 9001;
}

center:first-of-type {
	padding-bottom: 16px;
}

div.smalltext{
	margin-top:16px !important;
}

.logoLink{
	-webkit-transition:0.5s !important;
}

td.windowbg, td.windowbg2, td.catbg{
	border-width:2px !important;
	border-style:solid !important;

}

td.windowbg, tr.windowbg>td{
	background:#c0dcf8 !important;
	border:2px solid !important;
	border-color:#deebf8 #a5cef8 #a5cef8 #deebf8 !important;
}

td.windowbg2{
	background:#edf6ff !important;
	border-color:#fefeff #d5eaff #d5eaff #fefeff !important;
}

td.catbg, tr.titlebg>td, tr.titlebg>td>a{
	background:#8ab2db !important;
	border:2px solid !important;
	border-color:#a6c0db #729dc8 #729dc8 #a6c0db !important;
}

tr.titlebg>td>a{
	border:none !important;
}

input, textarea{
	background:#c0dcf8 !important;
}

div.quote{
	background:rgba(255,255,255,0.3) !important;
}

@keyframes shake{
  0% {
    transform: rotate(0deg);
  }

  33% {
    transform: rotate(5deg);
  }

  67% {
    transform: rotate(-5deg);
  }

  100% {
    transform: rotate(0deg);
  }
}

@-webkit-keyframes webkit-shake{
  0% {
    -webkit-transform: rotate(0deg);
  }

  33% {
    -webkit-transform: rotate(5deg);
  }

  67% {
    -webkit-transform: rotate(-5deg);
  }

  100% {
    -webkit-transform: rotate(0deg);
  }
}

@-moz-keyframes moz-shake{
  0% {
    -moz-transform: rotate(0deg);
  }

  33% {
    -moz-transform: rotate(5deg);
  }

  67% {
    -moz-transform: rotate(-5deg);
  }

  100% {
    -moz-transform: rotate(0deg);
  }
}

@-o-keyframes o-shake{
  0% {
    -o-transform: rotate(0deg);
  }

  33% {
    -o-transform: rotate(5deg);
  }

  67% {
    -o-transform: rotate(-5deg);
  }

  100% {
    -o-transform: rotate(0deg);
  }
}

.avatar{
  position:relative;
  margin-top: 8px;
}

.avatar:hover{
  animation: shake 0.2s;
  -webkit-animation: webkit-shake 0.2s;
  -moz-animation: moz-shake 0.2s;
  -o-animation: o-shake 0.2s;
}

a{
  transition: 0.2s;
  -webkit-transition: 0.2s;
  -moz-transition: 0.2s;
  -o-transition: 0.2s;
}

::selection{
  background-color: #c0dcf8 !important;
}
}
