css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {

/* PERFECT FACEBOOK */
  
/* change background image */

     body{
          background: url("/*[[myimage]]*/") fixed /*[[align]]*/ !important;
          background-size: cover !important;  
     }  

      #contentCol, #mainContainer{
          background-color: transparent !important;
          box-shadow: none !important;
     }   
 
/* font size and face */

@font-face {
    font-family: sarala;
    font-style: normal;
    font-weight: normal;
    src: url(https://fonts.gstatic.com/s/sarala/v1/s2t9exVCKzFG-HPaQHVGEA.woff2);
}
@font-face {
    font-family: 'sarala';
    font-style: normal;
    font-weight: 700;
    src: url(https://fonts.gstatic.com/s/sarala/v2/NktIEkUOaTGbiS3SsqVszVtXRa8TVwTICgirnJhmVJw.woff2);
}
* {
    font-family: "sarala" !important;
}

/* home page: left column */

.navHeader {
    display: none !important;
}
#navItem_1015776094 > a:nth-child(2), #seo_h1_tag {
    font-size: 1.5em !important;
    font-weight: bold !important;
}
#leftCol > div:nth-child(1) > div:nth-child(1) {
    background-color: rgba(229, 229, 229, 0.7);
    box-shadow: -10px 0px 0px 0px rgba(229, 229, 229, 0.7);
    padding-bottom: 10px !important;
    padding-top: 10px !important;
    margin-top: -12px !important;
    -webkit-border-bottom-left-radius: 10px;
    border-bottom-left-radius: 10px;
}
._3m75 .sideNavItem ._5afe::after {
    -webkit-border-bottom-left-radius: 10px;
    border-bottom-left-radius: 10px;
    -webkit-border-top-left-radius: 10px;
    border-top-left-radius: 10px;
    -webkit-border-bottom-right-radius: 0px;
    border-bottom-right-radius: 0px;
    -webkit-border-top-right-radius: 0px;
    border-top-right-radius: 0px;
    margin-top: 1px;
    margin-bottom: 1px;
    margin-right: 1px !important;
    border: 0px;
    background-color: rgba(255, 255, 255, 0.6) !important;
}
.fbxWelcomeBoxSmallRow::after {
    -webkit-border-bottom-left-radius: 10px !important;
    border-bottom-left-radius: 10px !important;
    -webkit-border-top-left-radius: 10px !important;
    border-top-left-radius: 10px;
    -webkit-border-bottom-right-radius: 0px;
    border-bottom-right-radius: 0px;
    -webkit-border-top-right-radius: 0px;
    border-top-right-radius: 0px;
    margin-right: 1px !important;
    border-top: 5px !important;
    border-bottom: 5px !important;
    border: 0px !important;
    background-color: rgba(255, 255, 255, 0.6) !important;
}
/* home page: right column */

.home_right_column {
    position: fixed !important;
    width: 300px !important;
    padding-top: 10px !important;
    padding-left: 10px !important;
    padding-bottom: 10px !important;
    margin-left: 0px !important;
    margin-top: -12px !important;
    box-shadow: 10px 0px 0px 0px rgba(229, 229, 229, 0.7);
    background-color: rgba(229, 229, 229, 0.7) !important;
    -webkit-border-bottom-right-radius: 10px;
    border-bottom-right-radius: 10px;
}
#pagelet_rhc_footer {
    display: none !important;
    background: none !important;
}
/* home page: center column */

#stream_pagelet {
    background-color: rgba(255, 255, 255, 0.8) !important;
    box-shadow: 0px 0px 0px 12px rgba(255, 255, 255, 0.8) !important;
}
#contentCol {
    background-color: transparent !important;
}
/* chat sidebar */

.fbChatSidebar {
    background-color: rgba(255, 255, 255, 0.8) !important;
    border: none !important;
    margin: 0 !important;
}
/* timeline: main feed */

#pagelet_timeline_main_column {
    background-color: rgba(255, 255, 255, 0.8) !important;
    box-shadow: 0px 0px 0px 12px rgba(255, 255, 255, 0.8) !important;
}
._5h60 {
    background-color: transparent !important;
}
/* pages */

div._1qkq._1ql0:not(#contentCol) {
    /* also applies to events main column and in a few other contexts */
    
    background-color: rgba(255, 255, 255, 0.8) !important;
    box-shadow: 0px 0px 0px 12px rgba(255, 255, 255, 0.8) !important;
}
div#pagelet_timeline_main_column:not(._5h60) {
    background: transparent !important;
    box-shadow: none !important;
}
/* full screen messenger */

._4sp8 {
    background-color: transparent !important;
}
._2xhi,
._36ic {
    background-color: rgba(229, 229, 229, 0.8) !important;
    box-shadow: none !important;
}
._4_j4,
._4_j5,
._673w {
    background-color: rgba(255, 255, 255, 0.8) !important;
}
/* events */

#entity_sidebar {
    background-color: rgba(229, 229, 229, 0.8);
    box-shadow: -10px 0px 0px 0px rgba(229, 229, 229, 0.8);
    -webkit-border-bottom-left-radius: 10px;
    border-bottom-left-radius: 10px;
}
#events_dashboard_find_events,
#events_dashboard_upcoming_birthdays,
#events_dashboard_export,
#event_related_events {
    background-color: transparent;
    padding-left: 12px;
}
._5c_5 {
    background-color: rgba(255, 255, 255, 0.8) !important;
}
/* groups page */

._4-u2 {
    border-radius: 0px !important;
    -webkit-border-radius: 0px !important;
}
#u_jsonp_8_0,
#u_jsonp_3_0,
#u_jsonp_4_0,
#u_jsonp_2_0,
#u_jsonp_5_0,
#u_jsonp_6_0,
#u_jsonp_7_0,
#u_jsonp_1_0,
#u_0_0 {
    position: static !important;
}
#contentCol.groupLitestandClassicJumpLayout {
    background-color: rgba(255, 255, 255, 0.8) !important;
    box-shadow: 0px 0px 0px 12px rgba(255, 255, 255, 0.8) !important;
}
/* marketplace 

div._2pie:nth-child(2) {
    background-color: rgba(255, 255, 255, 0.8) !important;
    
    margin-left: 100px !important;
}
 */

#u_0_s:not(._2sdm):not(._64b){
    background-color: rgba(229, 229, 229, 0.7);
    box-shadow: 0px 0px 0px 10px rgba(229, 229, 229, 0.7);
}

/* marketplace - search bar */

._4rtl {
    background-color: transparent;
}
._638s {
    top: 40px !important;
}
/* on this day */

#goodwill_throwback_permalink_content {
    background-color: rgba(255, 255, 255, 0.8) !important;
    box-shadow: 0px 0px 0px 12px rgba(255, 255, 255, 0.8) !important;
}
/* hide advertisments */

.fbTimelineSideAds,
.UIStandardFrame_SidebarAds,
#rightCol #pagelet_ego_pane .ego_column:nth-child(1),
#pagelet_side_ads,
.ego_column,
#sidebar_ads,
.fbPhotoSnowboxAds,
.fbPhotoSnowboxAdsSide,
#pagelet_adbox,
.fbEmu.fbEmuBlock.fbEmuEgo[id*="-id_"],
#ego,
a[href*="/campaign/111111"],
.fbEmuMarketplace,
#egoRefreshAds,
#pagelet_ego_pane_m,
#pagelet_ego_pane_w,
.profile_sidebar_ads,
.sidebar_ads,
.adcolumn_header,
.admarket_ad,
.ad_story,
.social_ad_advert,
.ads_feedback,
.next_ad_button,
.more_ads,
#pagelet_ego_pane {
    background: none !important;
    display: none !important;
}
  
}
