/* (Gp75's devuantART page) */

/* Table of contents */
/* 1. Header main part */
/* 2. Header corners */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gran-turismo.wikia.com") {

/* Header main part */

.WikiaHeader {
background-color: #001500!important;
background-image: -moz-linear-gradient(left, #000000 0, #001500 50%, #000000 100%)!important;
border-top: 3px solid black!important;
color: white!important;
}

.WikiaHeader nav {
background-image: url(http://images1.wikia.nocookie.net/__cb46612/common/skins/oasis/images/checkers.png)!important;
border-top: 1px solid #000!important;
border-bottom: 1px solid #000000!important;
}

.WikiaFooter .toolbar {
background-color: blue!important;
background-image: -moz-linear-gradient(left, #000000 0, #001500 50%, #000000 100%)!important;
border-top: 3px solid black!important;
color: white!important;
}

/* Header corners */

.WikiaHeader li {
border-left: 2px solid transparent!important;
}

.WikiaHeader .banner-corner-left {
border-color: black black transparent transparent!important;
}

.WikiaHeader .banner-corner-right {
border-color: #001500 transparent transparent #001500!important;
}

.WikiaFooter .banner-corner-right {
border-color: transparent transparent #001500 #001500!important;
}

}
