css:
@-moz-document domain("youtube.com") {




* {text-align:center !important;}

/*deleting unused stuff and ads*/
.guide-container,.guide-section,.guide-background,#feed-background,#video-sidebar,.ad-div,#masthead_child_div {display:none  !important;}

/*make feed ok*/
#feed {
width:100% !important;
margin-left:0px !important;
}

.feed-page {
padding:0 !important;
//background-image:url(http://osxdaily.com/wp-content/uploads/2011/10/NSTexturedFullScreenBackgroundColor.png) !important;

background-image:url(http://www.neowin.net/forum/uploads/monthly_02_2011/post-119000-0-30898900-1298696649.png) !important;
}

.guide-layout-container {
width:100%  !important;
}

#content {
width:100% !important;
}

#baseDiv {
width:100% !important;
padding-left:0px !important;
}

/*undo list view*/
.feed-item-container {
width: 475px !important;
display:inline-block !important;
//margin:15px auto !important;
height:161px !important;
clear:both !important;

//padding:1.25% !important;

}

/*disable show more button and show videos directly*/
.feed-item-show-aggregate {display:none  !important;}
.feed-item-sub-items {display:block  !important;}

/*add numbers*/
.feed-page > ul {
list-style:none  !important;
}

/*add border */
.feed-page > ul > li {
//background:rgba(214,214,214,1) !important;
margin:15px 25px !important;
display:inline-block !important;
}

.feed-item-container {
border:1px solid #fff ;
border-radius:15px !important;
background:-webkit-gradient(linear, left top, left bottom, from(#555), to(#222)) !important;
background: -moz-linear-gradient(top,  #555,  #222) !important;

-moz-box-shadow: 10px 10px 20px rgba(0,0,0,0.5) !important;
-webkit-box-shadow: 10px 10px 20px rgba(0,0,0,0.5) !important;
}

.feed-item-container:hover {
background:-webkit-gradient(linear, left top, left bottom, from(#666), to(#333)) !important;
background: -moz-linear-gradient(top,  #666,  #333) !important;
}


/*disable background of feed entrys*/
.feed-item-content > * {
background:transparent  !important;
color:#aaa  !important;
}

body {
//background:#333 !important;
}

#masthead-container {
background:#333  !important;
}

#masthead-user-bar-container {
background:-webkit-gradient(linear, left top, left bottom, from(#222), to(#555)) !important;
background: -moz-linear-gradient(top,  #222,  #555) !important;
}

#masthead-nav {
background:-webkit-gradient(linear, left top, left bottom, from(#222), to(#555)) !important;
background: -moz-linear-gradient(top,  #222,  #555) !important;

margin-left:50px !important;
border-radius:5px !important;
}

#masthead-nav > a {
color:#fff !important;

}

.description {
display:none !important;
}

.feed-item-content > h4{
height:50px !important;
}

.comments {
display:inline-block !important;
}

.feed-item-actions-line {color:#444;}


/* 2012/02/17 Updates for Youtube Changes */


}
