css:
@-moz-document domain('deviantart.com'){
.cc-name {
    letter-spacing: 0;
    font-size: inherit;
}

.cc-time {
    margin-left: 3px;
}

.cc-time a {
    color: #97a5a4 ;
}

.cc-avatar {
    margin-top: 0px;
    margin-left: 0px;
    margin-right: 1px;
}

.ccomment {
    margin-bottom: 0px;
}

.cc-meta {
    margin-bottom: 4px;
}

.ccomment div.text {
}

#gmi-CComment .ch-ctrl a.reply {
    margin-top: -30px;
    position: absolute ;
    right: 40px ;
    top: 32px ;
    left: auto ;
    bottom: auto ;
}

.ccomment .cc-in {
    min-height: 32px ;
    padding: 3px 4px;
}

.ccomment .ch-ctrl .ch-ctrl {
    border-width: 0px ;
    border-bottom-left-radius: 4px 4px;
    border-bottom-right-radius: 4px 4px;
    border-top-left-radius: 4px 4px;
    border-top-right-radius: 4px 4px;
    font-size: 0.9em;
    background-color: #E4EBDE;
    border-bottom: 1px solid #C9D3CA ;
    border-top: 1px solid #F5FAF5 ;
}

.ccomment i.l {
    display: none;
}

.ctext .cc-manage-button {
    visibility: hidden;
}

.ctext:hover .cc-manage-button {
    visibility: visible;
}

.cc-name .u {
    text-decoration: underline;
    color: #3B5A4A;
}

.ch-ctrl .reply {
    visibility: hidden;
}

.ch-ctrl:hover .reply {
    visibility: visible;
}

.talk-tower h2.c, .talk-tower .grmood, .cc-mood {
    display: none;
}

.cc-avatar .avatar {
    width: 25px;
    height: 25px;
}

div.nest {
    padding-left: 27px;
}
/* post reply */
.ccomment-post {
margin-top: 0px;
}
/* hide emoticons */
.ch-ctrl .previewbox td.rr {
display:none;
}

div.talk-post textarea, div.talk-post div.pager-holder, div.talk-post div.pager2 {
height: 70px !important;
}
}
