/* Powerfox 1.0 by Nicolas McCurdy (http://thenickperson.com) */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* display close buttons on all hovered tabs */
.tabbrowser-tab[pinned] .tab-close-button, .tabbrowser-tab[pinned]:hover .tab-close-button {display: none !important;}
.tabbrowser-tab:hover .tab-close-button {visibility: visible !important; display: inline !important;}

/* shrink tabs to their icons before scrolling them (like Chrome) */
.tabbrowser-tab:not([pinned]) {min-width: 0px !important;} /* previous width was 30px */
.tabbrowser-tab:not([pinned])[selected] {min-width: 0px !important;} /* preview width was 42px */

/* automatically hide back/forward buttons when they are disabled */
#back-button[disabled="true"], #forward-button[disabled="true"], #back-forward-dropmarker[disabled="true"] {display: none;}

/* keyword field in bookmark button panel (via the star to the right of the URL bar) */
#editBMPanel_keywordRow, #editBMPanel_locationRow {visibility: visible;}
#editBMPanel_tagsSelector[collapsed="true"] {display: none !important;}
