css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("funtoo.org"),
domain("gentoo.org"),
domain("larrythecow.org"),
domain("gentooexperimental.org"),
domain("gentoo-wiki.com"),
domain("gentoo-wiki.info"),
domain("gentooligans.com"),
domain("zugaina.org"),
domain("znurt.org")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h2, h3, h4, h5, h6, font, strong, p, form, footer, code

{
color: white !important;
background: black !important;
}

select, button, #gentoo-bar, .mw-content-ltr pre, .wikiEditor-ui-toolbar, #file img:hover, img.decoded, #wrapper, .active, .pun, #commit, #commit_top, #shadowWrap, #footer_utilities, .navbar

{
background: black !important;
}

div, span

{
background-color: black !important;
}

select, input, textarea, pre, label, div, dd, .gensmall, .postbody, .postdetails, b, .gen, .cattitle, .nav, .genmed, .mw-headline, .firstHeading

{
color: white !important;
}


/*links*/

a, .gensmall a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, input, kbd, option, .item-description pre, .dropdown-toggle:active, .open, .dropdown ul li a:hover

{
background-color: #333333 !important;
}

td.diff-addedline .diffchange

{
background: #000066 !important;
}

td.diff-deletedline .diffchange

{
background: #7F7F0F !important;
}

/*forums logo*/

#form1 table tbody tr td table tbody tr td img
{
background: black !important;
    height: 20px !important;
    padding: 9px 0px 0px 100px !important;
    width: 0px !important;
}

#form1 table tbody tr td table tbody tr td a img

{
background: url(https://bugs.gentoo.org/extensions/Gentoo/web/gentoo_org.png) no-repeat scroll 0 0 transparent !important;
height: 20px !important;
padding: 9px 0px 0px 100px !important;
width: 0px !important;
position:absolute;
top:0px;
left:5px;
}

/* wiki stuff */

.cell-yes

{
background: #040 !important;
}

.cell-no

{
background: #400 !important;
}

.cell-partial

{
background: #662 !important;
}

.wikiEditor-ui-buttons

{
border-top: 1px solid black !important;
}
    
.code
{
border-style:solid;
border-color:#fff;
}

input.submit_input {
background: url('http://forums.funtoo.org/public/style_images/dash/search_icon.png') no-repeat 50%;
  }
    
}
