/*infowindows

#myAssignmentsHeaderContainer .infoWindow {visibility:visible!important;position:relative!important;top:0!important;left:0!important;}*/

/* font  */
table, tr, td, input, select, textarea,.iw-base-link,.iw-base-tree-node,.iw-base-heading-link,.iw-base-heading-link-bold,.iw-base-tab-down-text,.iw-base-tab-up-text,.iw-base-heading-link-disabled,.iw-base-actionlist-link,.iw-ccpro-username-text,.iw-base-text-field-label-bold,.iw-ccpro-property-key,.iw-base-crumbtrail-link,.iw-base-crumbtrail-link-disabled,.iw-ccpro-property-value,.iw-base-listview-header-link,.iw-base-heading2-link,.iw-base-button, .iw-base-button-disabled,span,.xuipageheadingtext,a.menua_toplinktext,a.menua_childlinktext,a.menuA_ChildLinkText_over,a.xuilinktext2_sm,.xuilistcelldiv,.xuidisplaytext_sm,.fronttab_contenttext,a.reartab_linktext,a.menuA_TopLinkText_over,.navitemlink,a.xuilinktext2_sm_novisited
