/* link colors  */
img, a {background:transparent!important;}body a {color: #e98943 !important; text-decoration:none;text-shadow: 0.1em 0.1em 0.1em #000;font-weight:bold!important;}
body a:visited { color:#e9a843 !important;}

/* hide crap  */
#logo,#header {display:none;}
.header img { height:20px!important;width:auto!important;}
.header {display:none!important;}

/* tabs  */
body .tabon {background-color: #e98943 !important;}
body .tabon img, body .taboff img{visibility:hidden !important;}
body .tabon a img, body .taboff a img{visibility:visible !important;}

/* progress bars  */
img.pastdue,img.inprogress,img.warning {border:none!important;}
img.pastdue {background-color: #900!important;}
img.inprogress {background-color: #0C0!important;}
img.warning {background-color: #FF9!important;}

/* big buttons  */
input[type="radio"], input[type="checkbox"] {
margin: 3px 10px;
padding: 6px!important;
background-color: initial;
border: initial;
-webkit-transform: scale(2, 2);
-moz-transform:scale(2, 2);}

/* box sizing */
#leftColumnCell {width:1%!important;  }
#rightColumnCell {width:99%!important;  }
}
