@charset "utf-8";
@-moz-document domain("forums.warriorcats.com") {

body {
text-shadow: 0 2px 2px rgba(0,0,0,.8), 0 1px 1px rgba(0,0,0,.8);
background: url("http://fc07.deviantart.net/fs70/f/2012/002/4/9/beta_pixel_background_by_thegkoc-d4l2uf9.png") repeat scroll 0 0 #002222 !important;
}

INPUT.ev_input, INPUT.ev_input_normal, .ev_input_normal, INPUT.ev_input_small, .ev_input_small {
background: none !important;
}

embed[src="http://www.warriorcats.com/warriors_community_content/header.swf"] {
border-bottom: 20px none;
-moz-border-image: url("http://fc09.deviantart.net/fs70/f/2012/002/9/5/beta_3d_rectangular_bottom_by_thegkoc-d4l2u26.png") 0 0 100 0;
height: 218px !important;
}

embed[src="http://www.warriorcats.com/warriors_community_content/footer.swf"] {
border-bottom: 20px none;
-moz-border-image: url("http://fc09.deviantart.net/fs70/f/2012/002/9/5/beta_3d_rectangular_bottom_by_thegkoc-d4l2u26.png") 0 0 100 0;
height: 108px !important;
}

.ev_message_alert {
box-shadow: 0 2px 6px #000000 !important;
}

.ev_ubbx_tpc_avatar {
box-shadow: 0 2px 2px rgba(0,0,0,.8), 0 1px 1px rgba(0,0,0,.8);
}

#gs_quick_reply_replyto {
color: #EFEFEF !important;
text-shadow: 0 1px 1px rgba(0,0,0,.8), 0 1px 1px rgba(0,0,0,.8) !important;
}

#gs_req_field_message {
width: 500px !important;
}

textarea, input[type="text"] {
border: 1px solid #000000;
border-bottom: 1px solid #555555;
box-shadow: inset 0 3px 2px rgba(0,0,0,.8);
}

.starbox .colorbar, .starbox .rated .hover .colorbar {
background: #226622 !important;
}

.starbox .rated .colorbar {
background: #429342 !important;
}

.ev_tab, .ev_tab_hover {
border: none !important;
padding: 0px !important;
box-shadow: none !important;
}

.eve_tab, .eve_tab_hover {
padding: 2px !important;
padding-left: 5px !important;
padding-right: 5px !important;
border: 1px solid #000000 !important;
height: 26px !important;
}

.eve_tab {
background: #333333 !important;
box-shadow: 0 1px 0 #555555 inset;
-moz-transition: .25s linear;
-webkit-transition: .25s linear;
transition: .25s linear;
}

.ev_tab {
background: none !important;
}

.ev_tab_hover, .eve_tab_hover {
background: #226622 !important;
}

.eve_tab_hover {
box-shadow: 0 1px 0 #429342 inset;
-moz-transition: .25s linear;
-webkit-transition: .25s linear;
transition: .25s linear;
}

.eve_tools, .eve_find, .eve_new, .eve_go, .eve_reply, .eve_reply_quick {
vertical-align: middle !important;
}

TABLE.ev_tab_table_top {
-moz-border-image: url("http://fc09.deviantart.net/fs71/f/2012/002/d/9/beta_3d_posting_top_by_thegkoc-d4l2v9m.png") 0 100 0 0 !important;
border-right: 10px !important;
box-shadow: none !important;
}

#gs_online_now_list, #gs_menu_y_baseline, .ubbx_cfrm_cat_tbl, .ev_tab_table_top, 

.ev_com_tbl, .ev_msg_rowcolor1, .ev_msg_rowcolor2, .ev_body_table {
box-shadow: 0px 1px 4px #000 !important;
}

.ev_background_txt, #ev_copy_txt, div.ev_ubbx_pages, div.ev_ubbx_pages b {
text-shadow: 0px 1px 2px #000 !important;
}

body.ev_popup, body.ev_iframe {
background: #2b2b2b !important;
}

.ev_msg_rowcolor1, TABLE.ev_msg_rowcolor1, .ev_bgcolor_row1, ABLE.ev_member_table TD, TH, TD.ev_body_table_mypace_leftnav_td, TABLE.ev_ms_content TD.ev_ms_content_title, TABLE.ev_ms_content TH.ev_ms_content_title, TABLE.ev_ms_content TD.ev_ms_content_lr, TABLE.ev_ms_content TH.ev_ms_content_lr, .ev_ms_header, .ev_island_news, .ev_island, .ev_hopto_normal_hover, TD.ev_onlinenow_table {
background-color:#2b2b2b !important;
}

.ev_hopto_normal, TABLE.ev_com_tbl TD, TABLE.ev_com_tbl TH, TABLE.ev_com_tbl TH, .ev_menu_logout, .ev_island_alt, .ev_island_login, .ev_island_content, DIV.ev_ms_nav, TD.ev_body_table_myspace_content_td, TABLE.ev_member_table TH, .ev_bgcolor_row2, TABLE.ev_body_table, TABLE.ev_message_alert TD.ev_content, .ev_msg_rowcolor2, TABLE.ev_msg_rowcolor2, TABLE.ev_member_table TD, TH {
background-color:#232323 !important;
}

div.ev_tab a, div.ev_tab_hover a, div.ev_tab, div.ev_tab_hover {
color: #efefef !important;
}

#gs_tab_smilie, #gs_tab_color, #gs_tab_url, #gs_tab_bold, #gs_tab_italic, 

#gs_tab_strike, #gs_tab_quote, #gs_tab_list, #gs_tab_list, #gs_tab_email, 

#gs_tab_img, #gs_tab_tag, #gs_tab_posticon_border {
background-color: #666666 !important;
}

table.ev_container td *, table.ev_container_bottom td *, table.ev_container td a, table.ev_container td a:visited, table.ev_container_bottom td a:visited, table.ev_container td, table.ev_container_bottom td, table.ev_container th {
color: #FFFFFF !important;
}

table.ubbx_cfrm_cat_tbl, #gs_tab_posticon_menu, ABLE.ev_layer_table, TABLE.ev_help_menu, TABLE.ev_hopto_table, TABLE.ev_layer_table TD, TABLE.ev_help_menu, .ev_hopto_scroller, .ev_hopto_emphasized, DIV.ev_container_bottom, TABLE.ev_container TD, TABLE.ev_container_bottom TD, TABLE.ev_container, TABLE.ev_message_alert, TABLE.ev_message_alert TD.ev_grab_bar, TABLE.ev_post_table TD.ev_post_table_leftcol, ts.eve_tab {
background-color: #333333 !important;
box-shadow: 0 1px 0 #555555 inset;
z-index: 300;
}

table.ev_layer_table td, table.ev_help_menu td, table.ev_hopto_table tr td, table.ev_layer_table td, table.ev_help_menu {
background-color: #2B2B2B !important;
}

.ev_layer_table tbody tr td a, .ev_text_normal, .ev_layer_table b, table.ev_layer_table td a:hover, table.ev_layer_table td a:visited, .ev_hopto_emphasized a, .ev_hopto_emphasized_hover a, .ev_hopto_emphasized_hover a:hover, .ev_hopto_emphasized_hover a:visited, table.ev_layer_table td, table.ev_help_menu td, table.ev_hopto_table tr td, table.ev_post_table td.ev_post_table_leftcol *, table.ev_post_table td.ev_post_table_leftcol {
color: #EFEFEF !important;
}

textarea#ev_post_field_message, span textarea.ev_text_normal, input#gs_msg_insert_bold, input#ev_post_field_subject {
color: #000000 !important;
}

#ev_post_field_postto {
color: #000000 !important;
}

table.ev_layer_table td, table.ev_help_menu td, table.ev_hopto_table tr td {
border-bottom-color: #222222 !important;
border-bottom-width: 1px !important;
}

table.ev_layer_table td input {
background-color: #222222 !important;
}

.ev_help_menu_header {
color: #efefef !important;
}

DIV.ev_help_menu_header {
background-color: #2B2B2B !important;
}

TABLE.ev_ms_content, TABLE.ev_ms_content TH, TABLE.ev_ms_content TD {
border-color: #000000 !important;
}

.ev_container_bottom {
border-bottom: 22px none !important;
-moz-border-image: url("http://fc09.deviantart.net/fs70/f/2012/002/9/5/beta_3d_rectangular_bottom_by_thegkoc-d4l2u26.png") 0 0 100 0 !important;
}

body[onscroll] .ev_container_bottom {
border-bottom: none !important;
-moz-border-image: none !important;
}

TABLE.ev_tab_table_bot {
border: none !important;
}

#ev_topic_reply_bot {
height: 51px;
background: url("http://fc06.deviantart.net/fs71/f/2012/002/b/1/beta_3d_posting_bottom_by_thegkoc-d4l2u7v.png");
}

.ev_ubbx_tpc div.ev_tpc_signature {
background: rgba(0,0,0,.2);
box-shadow: 0 2px 3px rgba(0,0,0,.9) inset;
margin-top: 1em;
margin-bottom: .7em;
padding-left: 2em;
padding-right: 2em;
padding-bottom: 2em;
overflow-y: auto !important;
height: 102px;
max-height: 102px;
}

.ev_code_BLACK {
font-family: Georgia, serif !important;
font-size: 1.12em !important;
color: #EFEFEF !important;
}

.ev_code_GREEN {
color: #009900 !important;
}

span.adminModCustomTitle:before {
content: url("http://fc04.deviantart.net/fs70/f/2012/002/0/f/beta_moderator_badge_by_thegkoc-d4l325r.png");
display: block;
}

a[onclick*="Good King of Cats"]:after {
content: url("http://fc01.deviantart.net/fs70/f/2012/002/5/c/beta_button_header_tab_by_thegkoc-d4l2tkx.png");
display: block;
}

::-moz-selection {
background: #429342 !important;
color: #EFEFEF !important;
text-shadow: none;
}

::selection {
background: #429342 !important;
color: #EFEFEF !important;
text-shadow: none !important;
}
}
