@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pokecheck.org") {

/* Global elements */
body {
background: #000000 !important;
color: #FFFFFF !important;
}

a:link {color: #FF0000 !important;}
a:visited {color: #FF0000 !important;}

input,select {-moz-appearance: none !important;}

input[type="text"],input[type="password"],
input[type="button"],input[type="submit"],
input[type="checkbox"],select {
background: #000000 !important;
color: #FFFFFF !important;
border: 1px solid #550000 !important;
}

.warnmsg {color: #EFEF22 !important;}

/* Pokémon Selector */
.red {background: #220022 !important; color: #FFF !important;} /*DO NOT REMIX*/
.gold {background: #662200 !important; color: #FFF !important;} /*DO NOT REMIX*/
.emerald {background: #226600 !important; color: #FFF !important;} /*DO NOT REMIX*/
.platinium {background: #222 !important; color: #FFF !important;} /*DO NOT REMIX*/
.black {background: black !important; color: white !important;} /*DO NOT REMIX*/

/* Top section */
.mainheader {background: #111111 !important;
color: #EEEEEE !important;}

.dnsaddress {background: #550F00 !important;
border: 1px solid #CC0F00 !important;}
.overflowbox {border: 1px solid #550000 !important;}

.disclaimer {color: #FFA500 !important;}

/* Search/userinfo divider borders */
.mainheader,.linkbar {border-bottom: 1px solid red !important;}

/* Shoutbox styles */
.shoutbox {border: 1px solid #550000 !important;}
.msg0 {background: #330000 !important;}
.msg1 {background: #220000 !important;}
.admmsg0 {background: #33001F !important;}
.admmsg1 {background: #22001F !important;}
.privmsg {background: #103310 !important;}

/* img[src="i/refresh.png"] {
background: transparent url(http://i41.tinypic.com/vdzfkj.png) no-repeat top left !important;
height: 0 !important;
overflow: hidden !important;
} */

/* Fixed feedback bar */
.fixedbar {background: #332222 !important;}

/* Sortable Pokémon tables/box info */
.sortable {border: 1px solid red !important}
.pklisthdr {background: #552222 !important;}
.row0 {background: #330000 !important;}
.row1 {background: #220000 !important;}
.row0:hover {background:#771F1F !important;}
.row1:hover {background:#660F0F !important;}

/* Pokémon stats table */
div .markings{background: #000 !important;}

.dhead {
background: #441100 !important;
border: 1px solid #990000 !important;
}
.dblock {
background: #220000 !important;
border: 1px solid #CC0000 !important;
}

.stattable,.movetable {background: #110000 !important;}
.statheader {background: #552222 !important;}
.st0,.move0 {background: #330000 !important;}
.st1,.move1 {background: #220000 !important;}
.sthp {background: #4F2000 !important;}
.cont {color: #EEEEEE !important;}

.headless {
background: #220000 !important;
color: #FF0000 !important;
border: 1px solid red !important;
}

/* Nature stat colors */
.up {color: #9F0033 !important;} /*DO NOT REMIX*/
.down {color: #00669F !important;} /*DO NOT REMIX*/

/* Legality analysis */
.betawarn {background: #110C00 !important; border: 1px solid #993300 !important;}
.legaltable {background: #772222 !important;}
.legalheader {background: #551C00 !important;}
.legal0 {background: #330000 !important;}
.legal1 {background: #220000 !important;}
.valid {color: #00FF00 !important;} /*DO NOT REMIX*/
.warning {color: #FFA500 !important;} /*DO NOT REMIX*/
.error {color: #FF0000 !important;} /*DO NOT REMIX*/

/* ZIP notification */
.zipnotice {
background: #110800 !important;
color: #EECCEE !important;
border: 1px solid #CC0000 !important;
}

/* Sprite page */
div.spritereport,div.iconsubmit,div.icontable{
background: #111111 !important;
border: 1px solid red !important;}

/* Directory display */
.list {background: #220000 !important;
border: 1px solid red !important;}

/* Frequently Asked Questions */
.faqentry {
background: #220000 !important;
color: #FF0000 !important;
border: 1px solid red !important;
}

/* Public box display / account options */
.pubbox {background: #440000 !important;}
.prefs {background: #111111 !important;
color: #EEEEEE !important;}
.pk0 {background: #330000 !important; border: 1px solid #662F2F !important;}
.pk1 {background: #220000 !important; border: 1px solid #881F1F !important;}
.pk0:hover {background: #771F1F !important;}
.pk1:hover {background: #660F0F !important;}

}

/* SPRITES SUBDOMAIN */
@-moz-document domain("sprites.pokecheck.org") {

body {background: #000000 !important;
color: #FFFFFF !important;}

a:link,a:visited {color: #FF0000 !important;}

.header {background: #111111 !important;
border-bottom: 1px solid red !important;}
.tab {background: #220000 !important;}
.active,.tab:hover {background: #660000 !important;}

.fixbar {background: #332222 !important;}
}
