/************
  Pokécheck TrueBlue (DARK)
  © M.andrew 2012
  
  Changelog:
  2.8 - Cleanup, brightened borders and added account options
  2.3 - Updated FAQ to match class, skinned Pokécheck ad for "headless" display
  2.2 - Fixed header for most deposited stats
  2.1 - Skinned the FAQ
  2.0 - Added styles for sprites subdomain
  1.9 - Brightened borders around the iframes
  1.0 - Initial release (remix from BloodRed DARK 1.59)
************/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pokecheck.org") {

/* Global elements */
body {
background: #000000 !important;
color: #FFFFFF !important;
}

a:link {color: #3333FF !important;}
a:visited {color: #3333FF !important;}

input,select {-moz-appearance: none !important;}

input[type="text"],input[type="password"],
input[type="button"],input[type="submit"],
input[type="checkbox"],select {
background: #000000 !important;
color: #FFFFFF !important;
border: 1px solid #000088 !important;
}

.warnmsg {color: #EF22EF !important;}

/* Pokémon Selector */
.red {background: #220022 !important; color: #FFF !important;} /*DO NOT REMIX*/
.gold {background: #662200 !important; color: #FFF !important;} /*DO NOT REMIX*/
.emerald {background: #226600 !important; color: #FFF !important;} /*DO NOT REMIX*/
.platinium {background: #222 !important; color: #FFF !important;} /*DO NOT REMIX*/
.black {background: black !important; color: white !important;} /*DO NOT REMIX*/

/* Top section */
.mainheader {background: #111111 !important;
color: #EEEEEE !important;}

.dnsaddress {background: #0F0055 !important;
border: 1px solid #0F00CC !important;}
.overflowbox {border: 1px solid #000088 !important;}

.disclaimer {color: #A500FF !important;}

/* Search/userinfo divider borders */
.mainheader,.linkbar {border-bottom: 1px solid blue !important;}

/* Shoutbox styles */
.shoutbox {border: 1px solid #000088 !important;}
.msg0 {background: #000033 !important;}
.msg1 {background: #000022 !important;}
.admmsg0 {background: #001F33 !important;}
.admmsg1 {background: #001F22 !important;}
.privmsg {background: #331010 !important;}

/* img[src="i/refresh.png"] {
background: transparent url(http://i41.tinypic.com/vdzfkj.png) no-repeat top left !important;
height: 0 !important;
overflow: hidden !important;
} */

/* Fixed feedback bar */
.fixedbar {background: #222233 !important;}

/* Sortable Pokémon tables/box info */
.sortable {border: 1px solid blue !important}
.pklisthdr {background: #222255 !important;}
.row0 {background: #000033 !important;}
.row1 {background: #000022 !important;}
.row0:hover {background:#1F1F77 !important;}
.row1:hover {background:#0F0F66 !important;}

/* Pokémon stats table */
div .markings{background: #000 !important;}

.dhead {
background: #110044 !important;
border: 1px solid #000099 !important;
}
.dblock {
background: #000022 !important;
border: 1px solid #0000CC !important;
}

.stattable,.movetable {background: #000011 !important;}
.statheader {background: #222255 !important;}
.st0,.move0 {background: #000033 !important;}
.st1,.move1 {background: #000022 !important;}
.sthp {background: #20004F !important;}
.cont {color: #EEEEEE !important;}

div[style="margin:0px auto;width:720px;background:#EEE;border:1px solid black;padding:2px;text-align:center;"]{
background: #000022 !important;
color: #3333FF !important;
border: 1px solid blue !important;
}

/* Nature stat colors */
.up {color: #9F0033 !important;} /*DO NOT REMIX*/
.down {color: #00669F !important;} /*DO NOT REMIX*/

/* Legality analysis */
.betawarn {background: #0C0011 !important; border: 1px solid #330099 !important;}
.legaltable {background: #222277 !important;}
.legalheader {background: #1C0055 !important;}
.legal0 {background: #000033 !important;}
.legal1 {background: #000022 !important;}
.valid {color: #00FF00 !important;} /*DO NOT REMIX*/
.warning {color: #FFA500 !important;} /*DO NOT REMIX*/
.error {color: #FF0000 !important;} /*DO NOT REMIX*/

/* ZIP notification */
.zipnotice {
background: #080011 !important;
color: #CCEEEE !important;
border: 1px solid #0000CC !important;
}

/* Sprite page */
div.spritereport,div.iconsubmit,div.icontable{
background: #111111 !important;
border: 1px solid blue !important;}

/* Directory display */
.list {background: #000022 !important;
border: 1px solid blue !important;}

/* Frequently Asked Questions */
.faqentry {
background: #000022 !important;
color: #3333FF !important;
border: 1px solid blue !important;
}

/* Public box display */
.pubbox {background: #000044 !important;}
.prefs {background: #111111 !important;
color: #EEEEEE !important;}
.pk0 {background: #000033 !important; border: 1px solid #2F2F66 !important;}
.pk1 {background: #000022 !important; border: 1px solid #1F1F88 !important;}
.pk0:hover {background: #1F1F77 !important;}
.pk1:hover {background: #0F0F66 !important;}

}

/* SPRITES SUBDOMAIN */
@-moz-document domain("sprites.pokecheck.org") {

body {background: #000000 !important;
color: #FFFFFF !important;}

a:link,a:visited {color: #3333FF !important;}

.header {background: #111111 !important;
border-bottom: 1px solid blue !important;}
.tab {background: #000022 !important;}
.active,.tab:hover {background: #000066 !important;}

.fixbar {background: #222233 !important;}
}
