/************
  Pokécheck MeanGreen (DARK)
  © M.andrew 2012
  
  Changelog:
  2.8 - Cleanup, added account options page
  2.4 - Altered PM background color to blue to distinguish from TrueBlue
  2.3 - Updated FAQ to match class, skinned Pokécheck ad for "headless" display
  2.2 - Fixed header for most deposited stats
  2.1 - Skinned the FAQ
  2.0 - Styled the sprites subdomain
  1.2 - Fixed sprite submission page
  1.1 - Fixed betawarning border
  1.0 - Initial release (remix from BloodRed DARK 1.57)
************/

@namespace url(http://www.w3.org/1999/xhtml);

/* MAIN SITE*/
@-moz-document domain("www.pokecheck.org") {

/* Global elements */
body {
background: #000000 !important;
color: #FFFFFF !important;
}

a:link {color: #00FF00 !important;}
a:visited {color: #00FF00 !important;}

input,select {-moz-appearance: none !important;}

input[type="text"],input[type="password"],
input[type="button"],input[type="submit"],
input[type="checkbox"],select {
background: #000000 !important;
color: #FFFFFF !important;
border: 1px solid #005500 !important;
}

.warnmsg {color: #EFEF22 !important;}

/* Pokémon Selector */
.red {background: #220022 !important; color: #FFF !important;} /*DO NOT REMIX*/
.gold {background: #662200 !important; color: #FFF !important;} /*DO NOT REMIX*/
.emerald {background: #226600 !important; color: #FFF !important;} /*DO NOT REMIX*/
.platinium {background: #222 !important; color: #FFF !important;} /*DO NOT REMIX*/
.black {background: black !important; color: white !important;} /*DO NOT REMIX*/

/* Top section */
.mainheader {background: #111111 !important;
color: #EEEEEE !important;}

.dnsaddress {background: #0F5500 !important;
border: 1px solid #0FCC00 !important;}
.overflowbox {border: 1px solid #005500 !important;}

.disclaimer {color: #A5FF00 !important;}

/* Search/userinfo divider borders */
.mainheader,.linkbar {border-bottom: 1px solid green !important;}

/* Shoutbox styles */
.shoutbox {border: 1px solid #005500 !important;}
.msg0 {background: #003300 !important;}
.msg1 {background: #002200 !important;}
.admmsg0 {background: #00331F !important;}
.admmsg1 {background: #00221F !important;}
.privmsg {background: #101033 !important;}

/* img[src="i/refresh.png"] {
background: transparent url(http://i41.tinypic.com/vdzfkj.png) no-repeat top left !important;
height: 0 !important;
overflow: hidden !important;
} */

/* Fixed feedback bar */
.fixedbar {background: #223322 !important;}

/* Sortable Pokémon tables/box info */
.sortable {border: 1px solid green !important}
.pklisthdr {background: #225522 !important;}
.row0 {background: #003300 !important;}
.row1 {background: #002200 !important;}
.row0:hover {background:#1F771F !important;}
.row1:hover {background:#0F660F !important;}

/* Pokémon stats table */
.dhead {
background: #114400 !important;
border: 1px solid #009900 !important;
}
.dblock {
background: #002200 !important;
border: 1px solid #00CC00 !important;
}

.markings{background: #000 !important;}
.stattable,.movetable {background: #001100 !important;}
.statheader {background: #225522 !important;}
.st0,.move0 {background: #003300 !important;}
.st1,.move1 {background: #002200 !important;}
.sthp {background: #204F00 !important;}
.cont {color: #EEEEEE !important;}

.headless {
background: #002200 !important;
color: #00FF00 !important;
border: 1px solid green !important;
}


/* Nature stat colors */
.up {color: #9F0033 !important;} /*DO NOT REMIX*/
.down {color: #00669F !important;} /*DO NOT REMIX*/

/* Legality analysis */
.betawarn {background: #0C1100 !important; border: 1px solid #339900 !important;}
.legaltable {background: #227722 !important;}
.legalheader {background: #1C5500 !important;}
.legal0 {background: #003300 !important;}
.legal1 {background: #002200 !important;}
.valid {color: #00FF00 !important;} /*DO NOT REMIX*/
.warning {color: #FFA500 !important;} /*DO NOT REMIX*/
.error {color: #FF0000 !important;} /*DO NOT REMIX*/

/* ZIP notification */
.zipnotice {
background: #081100 !important;
color: #CCEEEE !important;
border: 1px solid #00CC00 !important;
}

/* Sprite page */
.spritereport,.iconsubmit,.icontable{
background: #111111 !important;
border: 1px solid green !important;}

/* Directory display */
.list {background: #002200 !important;
border: 1px solid green !important;}

/* Frequently Asked Questions */
.faqentry {
background: #002200 !important;
color: #00FF00 !important;
border: 1px solid green !important;
}

/* Public box display / account options */
.pubbox {background: #004400 !important;}
.prefs {background: #111111 !important;
color: #EEEEEE !important;}
.pk0 {background: #003300 !important; border: 1px solid #2F662F !important;}
.pk1 {background: #002200 !important; border: 1px solid #1F881F !important;}
.pk0:hover {background: #1F771F !important;}
.pk1:hover {background: #0F660F !important;}

}

/* SPRITES SUBDOMAIN */
@-moz-document domain("sprites.pokecheck.org") {

body {background: #000000 !important;
color: #FFFFFF !important;}

a:link,a:visited {color: #00FF00 !important;}

.header {background: #111111 !important;
border-bottom: 1px solid green !important;}
.tab {background: #002200 !important;}
.active,.tab:hover {background: #006600 !important;}

.fixbar {background: #223322 !important;}
}
