css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.baidu.com") {

	html{
		background:url(http://hiphotos.baidu.com/soinlia/pic/item/368ab0dda9ec8a1362744aaef703918fa1ecc0be.jpg) no-repeat center center fixed !important;
		background-size:100% auto !important;
	}
	body{
/*		background:rgba(255,255,255,0.5) !important;*/
		background:transparent !important;
	}

	#lg{
		background-image: url(http://hiphotos.baidu.com/soinlia/pic/item/c8d19810b31bb0519deb2299367adab448ede0d9.jpg);
		background-repeat: no-repeat !important;
		background-position: center center !important;
		opacity:0.9 !important;
	}
	#lg img{
/*		visibility:hidden;*/
		opacity:0 !important;
	}

	.nv a img {
		border-radius:5px 5px 0px 0px !important;
		background:rgba(255,255,255,0.3) !important;
		opacity:0.8 !important;
	}


	form[action] input[name=wd]{background:rgba(255,255,255,0.3) !important;}
	.btn_wr {background: transparent !important;}
	.s_btn_wr {background: none !important;}
	#fm input[name="kw"],#head input[name="kw"] {opacity:0.8 !important;}
	#fm input[type="button"],#fm input[type="submit"],
	#head input[type="button"],#head input[type="submit"]{
		-moz-appearance:none !important;
		color:transparent !important;
		margin:0px 32px 0px -6px !important;
		border:1px solid #bbb !important;
		border-radius:0px 5px 5px 0px !important;
		background:url(data:image/png;base64,) no-repeat center center, -moz-linear-gradient(top, rgba(68,68,68,1) 0%, rgba(102,102,102,1) 50%, rgba(60,60,60,1) 51%, rgba(68,68,68,1) 71%, rgba(51,51,51,1) 100%) !important;
		width: 95px !important;
		height: 32px !important;
	}
	#fm input[type="button"]:hover,#fm input[type="submit"]:hover,
	#head input[type="button"]:hover,#head input[type="submit"]:hover{
		box-shadow: 0px 0px 1px 0px gray !important;
	}
	#fm input[type="button"]:focus,#fm input[type="submit"]:focus,
	#head input[type="button"]:focus,#head input[type="submit"]:focus{
		box-shadow: 0px 0px 1px 0px gray !important;
	}



	div[class='mod-sc s-mod']{opacity:0.8 !important;}
	div[class='mod-sc s-mod']:hover{opacity:1.0 !important;}

	#container table {
		border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.8) !important;
	}

}
