css:
@-moz-document regexp("https?://(www\.)?youtube.com/(feed/.*)?"){
 .branded-page-v2-col-container {
   background-position:-1178px !important;
   border-right:1px solid #E2E2E2 !important;
 }
 .branded-page-v2-secondary-col {
   display:none !important;
 }
 .feed-author-bubble {
   float:right !important;
   background-image:none !important;
   margin-right:0 !important;
   width:28px !important;
   height:28px !important;
 }
 .feed-item-dismissal {
   padding:10px 0 !important;
 }
 .feed-item-container {
   padding:0 16px !important;
 }
 .feed-item-container.first {
   padding-top:3px !important;
 }
 .feed-item-main {
   padding-bottom:0px !important;
   margin-top:10px !important;
   min-height:83px !important;
 }
 .feed-item-author {
   margin:0 !important;
 }
 .feed-item-action-menu {
   right:54px !important;
 }
 .feed-item-header, .feed-item-content-wrapper {
   margin-left:128px !important;
   margin-bottom:10px !important;
 }
 .feed-item-thumb {
   position:absolute !important;
   bottom:10px; left:16px;
 }
 .feed-item-thumb .video-thumb {
   width:118px !important;
   height:72px !important;
 }
 .feed-item-thumb .video-thumb img {
   width:128px !important;
 }
 .feed-item-content > h4 {
   display:inline !important;
   line-height:normal;
 }
 .feed-item-content > h4 > a, .feed-item-content > .metadata {
   display:inline !important;
 }
 .feed-item-content > .metadata > .view-count {
   vertical-align:baseline !important;
 }
 .feed-item-content > .metadata > .description {
   margin-top:4px !important;
  }
  .branded-page-v2-col-container-bottom-border {
    width:100%;
  }
}
