css:
/** Habr Zen-Comment v2.18_2012-03-17, FF3.6+, Opera11+, Safari5, Chrome, IE8-9
 * http://userstyles.org/styles/36690
 * Author: spmbt, http://spmbt.habr.ru/
 */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("habrahabr.ru"){

/*начало стилей для не-Firefox*/
/*для лучшего контраста без HabrAjax - раскомментируйте* /.info a.username{color:#59d!important}/* */
body{text-align: inherit!important; font-family: Verdana,sans-serif!important}
#layout{width:auto!important;
	min-width: 314px!important;
	max-width: 1420px!important;
	margin: 0 auto!important;
	overflow: hidden;/*Chrome need it <=no break-word*/
	padding: 6px 0 0!important}
#wrapper{margin: -15px 0 1px!important; width: auto!important; padding: 20px 0 4px 6px!important; overflow: hidden; min-width: 314px!important}
/*дзен-режим: поставить/убрать пробел после звездочки:* /
.comments.c2 >.comment_item .info .voting,#comments >.comment_item .info .voting{display:block!important}
.comments.c2 >.comment_item .info time, #comments >.comment_item .info time{visibility: visible}
.comments.c2 .comment_item .info a.username, #comments .comment_item .info a.username
	{visibility: visible!important}
.comments.c2 .comment_item, #comments .comment_item{
	margin-top: 1px!important;margin-right:-2px!important;
	padding-top: 9px!important;
	border-right: 2px solid #fff!important;
	background:#fff!important}
#comments:hover >.comment_item:not(:hover) .comment_item:last-child:not(:only-of-type) >.reply_comments{border-left: 2px solid #fff!important}
#comments:hover >.comment_item:not(:hover) .reply{border-top: 1px solid #fff!important}
.comment_item .message{width: 100%}
/* /конец дзен-реж.*/
.header, #header{width: 100%;
	height: 13px;
	top: -5px;
	min-width: 314px!important;
	margin: 0;
	padding: 0!important}
.panel-personal,
#header .userpanel{
	position: fixed!important;
	top: 0!important;
	right: 0;
	left: auto!important;
	z-index: 10026;/*!is indication of ZenComment styles!*/
	float: right;
	width: auto!important;
	height: 15px;
	padding: 0 6px 2px!important;
	font-size: 12px;
	border-bottom: 1px solid #999!important;
	border-left: 2px solid #666!important;
		-moz-border-radius-bottomleft:5px;-webkit-border-bottom-left-radius:5px;border-bottom-left-radius:5px;
	background-color: #ccc;
	opacity: 0.5;filter: alpha(opacity=50);
	*width: 150px!important}
.panel-personal:hover,
#header .userpanel:hover{background-color: #fff;
	*width: 350px!important;
	opacity: 1;filter: none}
.panel-personal dt,
#header .userpanel .top{
	float: right;
	display: inline-block;
	white-space: nowrap;
	text-align: right}
.panel-personal dt .habrauser,
#header .userpanel .top a.username.dark{margin-left: 1px}
.panel-personal dt a.nav-settings,
#header .userpanel .top a.nav-settings{
	display: none;
	float: left;
	margin: 1.3em 6px 0!important}
.panel-personal:hover dt a.nav-settings,
#header .userpanel:hover .top a.nav-settings{display: inline-block;
	position: relative; top: -0em}
*|html[xmlns*=""] .panel-personal:hover dt a.nav-settings, *|html[xmlns*=""] #header .userpanel:hover .top a.nav-settings{position: relative; top: -1.3em}/*Op*/

.panel-personal dt a[href*="login"],
#header .userpanel .top a[href*="login"]{display: inline-block!important; float: right}
.panel-personal dt a[href*="logout"],
#header .userpanel .top a[href*="logout"]{/*знак "logout"*/
	display: inline-block;
	width: 12px;
	margin-right: 3px!important;
	background: no-repeat 0% 50% url(data:image/gif;base64,R0lGODlhDAAKAIABAN3d3f///yH5BAEAAAEALAAAAAAMAAoAAAIYhB+plhyw4JPOqQbZ2XvyfmGTOJZVBF4FADs=);
	color: transparent!important}
.panel-personal dd,
#header .userpanel .bottom{float: left}
.panel-personal dd a[href*="register"],#header .userpanel a[href*="register"]{display: none;float: left}
.panel-personal dd a:not(.habrauser-newmail), .userpanel .bottom a:not(.habrauser-newmail){display: none; margin: 0 0 0 6px!important}
.panel-personal:hover dd a, .userpanel:hover .bottom a{display: inline}
.panel-personal:hover dd a[href*="register"],
#header .userpanel:hover a[href*="register"]{display: block}
.panel-personal dd a.habrauser-newmail, .userpanel .bottom a.newmail{
	display: inline-block!important;
	margin: 0 4px!important;
	background-color: #eee!important;
	background-position: 3px 3px!important;
	padding: 0 4px 3px;
	border: 1px solid #999;-moz-border-radius:5px;-webkit-border-radius:5px;border-radius:5px}
.panel-personal .habrauser-charge,
#header .userpanel .charge{
	position: absolute;
	display: none!important;
	width: 9em;
	right: 3px;
	margin: 0!important;
	padding: 0 3px 1px!important;
	border: 1px solid #999!important;-moz-border-radius:3px;-webkit-border-radius:3px;border-radius:3px;
	text-align: right;
	font-size: 9px;
	font-family: Tahoma,Arial;
	color: #59d!important;
	opacity: 0.5!important}
.panel-personal:hover .habrauser-charge,
#header .userpanel:hover .charge{
	float: right!important;
	display: inline-block!important;
	white-space: normal;
	top: 16px;
	background: #fff}
.panel-personal .habrauser-charge:hover,
#header .userpanel .charge:hover{opacity: 1!important}
.header .logo,
#header a.logo{
	position: fixed;
	z-index: 10027;
	width: 86px!important;
	height: 78px!important;
	left: 0;
	top: 0;
	margin: -60px -55px 0!important;
	background-position: 50% -144px!important;
		-moz-border-radius-bottomright:5px;-webkit-border-bottom-right-radius:5px;border-bottom-right-radius:5px;
	background-color: #ccc!important;
	opacity: 0.5;filter: alpha(opacity=50)}
.header .logo:hover,
#header .logo:hover{background-position: 50% -274px!important}
.panel-search,
#header .search{
	position: absolute!important;
	top: -1px!important;
	right: 178px!important;
	z-index: 25!important;
	width: 240px!important;
	margin: 0!important}
.panel-search .in-text,
#header .search #search_form input[name="q"]{
	width: 235px!important;
	height: 17px!important;
	padding: 0 3px 1px!important;
	-moz-border-radius:3px;border-radius:3px;-webkit-border-radius:3px;
	-moz-box-shadow: 0 2px 10px #eee inset;box-shadow: 0 2px 10px #eee inset;-webkit-box-shadow: 0 2px 10px #eee inset;
	font-size: 14px!important;
	opacity: 0.8;filter: alpha(opacity=80)}
.panel-search .in-text:focus,
#header .search input[name="q"]:focus{
	border-color: #5699D8!important;
	outline: 0!important;
	-moz-box-shadow: 0 2px 10px #eee inset, 0 0 2px #5ec6fb;box-shadow: 0 2px 10px #eee inset, 0 0 2px #5ec6fb;-webkit-box-shadow: 0 2px 10px #eee inset, 0 0 2px #5ec6fb;
	color:#333!important;
	opacity: 1;filter: none}
.panel-search .js-autosuggest-output:empty,
.panel-search input[type="submit"],
#header .search #search_form input[type="submit"]{display:none!important;
	float: right!important;
	margin-left: -1px!important;
	padding: 1px 3px!important;
	color:#666!important}
.panel-search input[type="submit"]:not([value=""]),
#header .search #search_form input[type="submit"]:not([value=""]){clear: both}
form#search_form,
.panel-search >p{width: 273px!important;height:21px}
#header form#search_form:hover input[type="submit"],
.panel-search >p:hover #do{display:block!important}
.panel-search .panel-search-label span{font-size: 13px; cursor: text!important; top: 2px!important}
.js-autosuggest-output-container{position: absolute!important}
 .autocomplete
,.panel-search .js-autosuggest-output{
	z-index: 25!important;
	position: absolute!important;
	margin: -2px 0 0 1px!important;
	border: 0!important;
	-moz-border-radius:0 0 5px 5px;border-radius:0 0 5px 5px;-webkit-border-radius:0 0 5px 5px;
	width: 244px!important;
	-moz-box-shadow: 0 0 10px rgba(0,0,0,.3);box-shadow: 0 0 10px rgba(0,0,0,.3);-webkit-box-shadow: 0 0 10px rgba(0,0,0,.3)}
 .autocomplete
,.panel-search .js-autosuggest-output{*border: 1px solid #e7e7e7!important}/*IE*/
 .autocomplete div
,.panel-search .js-autosuggest-output div{position: relative!important;border: 0!important;z-index: 50!important}
 .autocomplete div:hover
,.panel-search .js-autosuggest-output div:hover{border-bottom-color: #fff!important}
 .autocomplete >div:last-child
,.panel-search .js-autosuggest-output >div:last-child{-moz-border-radius: 0 0 5px 5px;border-radius: 0 0 5px 5px;-webkit-border-radius: 0 0 5px 5px}
.header .panel-nav-top,
#header .main_menu{
	position: fixed!important;
	width: 16px!important;
	height: 20px;
	min-width: 1.3em!important;
	top: 0; left: 0;
	overflow: hidden;
	z-index: 10027;
	margin: -5px 0 0 7px!important;
	padding: 1px 0 0!important;
	border: 1px solid #999!important;
	border-right: 2px solid #666!important;
		-moz-border-radius-bottomright:5px;-webkit-border-bottom-right-radius:5px;border-bottom-right-radius:5px;
	font-size: 18px!important;
	background-color: #ccc;
	opacity: 0.5;filter: alpha(opacity=50)}
.header .panel-nav-top:hover,
#header .main_menu:hover{width: auto;
	overflow: visible;
	width: auto!important;
	background-color: #fff;
	opacity: 1;filter: none}
.header .panel-nav-top li,
#header .main_menu a{font-size: inherit!important}
.header .panel-nav-top li,
#header .main_menu a {font-size: 18px!important;margin-right: 10px!important}
 .panel-nav-top a[href*="companies"]
,.main_menu a[href*="companies"]{display: block; width: 1.25ex; overflow: hidden}
 .panel-nav-top a:hover[href*="companies"]
,.main_menu a:hover[href*="companies"]{width: auto; margin-right: 3px!important}

.profile-header{
	height: 28px!important;
	padding: 13px 0 3px 5px!important;
	font-size: inherit!important}
.profile-header.company-header{height: 22px!important}
.profile-header .profile-actions dt{font-size: 220%!important;padding-top: 6px!important}
.profile-header h1{margin: 0!important}
.profile-header h1 .favicon{margin-top: 6px!important}
.profile-header dt{margin-bottom: -3px!important}

#js-addFan, #js-addWorker{margin: 6px 0 0!important}
.profile-karma-holder{margin: -10px 8px!important}
.company-header .profile-karma-holder{margin: 0 12px 0 0!important}
.profile-header .vote_holder .mark span{position: relative!important; top: 5px!important; font-size: 75%!important}
.profile-header .profile-actions dt,
.profile-karma-holder .karma{background: transparent!important}
.profile-karma-holder >dl >dt,
.profile-header .habraforce >dt{font-size: 9px!important}
.profile-header .habraforce .number{
	height: 30px!important;
	margin: -13px 0 -4px!important;
	padding: 15px 0 0 0!important;
	font-size: 220%!important}
.profile-header .karma .total{margin-top: -2px!important}
.profile-header .karma .total em{padding: 0 2px; background: #fff; font-size: 9px!important}
.habraforce{background: transparent!important}
.profile-header .karma a{margin-top: 4px!important}
.profile-header .karma dt, .profile-header .karma .total, .profile-header .habraforce dt, .profile-header .habraindex dt, .habraindex dt{color: #999!important}
.profile-header .habraindex .number{font-size: 200%!important}
.profile-header .karma dd.vote{overflow: visible!important}
.habrauserava img{border: 1px solid #ddd!important; margin-top: -2px!important}
.habrauserava b{height: 0!important}
.user-actions dd, .user-actions dt{-moz-border-radius:5px!important;-webkit-border-radius:5px!important;border-radius:5px!important}
.profile-actions-menu{padding: 2px 3px!important;-moz-border-radius:5px!important;-webkit-border-radius:5px!important;border-radius:5px!important;-moz-box-shadow:0 0 15px rgba(0,0,0,.3);-webkit-box-shadow:0 0 15px rgba(0,0,0,.3);box-shadow:0 0 15px rgba(0,0,0,.3);border-color: #397dbd!important}
.profile-actions-menu .cor-1px{-moz-border-radius:5px!important;border-radius:5px!important;-webkit-border-radius:5px!important;border: 0!important}

.popup-text-field{border:1px #ceceaf solid;-moz-border-radius:5px;-webkit-border-radius:5px;border-radius:5px;background-image: none!important;-moz-box-shadow:0 0 15px rgba(0,0,0,.3);-webkit-box-shadow:0 0 15px rgba(0,0,0,.3);box-shadow:0 0 15px rgba(0,0,0,.3)}
.popup-text-field div{background-image: none!important}

.user_profile{width: 99%!important}
.user_profile dl{margin-bottom:5px!important}
.user_profile .fullname{margin:0 0 5px!important;padding: 2px 0 0 36px!important}
.user_profile #people-tags li{margin-bottom: 0!important}
.user_profile .dl_logic_wrap{margin: 0 0 2px!important}
.user_profile .dl_logic_wrap ul, .user_profile .friends_list ul{max-height: 120px; overflow-y: auto}
.user_profile #people-tags a{height: auto!important; min-height: 28px}
.user_profile #people-tags a.habred{display: none!important}
.user_profile #people-tags li{padding-bottom: 5px!important}
.user_profile #people-tags{padding-top: 2px!important}
.user_profile dl{margin: 0 0 3px!important}
.page-navigation{overflow: inherit!important;margin-bottom: 5px!important}


.hub_header{margin: -6px 0 0 26px!important; padding:0!important}
.hub_header h2 a{font-size: 20px!important}
.hub_header h2 span.profiled_blog{z-index: 3}
.hub_header .hub-index .label{line-height:18px!important}
.hub_header .hub-index .value{font-size: 18px!important;line-height:6px!important}
.user_header h2.username a,
.hub_header .hub_title{color: #6da3bd!important}
.user_header h2.username, .user_header .karma .score .num, .user_header .rating .num{font-size: 24px!important}
.user_header h2.username a:hover,
.hub_header h2 .hub_title:hover{color: #84b18a!important}

#main-content,
.content_left{min-width: 74%; margin-right: 0!important; padding:0!important}
.content_left .add_post{position: relative; left: 35px;opacity: 0.5;filter: alpha(opacity=50)}
.content_left table.menu{word-wrap:break-word}
.content_left table.menu tr td a{padding: 9px 0 7px!important;
	display: inline-block!important;
	width: 100%;
	font-size: inherit!important}
.content_left .submenu{margin-bottom: 4px!important}

/*содержание (в статье или в группе .posts)*/
.content_left .post {margin:12px 0 32px!important}
.content_left .company_post .post{margin-bottom: -5px!important}
h2.entry-title{padding: 0!important}
#questionslist h2.entry-title,
.question_hentry h2.single-entry-title,
#main-content .hentry .entry-title,
.content_left .post h1.title{
	margin: 3px 0 -3px!important;
	padding-left: 25px!important;
	font: 16px/118% normal Verdana,Tahoma,sans-serif!important}
#questionslist h2.entry-title{margin-bottom: 1.2em!important}
#main-content .hentry .entry-title a,
#questionslist h2.entry-title a{text-decoration: none!important}
h2.entry-title span{color:#48A!important}
#questionslist h2.entry-title a:hover,
#main-content .hentry .entry-title a:hover span,

#main-content .hentry .entry-title a,
#questionslist h2.entry-title a,
.question_hentry h2.single-entry-title span,
#main-content .items h2.entry-title a,
#main-page.postoffice h2.entry-title,
.content_left .post h1.title .post_title{
	line-height: 16px;
	font-size: 16px;
	text-decoration: none!important;
	color: #48a!important}
.post h1.title .locked{background-position: 2px 0px!important}
#main-content .hentry .entry-title a:visited,
#questionslist h2.entry-title a:visited,
#main-content .items h2.entry-title a:visited,
.content_left .post h1.title .post_title:visited{color: #b99!important}

.hentry.question_hentry +.post-comments{margin-bottom: -1.8em!important}
.question_hentry h2.single-entry-title span,
.content_left .company_post h1.title .post_title{font-size: 18px!important;font-weight: normal}
#main-content .hentry .entry-title a:hover,
#main-content .items h2.entry-title a:hover,
.content_left :not(.company_post) .post h1.title .post_title:hover{color: #84b18a!important}
.content_left .post h1.title a[href="/sandbox/"],
div.hentry h2.entry-title sup, .title sup{
	padding: 0 5px;
	font: 13px/118% normal Verdana,Tahoma,sans-serif !important;
	font-size-adjust: 0.45;
	font-variant: small-caps;
	vertical-align: 5px;
}
.content_left .post .hubs{float: right; position: relative; z-index:2; margin: 2px 8px -4px 48px!important;opacity: 0.7}
.content_left .hubs +.content{clear: both}

.content .poll table.answer{margin: 0!important}
.content .poll .answer .label,.content .poll .answer td{padding: 0!important}
.post .content ul li, .post .content ol li {position: relative; left: 1em;	margin: 0 0.5em 0 0!important;padding-left:0.5em!important}
.hentry >.content,
.content_left .event,
.content_left .post .content{
	max-width: 1200px;
	min-height: 2px;
	margin: 6px 6px 6px 9px!important;
	padding-left: 24px!important;
	word-wrap: break-word;
	line-height: 1.31!important;
	font-size: 13px!important}
.comment_item .message img[align="left"],.comment_item .message img[align="right"],
.content img[align="left"],.content img[align="right"]{max-width: 50%!important}
.comment_item .message img[align="left"],.content img[align="left"]{margin: 2px 15px 2px -2px!important}
.comment_item .message img[align="right"],.content img[align="right"]{margin: 2px -2px 2px 15px!important}
.post .content ul,
.post .content ol{margin: 6px 9px 6px 18px!important;list-style: decimal inside none!important; text-indent:-0.75em}

 #wrapper .comment_item .message pre
,#wrapper .comment_item .message code
,.content_left #comments .comment_item .message pre
,.content_left #comments .comment_item .message code
,.content_left .post .content pre
,.content_left .post .content code{
	max-width: 100%;
	overflow: auto;
	line-height: 1.3!important;
	font-size:100%!important}
 .content_left .post .content br+br
,.content_left .post .content h4+br
,.content_left .post .content pre+br
	{display: inline-block!important; height: 10px!important;line-height: 10px!important}
*|html[xmlns*=""] .content_left .post .content br+br
,.content_left .post .content h4+br
,.content_left .post .content pre+br
	{display: block!important}/*Op*/
 .comment_item >.message br+br, .comment_item >.message blockquote+br
,ul.hentry .entry-content .entry-content-only br+br
,ul.hentry .entry-content .entry-content-only blockquote+br
	{display: inline-block!important;height: 6px!important;line-height: 6px!important}
*|html[xmlns*=""] .comment_item >.message br+br, .comment_item >.message blockquote+br
,ul.hentry .entry-content .entry-content-only br+br
,ul.hentry .entry-content .entry-content-only blockquote+br
	{display: block!important}/*Op*/
.entry-content-only blockquote,
.comment_item .message blockquote{margin: 2px 0!important;padding-left: 6px!important}

.content .vSpace,.comments .vSpace, .content .vSpace3,.comments .vSpace3, #comments .vSpace, #comments .vSpace3
	{width: 3.6em; height: 4px; margin: -1px 0 -3px 1.6em!important; background: #ddd}
.content .vSpace3,.comments .vSpace3, #comments .vSpace3{width: 2.8em; height: 2px; margin: 0 0 -2px 1.6em!important}

.content_left .post .content iframe{max-width:100%}
.content_left .post .content a[name="habracut"] .btnBack{margin: 1px 12px -1px 0; padding-left: 3px; border: 1px solid #9bc; border-radius: 3px; background: #cdd; cursor: pointer}
.content_left .post .content a[name="habracut"] .btnBack.inln{display: inline-block; width: 1.2em; height: 16px; margin: 0 2px; overflow: hidden; vertical-align: middle}

div.hentry .tags,
#layout >.content_left .post div.tags,
.hentry.question_hentry .tags,
.content_left .post ul.tags{
	float: right;
	max-width: 1200px;
	margin: -10px 10px -9px 52px!important;
	padding: .2em 0 3px 19px!important;
	background-position: 2px 3px!important;
	background-color: transparent!important;
	color:#666!important;
	opacity: 0.8;filter: alpha(opacity=80)}
.hentry.question_hentry .tags a,
ul.tags li a,
ul.tags li{font-size: 10px!important}

div.hentry >.entry-info.vote_holder,
.content_left .post .infopanel{
	min-width: 93%;
	min-height: 19px;
	top: 2px;
	margin: 7px 10px -26px 17px!important;
	padding: 0 10px 0!important;
	border-color: #d7d2b6!important;
		-moz-border-radius:6px;-webkit-border-radius:6px;border-radius:6px;
	background-color: #fdfdf4}
.i-am-your-father-luke .post .infopanel{width: 94%; margin: 3px 0 0 4px!important;border: 1px solid #D7D2B6;border-radius: 5px;font-size:11px;line-height:1.7}
.company_post .post .infopanel{min-width: 0%;margin-top: 11px!important}
.entry-info.vote_holder,
.content_left .post .infopanel >div{
	margin-top: 0!important;margin-bottom: 0!important;
	padding-top: 0!important;padding-bottom: 0!important;
	line-height: 16px!important;
	background-position: 0 0!important}
div.informative span{padding-top: 2px!important}
 .content_left .post .infopanel .published
,.content_left .post .infopanel .voting{margin-right: 7px!important}
.content_left .post .infopanel .voting .mark{font-weight: normal!important}
.entry-info .entry-info-wrap .published,
.content_left .post .infopanel .published{font-size: 10px!important}
.content_left .post .infopanel .favorite{padding-right: 3px!important}
.content_left .post .infopanel .favs_count{height: 1.3em!important;padding-right: 12px!important}
.content_left .post .infopanel a,
.entry-info .favs_count, .entry-info .voting .mark span, .entry-info .comments{font-weight: normal!important;font-size: 12px!important;line-height:12px!important}
.entry-info .original-author a,
.content_left .infopanel .original-author{color:#a33!important}
.post .infopanel .author .rating{vertical-align: -3px!important}
.post .infopanel .comments{margin-left: 7px!important}
.page-nav #nav-pages,
/*в группе ".posts: .posts_list .posts*/ .page-nav{
