css:
@-moz-document url("http://www.tumblr.com/following"), url("http://www.tumblr.com/likes"), url("http://www.tumblr.com/inbox"), url("http://www.tumblr.com/blog/queue"), url("http://www.tumblr.com/blog/drafts"), url("http://www.tumblr.com/blog/followers"), url("http://www.tumblr.com/blog/settings"), url-prefix("http://www.tumblr.com"), url-prefix("http://www.tumblr.com/tagged/") {
* {
-moz-box-shadow:none!important;
-webkit-box-shadow:none!important;
box-shadow:none!important;
text-shadow:none!important;
}

#dashboard_index,#dashboard_settings,#dashboard_transcoding,#dashboard_inbox,#dashboard_edit_post,#dashboard_submissions,#dashboard_drafts,#dashboard_post_queue,#dashboard_likes,#dashboard_followers,#dashboard_following,#dashboard_directory,#dashboard_lookup_contacts,#main_doc_page,#recommended_tumblelogs,#dashboard_create_tumblelog,#dashboard_members,#main_meetups,#main_about,#main_explore,#dashboard_goodies,#main_developers,#main_themes,#dashboard_manage_themes,#main_edit_theme,#main_jobs,#main_help,#main_policy,#tagged_display {
background:url(http://i40.tinypic.com/p3hcm.jpg) top right #000 repeat!important;
}

#logo {
background:url(http://i44.tinypic.com/1zgq0eh.jpg) no-repeat!important;
height:0!important;
padding-left:230px!important;
padding-top:52px!important;
width:0!important;
}

#footer,#footer a {
background-image:none!important;
color:#666!important;
}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(0,0,0,0.5)!important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
-moz-border-radius:20px;
-webkit-border-radius:20px;
background:rgba(0,0,0,0.75)!important;
border-radius:20px;
margin:auto;
padding-bottom:20px;
padding-top:20px;
}

h5:hover {
color:#e6e6e6!important;
}

a:hover {
color:#aeaeae!important;
}

img#content_top,img#content_bottom {
display:none;
}

#auto_pagination_loader {
background:rgba(56,161,207,0.00)!important;
}

.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]) {
background:none!important;
border-bottom:solid 11px #FFF!important;
border-left:11px solid transparent!important;
border-right:11px solid transparent!important;
height:0!important;
margin-top:0!important;
width:0!important;
}

.selection_nipple:not([class="selection_nipple white"]) {
border-bottom:solid 11px #111!important;
}

.avatar_frame {
background-color:transparent!important;
border:none!important;
}

.post.photo_post .photo,.post.photo_post .video_thumbnail {
-moz-box-shadow:1px 1px 3px #000!important;
border-radius:16px!important;
}

#posts .notification a:not(.avatar_frame) {
border-bottom:none!important;
}

#nav .nav_item.active {
background-color:#3e3e40;
}

#right_column {
color:#3e3e40;
}

#right_column .controls_section .selected {
background:rgba(255,255,255,0.2)!important;
background-image:none!important;
border-top:1px solid rgba(0,0,0,.09)!important;
}

#posts .post .post_avatar {
background-color:#222!important;
border:0!important;
}

#left_column #posts .notification .avatar {
background-color:#222!important;
border-color:#000!important;
}

#left_column #posts .post .permalink {
background:rgba(0,0,0,0.8)!important;
background-color:#3e3e40;
}

#nav .nav_item.active,#nav .nav_item .nav_menu,#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#3e3e40!important;
background-image:none!important;
border:0!important;
}

#left_column #posts .notification {
background:rgba(0,0,0,0.8)!important;
border-color:#333!important;
color:#666!important;
}

#posts .notification.alt .nipple {
border-right:6px solid #333!important;
}

#posts .notification .nipple {
border-bottom:6px solid transparent;
border-right:6px solid #333!important;
border-top:6px solid transparent;
height:0;
left:-5px;
position:absolute;
top:10px;
width:0;
}

h1,h1:hover,h2,h2:hover,h3,h3:hover,h4,h4:hover,h5,h6,h6:hover {
color:#666!important;
}

a.explore_more_tags,a.add_and_remove,a.customize,a.mass_editor,#right_column .controls_section .recessed {
background-color:transparent!important;
}

.content.is_media,.mask.top,.mask.bottom,li.selected,li.recessed,a.active,div.post_filter_tabs a,.content.is_media,.mask.top,.mask.bottom,div#right_column,div#right_column:after,#help_search {
background:none!important;
}

.login.sign_up,A[href=/forgot_password],.notification_type_icon,.notification_type_icon,#right_column::after {
display:none!important;
}

#new_post_notice_container,#new_post_notice_container a {
color:#fff!important;
}
}
