*{
margin     :0    !important;
padding    :0    !important;
background :none !important;
border     :none !important;
font-size  :1em  !important;
clear      :both !important;
}

div#container div#banner span.description,
div#container table,
div#container h2,
div#container div.navi,
div#container div.blog div.posted,
div#container div.blog div.text a[href^="http://www.amazon.co.jp/"],
div#container div#links,
div#container div#footer,
div#container div#comments,
span#drecom_rss_blog_list_ad,
span.drecomrss_blog_list_item,
span.drecomrss_blog_list_powered{
display:none !important
}

div#container,div#content{width:98% !important;}

div#banner   {height :1.6em   !important; margin   :0.5em 1em !important;}
div#banner h1{float  :left    !important; font-size:1.6em     !important;}

h3 a      {background-color:#ffc0cb !important; display:block !important; padding:0.5em 1em !important;}
h3 a:hover{background-color:#e40d32 !important; color:#fff    !important;}

div#container div#content div        {height:inherit !important;}
div#container div#content br         {display:none !important;}
div#container div#content div.text br{display:block !important;}

div#container div#content{
margin :0.5em 1em         !important;
border :2px solid #ffc0cb !important;
}

div#container div#content div.text{
padding     :0.5em 1em !important;
color       :#000000   !important;
line-height :1.4       !important;
}

div#container div#content div.blog{margin-bottom:2em !important;}




}
