css:
@namespace url(http://www.w3.org/1999/xhtml);


/*******************************************

     SHIPS PAGE
     
*******************************************/
@-moz-document regexp("https?://[0-9]*\\.?game\\.nemexia\\.com/ships\\.php") {

/** Ads **/
a.orderCredits {
    display: none !important;
}




#structuresList {
    background-color: black !important;
}

.structureItem {
    width: 704px !important;
    height: 20px !important;
    /*background: transparent !important;*/
    padding: 10px 0px 10px 0px !important;
    border-bottom: 1px dotted #828282 !important;
    position: relative !important;
    display: block !important;
    margin: 0px !important;
}

.structureItem:nth-child(odd) { 
    background: #1B2759 !important;
}
.structureItem:nth-child(even) {
    background: #04091D !important;
}

.disabledShipMessage {
    left: 200px !important;
}


.requirement, .disabledShipMessage {
    background: #FF5454 !important;
    color: black !important;
    border: 1px solid #5B0B0B !important;
    position: absolute !important;
    padding: 0px !important;
    font-size: 1em !important;
    display: inline !important;
    top: 21px !important;
    left: 450px !important;
    z-index: 20 !important;
}

.requirement a, br { display: none !important; }




/*.shipInfo h3 a:first-child {*/
.shipInfo > h3 > a {
    position: relative !important;
    display: inline-block !important;
    width: 100px !important;
    left: 0px !important;
    background-image: none !important;
}

.shipInfo > h3 {
    display: inline !important;
    padding-left: 75px !important;
}

/* The cost of the ship */
.shipInfo > ul > li {
    display: inline-block !important;
    position: relative !important;
    width: 40px !important;
    min-width: 40px !important;
   /* background-color: blue !important;*/
    top: 0px !important;
    margin: 0px !important;
    padding: 0px 0px 0px 30px !important;
    
}


.itemCurrentCount {
    font-size: 1.5em !important;
    font-weight: bold !important;
}

/* The cost of the ship */
.shipInfo > ul {
    display: inline !important;
    top: 0px !important;
    left: 0px !important;
    position: relative !important;
}

/* The training form */
.shipInfo form {
    display: inline !important;
    position: relative !important;
}

.maxAmmount {
    top: 0px !important;
    left: 00px !important;
    display: inline !important;
    position: relative !important;
    width: auto !important;   
    background: #080E1C !important;
    z-index: 20 !important;

}

.action {
    top: 0px !important;
    left: 00px !important;
    display: inline !important;
    position: relative !important;
    width: auto !important;
}
.action a {
    top: 0px !important;
    left: 00px !important;
    display: inline !important;
    position: relative !important;
    width: auto !important;   
    background: none !important;
    padding: 0px !important;
    margin: 0px !important;
    text-shadow: none !important;
    background-color: orange !important;
}
.action input { margin: 0px !important; }

.shipTime.repair {
    top: 19px !important;
}



.shipTime { 
    top: 7px !important;
    left: 560px !important;
    display: inline !important;
    position: absolute !important;
    width: 150px !important;
    background: none !important;
    
}


/**  Need to absolute position the ship image*/
.thumbnail a { position: relative; }
.thumbnail img {
    position: absolute !important;
    top: -10px !important;
    left: 40px !important;
    width: 20px !important;
    height: 20px !important;
    background-position: -40px 20px !important;
}

.thumbnail {
    display: inline !important;
}

}




/********************

        LABORATORY   and STARPORT
        
*********************/
@-moz-document regexp("https?://[0-9]*\\.?game\\.nemexia\\.com/laboratory\\.php"),
                regexp("https?://[0-9]*\\.?game\\.nemexia\\.com/ship_upgrade\\.php") {

#mainFrame { 

    position: relative !important;
}

.box { 
    background: none !important;
}

#researchesList {
    margin: 0px !important;
    height: 527px !important;
    vertical-align: top !important;
}

#researchesList .researchItem {
    display: block !important;
    height: 40px !important;
    margin: 0px !important;
    padding: 5px 0px 20px 5px !important;
    border-bottom: 1px dotted white;
    float: left !important;
}




.researchItem:nth-child(odd) { 
    background: #1B2759 !important;
}
.researchItem:nth-child(even) {
    background: #04091D !important;
}

.researchItem .thumbnail {
    float: none !important;
    width: 20px !important;
    height: 20px !important;
    /*display: none !important;*/
    background: none !important;
    margin: 0px !important;
    padding: 0px !important;
    display: inline !important;
}
.researchItem .thumbnail span
{
    background: none !important;
    width: 0px !important;
    display: inline !important;
}
.bar { 
    margin: 0px !important;
    display: inline !important;
    height: 1em !important;
    width: 0px !important;
    background: none !important;
    border: none !important;
}

.details { 
    margin: 0px !important;
    height: 25px !important;
    display: inline !important;
}


.neededResources > ul {
    float: right !important;
    clear: both !important;
}

.neededResources > ul li {
    position: relative !important;
    left: 0px !important;
    top: 0px !important;
    margin: 0px !important;
    display: inline-block !important;
    float: left !important;
    padding: 0px 0px 0px 25px !important;
}

#buildingInfo {
   clear: both !important;
}

a.cancelResearch {
    position: relative !important;
    left: 0px !important;
    top: -10px !important;
    display: inline-block !important;
    padding: 3px !important;
    margin: 15px 0px 0px 5px !important;
    float: none !important;
}

/** levle number */
.bar span b {
 font-size: 1.5em;
 padding: 2px;
 
}

.title {
    font-size: 20px !important;
    display: inline !important;
}

#scienceReqs {
    display: block !important;
}

.title, .description,
.neededResources,
.neededResources > *,
.upgradeContainer,

#scienceReqs * {
    position: relative !important;
    left: 0px !important;
    top: 0px !important;
    display: inline !important;
    padding: 0px !important;
    margin: 0px !important;
    float: none !important;

}

.singleScienceReq img {
    width: 25px !important;
    height: 25px !important;
}


/**
the warning message in the lab about researching once
ever
**/
#TabAdditional > div.scienceBox > div.message.information {
    position: relative !important;
    float: none !important;
    clear: both !important;
    height: 60px !important;
    max-height: 60px !important;
    display: block !important;
    top: 0px !important;
    margin: 10px 0px !important;
}

#TabBasic, #TabAdvanced, #TabMaster, #TabAdditional {

 display: block !important;
}

#TabBasic h1:first-child,
#TabAdvanced h1:first-child,
#TabMaster h1:first-child,
#TabAdditional h1:first-child,
ul.menu.laboratory li,
.details br {
    display: none !important;
}


.premiumButton { display: none !important; }

#researchesList.scienceBox {
    max-height: 275px !important;
}

#researchesList.scienceBox .researchItem .details a.doResearch {
    position: relative !important;
    left: 0px !important;
    top: 0px !important;
    display: inline-block !important;
    padding: 4px !important;
    margin: 0px !important;
    float: right !important;
    height: auto !important;
    width: auto !important;
    line-height: 1em !important;
    background: #3B5CB5 !important;
    border: 1px solid #143E6E !important;
    clear: both !important;
    z-index: 5 !important;
}
.doResearch br { display: none !important; }



a.action {
    position: relative !important;
    left: 0px !important;
    top: 0px !important;
    display: inline !important;
    padding: 0px !important;
    margin: 0px !important;
    float: none !important;

}

#mainFrame > h1 { display: none !important; }

/**
 starport specific stuff **/
 
 /** upgrade button */
 #researchesList .researchItem .details .title a.doResearch, #researchesList .researchItem .details .neededResources a.cancelResearch {
 
    top: 25px !important;
    left: 0px !important;
    background: #04091D !important;
    border: 1px solid #1B2759 !important;
    height: 15px !important;
    width: 120px !important;
    line-height: 1em !important;
    font-size: 12px !important;
    padding: 5px !important;
    margin: 0px !important;
 }

} /* close laboratory and starport  */

