css:
/*****  Chatty Profiles Style by Ashun  */


@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("chattyprofil.es") {


body {

    background-color: #262420 !important;
    color: white;
    
    }

#bodywrap {

    width: auto !important;
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
    background-color: black !important;
    background: black !important;
    border-style: ridge !important;
	border-color: #FF295E !important;
    border-width: 0px 0px 4px 0px !important;
  
  }
  
#masthead {

    background-image: url('http://cf.shacknews.com/images/hdr-bg.gif') !important;
    background-repeat: repeat-x !important;
    width: auto !important;
    height: 81px !important;
	border-style: ridge !important;
	border-color: #FF295E !important;
    border-width: 0px 0px 4px 0px !important;
    -moz-box-shadow: none !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;

  }
  
#masthead #altsitelinks {

    position: absolute;
    top: 89px;
    left: 530px;
    width: auto;
    border: 0px solid green ;
    float: right !important;
    background: none !important;
    margin-right: -1px;
    -moz-box-shadow: none !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;

    }  

#masthead #altsitelinks a {

    color: #B31C42 !important;
    text-decoration: none;
	text-transform: uppercase !important;
	font-family: calibri !important;
	font-size: 16px !important;
	font-weight: bold !important;
	text-shadow : #B31C42 0px 0px 6px !important;

    }
    
#masthead #altsitelinks a:hover {

    color: #FF295E !important;

    }    
    
#masthead a {

    color: white !important;
    text-decoration: none !important;
	text-transform: uppercase !important;
	font-family: calibri !important;
	font-size: 18px !important;
	font-weight: bold !important;
	text-shadow : #00A2F2 0px 0px 6px !important;

    }

#masthead a:hover {

    color: #09F !important;

    }
    
#textlogo {

    position: relative !important;
    border: 1px green solid !important;
    margin: -82px 0px -32px -30px !important;
    padding: 10px 0px 95px 0px !important;
    
    }    
    
img#textlogo { 
    border: 0px solid green !important;
    background:url('http://dl.dropbox.com/u/23922406/chattyprofileslogo.png');
    width:350px;
    height:57px;
    vertical-align:middle;
    }
       
#login-link {

    position: relative !important;
    top: 15px !important;
    right: 100px !important;
    font-size: 8pt;
    display: inline !important;
    padding: 5px 15px 5px 15px;
    color: #FFF;

    }  

#masthead-footer {

    display: none !important;

    }
    
#content {

	border-style: dotted dotted dotted solid !important;
	border-color: #444 #444 #444 #00bff3 !important;
    border-width: 2px 2px 2px 2px !important;
    margin: 28px 28px 0px 28px !important;
    padding: 15px 15px 15px 15px !important;
    width: 954px !important;
    height: auto !important;
    background-color: #262420 !important;

    }
    
#content a {

    color: #6CF !important;
    text-decoration: none !important;
    font-weight: bold !important;
    font-family: calibri !important;
    font-size: 18px !important;
    text-transform: uppercase !important;
    /*border-bottom: 2px dotted #444 !important;*/
    
    }
    
#content a:hover {

    color: white !important;
    text-decoration: none !important;
    /*border-bottom: 2px dotted #FF295E !important;*/
    
    }    

#allpboxes a {

    color: #A19D97 !important;
    text-decoration: underline !important;
    font-weight: normal !important;
    font-family: arial !important;
    font-size: 13px !important;
    text-transform: none !important;
    border-bottom: none !important;
    
    }
    
#allpboxes a:hover {

    color: white !important;
    text-decoration: underline !important;
    font-weight: normal !important;
    font-family: arial !important;
    font-size: 13px !important;
    text-transform: none !important;
    border-bottom: none !important;
    
    }    


#pbox-username {

    background-color: #262420 !important;
    padding: 0px !important;
    border: 0px solid pink !important;
    height: 44px;

    }
    
#pbox-username h1 {

    border: 0px solid green !important;
    padding: 0px 0px 0px 16px !important;
    color: #6CF !important;
    font-size: 160%;
    }

#pbox-username ul li {

    color: #A19D97 !important;
    font-weight: bold !important;
    font-family: calibri !important;
    font-size: 18px !important;
    text-transform: uppercase !important;
    }

.pboxheader {

    background-color: #262420 !important;
    border-bottom: dotted 2px #444 !important;

    }

.pbox {

    background-color: #262420 !important;
    
    }

.pboxentries {

    border: none !important;

    }

.pbox li li {

    border: none !important;

    }

.pbox.userbio .answer {

    padding-left: 0px !important;

    }

#footer {

    position: relative !important;
    border: 0px solid green;
    height: 45px !important;
    top: 80px;
    left: 0px;
    text-align: center !important;
    padding: 0px 0px 0px 0px !important;
    margin: 0px 0px 0px 0px !important;
    font-size: 11px !important;
    color: #A19D97 !important;

    }

#profile-edit {

    border: 0px solid red !important;
    top: 26px;
    margin: 14px 0px 12px 0px !important;
    font-size: 14px !important;
    font-weight: bold;

    }
    
label {   

	color: #A19D97;
	font-weight: normal;
    } 

input {

	left: 165px !important;

    }

select {

    left: 165px !important;

    }

.form-centered {

    position: relative !important;
    top: 20px !important;
    left: 15px;
    width: 308px !important;
    padding: 10px 0px 0px 0px !important;
    margin: 0px 0px 18px 5px !important;
    font-size: 10pt;
    color: #FFF;
    border: 0px solid green !important;

    }

.messages-success {

    text-align: center;
    position: absolute;
    left: 362px !important;
    top: 604px;
    border-width: 0px 0px 0px 2px !important;
    border-style: dotted dotted dotted dotted !important;
    border-color: #267F00 !important;
    background: none !important;
    border-radius: 0px 0px 0px 0px !important;
    color: #B4FF7F !important;
    display: block !important;
    list-style: none;
    margin: 8px 11px 8px 11px;
    padding: 8px !important;
    width: auto !important;
    
    }

.answer-small {

    position:absolute;
    margin: -2px 0px 0px 0px !important;
    font-size: 80%;
    color: #777;

    }

}
