@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("^http://enc?[123456789]?[1234567890]\\.tribalwars.*")

{
    /*   -- Tribal Wars Black --   */
/*by: alexknoxx.com edited by jonardkid*/

body{color:#d5d5d5!important;background:#000000!important;}
.top_bar{}
hr{border-bottom:1px solid #555!important;border-top:1px solid #555!important;}
.top_shadow{background:#666!important;}
th, .vis > h4{border-radius:5px!important;box-shadow:1px 1px 20px #000!important;color:#999!important;background-image: url(http://images.orkut.com/orkut/photos/OQAAAGjCh1OQksKX_V4niIaFuum9FRJ9HjZKVtc1xqYsFaKScS7UwxO-GQFkPj9abVyJSEGOdNX_f8zghkVhmrgy8j0Am1T1UGyNbVn476HmInjGG81NZnrR_z5a.jpg) !important;  background-color: transparent !important;}
.vis td, .vis_item{color:#bbb!important;background:#000!important;}
h2{color:#cccccc!important;}
a{color:#d0d0d0!important;}
a:hover{text-shadow:1px 1px 2px aqua!important;}
.post{background:#111!important;}
.igmline{background:#333!important;}
.maincell{background:#111!important;}
.header-border .firstcell,.icon-box,.box-item{color:#fff!important;border:solid 1px #555!important;padding:3px!important;background:#333!important;}
.quest{background-color:#111!important;border:1px solid #333!important;}
.report_transparent_overlay{background-image:none!important;box-shadow: 1px 1px 50px #000 inset !important;}
#content_value table{border:none !important;}
#mapCoords{background-color:#000!important;}
.server_info{color:#fff!important;text-shadow:1px 1px 2px aqua !important;}
/*layout*/
.content-border{background:#111!important;border:solid 1px #666!important;}
#main_layout .bg_left{background:#111!important;}
#main_layout .bg_bottomleft{background:#111!important;}
#main_layout .bg_bottomcenter{background:#111!important;}
#main_layout .bg_bottomright{background:#111!important;}
#main_layout .bg_right{background:#111!important;}
#main_layout .bg_leftborder{background:#111!important;}
.server_info{background:#111!important;}
#main_layout .bg_rightborder{background:#111!important;}
#footer,#inline_popup_menu{background-image: url(http://images.orkut.com/orkut/photos/OQAAAGjCh1OQksKX_V4niIaFuum9FRJ9HjZKVtc1xqYsFaKScS7UwxO-GQFkPj9abVyJSEGOdNX_f8zghkVhmrgy8j0Am1T1UGyNbVn476HmInjGG81NZnrR_z5a.jpg) !important;  background-color: transparent !important;}
/*fim - layout*/
/*premium*/
#quickbar_inner .bottomborder .main, .quickbar, .topborder .main, #quickbar_inner .left, #quickbar_inner .right {background-image: url(http://images.orkut.com/orkut/photos/OQAAAGjCh1OQksKX_V4niIaFuum9FRJ9HjZKVtc1xqYsFaKScS7UwxO-GQFkPj9abVyJSEGOdNX_f8zghkVhmrgy8j0Am1T1UGyNbVn476HmInjGG81NZnrR_z5a.jpg) !important;  background-color: transparent !important;}
#quickbar_inner .left, #quickbar_inner .right{width:1px!important;}
/*fim - premium*/
#inner-border{border:solid 1px #555!important;}
div.vis{border:1px solid #000000!important;border-radius:10px!important;box-shadow:1px 1px 20px #000!important;}

#content_value th{background-color:#333!important;}
#inline_popup_main{border:solid 1px #555!important;background:#333!important;}
#inline_popup{border:solid 1px #444!important;}
.forum{background:#333!important;}
#forum_box{background:#000!important;}
.thread_button span{color:black!important;}

/*   -- Everything below is added by jonardkid --  */
/*--Popup Box / Quests - Fix Incomplete--*/
table#popup_box table {background: #111111!important;}
td.popup_box_content {background: #111111!important;}
td.popup_box_content table {background: #202020!important;}
td.popup_box_content table tbody {background: #202020!important;}
td.popup_box_content table tbody tr td.quest-summary {background: #252525!important;}

/*--Tooltips Fix--*/
div#tooltip {background: #111111!important;}

/*--Spoiler Fix--*/
div.spoiler div {background: #202020!important;}

/*--Forum Fix--*/
div#forum_box table{background: #111111 !important; color:#656565 !important;}
div#forum_box a{color:#656565 !important;}
td.small {color: #7f7f7f !important;}
div.containerBorder table{background: #202020 !important;}

/*--Map Fix--*/
div#worldmap div#worldmap_header div{background: #111111 !important;}
div#worldmap div#worldmap_footer {background: #111111 !important;}
}
