css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://twitter.com/") ,
url-prefix("https://twitter.com/") {

/* delete */
[id="j_ad"],
[class*="promo"],
div.eTologyToken
{display:none !important;}

/* サイズを大きく＆左右入れ替え */
div#page-container{
width:1020px !important;
padding: 50px 5px 0px 5px !important;
}
div.dashboard{
width:300px !important;
 float: right !important; 
}
div.content-main{
width:700px !important;
 float: left !important; 
}

/* 検索を見やすく */
input#search-query{background-color:#D3D3D3 !important;}
.global-nav .search-input:-moz-placeholder{color:Black !important;}

/* 新しいツイートを目立たせる */
div.new-tweets-bar.js-new-tweets-bar{
color:black !important;
font-size:18px !important;
font-weight:bold !important;
background-color:#D3D3D3 !important;
}

/* 最新のツイートの最後 */
div.last-new-tweet{
border-bottom: 5px solid black !important;
margin-bottom: 10px !important;
}

/* リンクの色分け */
a.twitter-atreply{
font-weight: bold !important;
color:red !important;
}
a.twitter-hashtag{
font-weight: bold !important;
color:blue !important;
}
a.twitter-timeline-link{
font-style: italic !important;
text-decoration: underline !important;
color:#009966 !important;
}

}
