css:
/*搜索栏区域的背景颜色*/
#gbx1,
.k-wb-nb
{
background: #F2EFD4 !important;
box-shadow: 0 1px 2px #D1CDA7 !important;
-moz-box-shadow: 0 1px 2px #D1CDA7 !important;
-webkit-box-shadow: 0 1px 4px #D1CDA7 !important;
} 

/*链接字体颜色*/
#contentPane a,
.k-ldzwdb-ue a,
.lzqA1d .c-C,
.iq5wYb /*右栏那个showall*/
{
color: #1B91E0 !important; 
} 

/*浅黄背景*/
#content, 
.k-fa, 
#contentPane,
/*下5行都是照片*/
.YmkEXd,
.s-W-Oh,
.bBG8Ec,
.Lhhrrd,
.aN8flf,
/*信息*/
.KSB3fe,
.c-Ya-sa,
/*分享*/
.Y-S,
.Y-S-za,
.e-Mf,
/*圈圈*/
.Yy,
.az,
.Ig,
.Zx
{
background: #F2EFD4 !important; 
} 

/*stream外缩进*/
body .Te
{
margin: 20px;
} 

 /*鼠标悬浮时候字体发光+下划线去除*/
#contentPane a:hover,
.k-ldzwdb-ue a:hover,
.k-ldzwdb-ue .qG:hover,
.iq5wYb:hover, /*右栏那个showall*/
.lzqA1d .c-C:hover
{
text-decoration: none !important;
background: #88C4F9 !important;
color: white !important;
box-shadow: 0 0 10px #1B80DA !important;
-webkit-box-shadow: 0 0 10px #1B80DA !important;
-moz-box-shadow: 0 0 10px #1B80DA !important;
}

/*按钮发光*/
.k-ldzwdb-ue [role=button]:hover,
#contentPane [role=button]:hover
{
box-shadow: 0 0 10px #1B80DA ; 
-webkit-box-shadow: 0 0 10px #1B80DA ; 
-moz-box-shadow: 0 0 10px #1B80DA ;
} 

/*柔角*/
.e-Ia-If,
.g-i-h-Ia-B,
.Te, /*stream*/
.g-z-e-Ia, /*评论输入框*/
.Y-S, /*分享*/
.z-e-ib,
.e-Ia-If,
.k-ldzwdb-ue [role=button],
#contentPane [role=button]
{
border-radius: 8.5px;
 } 

.c-Ya-sa,
.KSB3fe,
.F2ydzf,

.Ua-O-Ua,

.Te,
.gbqfqw,
.e-bb,
.g-i-h-Ia-B,

#contentPane,

.g-z-e-Ia
{
border-style: none !important;
}
