@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.com") {

.a {color:#888 !important;}
a:link {
color:#000 !important;
font-weight:100 !important;
}


body {font:100% verdana !important;
letter-spacing:1px !important;}


input[name=q] {
width:600px !important;
height:1em !important;
border:0;
border-bottom:1px solid #000 !important;
text-align:center;
color:#000 !important;
font:100 750% arial !important;
letter-spacing:-0.1em;
display:block;
margin:1em 0 0;
}

.n a,.n .i {
text-decoration:none !important;
margin:5px;
}

.n {
margin:3em;
}

.g {
margin:3em 1em;
}

.i {
color:#000 !important;
}

img[src*="/intl/en_ALL/images/logo.gif"],input[name=btnI], img[src*="/intl/en_ALL/images/logo.gif"], img[src*="/logos/"],img[src*="/intl/en_ALL/images/"], #logo, #ap, img[src*="http://www.google.com/intl/en_us/images/"], .productname, #hf, tbody tr td[style="border-bottom: 1px solid rgb(51, 102, 204);"], tr td[style*="border-bottom: 1px solid rgb(51, 102, 204);"], div nobr font b, div nobr font a, disclaimer, .e, form[name=fb],td[nowrap="nowrap"], .z {
