css:
@-moz-document domain(biblegateway.com) {

div#rotate-link, div#banner, div#nav-col, .merch, .publisher-info-bottom, div.header-rule, div[class="classic with-alert"], div[class="heading passage-class-0"], div#sig {display:none !important;}

body, td, th, input, select, option {
font: 14px sans-serif !important;
}
body {background: url(http://i.imgur.com/uTd7f.jpg) fixed !important;
width: 700px !important;
margin: auto !important;
margin-top: 12px !important;
}
#body {
margin: 0 0 35px 0 !important;
padding: 0 !important;
box-shadow: 0 0 77px #000 !important;
border-bottom: 15px solid #7A0000 !important;
/*border-top: 15px solid #7A0000 !important;*/
/*border-radius: 2px !important; */
line-height: 1.4em !important;
}

#main-col {
margin: 0 !important;
}
#content {margin: 15px !important;}

/* search */
#search-box .button-search {margin-right: 0 !important;}
#search-header, #search-button-bar {display:none !important;}
#search-box {border: none !important; margin: 0 !important; padding: 0 !important;}
form#quick-search-form {margin-top: 0 !important;}
#search-main {background-color: #7A0000 !important; /*border-radius: 0 0 6px 6px !important;*/ height: 22px !important;margin-right: 0px !important;}
#search {border: 1px solid #777 !important;}
.translation-dropdown {font-size: 14px !important;width:315px !important;}
#search-submit {margin-left: 10px !important;background:-moz-linear-gradient(center top , #FCFCFC 0%, #CCC 100%) !important; border: 1px solid #777 !important;color: #001B52 !important; font-size: 12px !important;}
#search-box .translation-dropdown {margin-right: 0 !important;}
option {font-size: 14px !important;}
div#page-options-button {margin: 0px 5px 5px !important; padding: 2px !important; font-size: 10px !important; background-image: -moz-linear-gradient(center top , #F3F3F3 0%, #BBB 100%) !important;}
select {font-size: 14px !important;}

.passage-parallel-add, .passage-resources-open {font-size: 12px !important; margin: 0 !important;}
.passage-tools {margin-bottom: -100px !important;}
.result-text-style-normal {font-family: Myriad Web Pro !important;}
h4 {font: 18px Helvetica !important; font-weight: 600 !important;}
h5 {font: 14px Helvetica !important; font-weight: 600 !important; margin-bottom: 10px !important;}
.passage-wrap {padding: 0 !important;margin-top: -10px !important;}



#result-options1 {background: -moz-linear-gradient(center top , #F5F5F5 0%, #CCC 100%) !important;letter-spacing: 0 !important;border: 1px solid #CCC !important;}
.scroller-icon {margin: 2px 0 0 0 !important; padding: 0 15px !important; color: #333 !important; font-family: Calibri !important;}
.result-options li, .result-options-img-bar li img {height: 20px !important;border-right: 0px solid #CCC !important;border-radius: 3px !important; margin: 0 2px !important;}
div[class="passage-audio button"] {display:none !important;}
div[class="scroller-icon scroller-icon-passage-expand faded"] {color: #BBB !important;}




/* hide More on the NIV */
.section-box {display: none !important;}
.content-col {margin-right: 25px !important;}

font.woj {color: #7A0000 !important;}
sup, .versenum {font-size: 0.65em !important; font-weight: normal !important; opacity: 0.5 !important;}

}
