}

.rowlegenda {
background-color: #111111!important;
}


th {
color: #DDDDDD!important;
font-size: 11px;
font-weight: bold;
background-color: #C6D3E4;
background-image: url('http://i43.tinypic.com/aebltc.jpg')!important;
height: 27px;
white-space: nowrap;
text-align: center;
padding-left: 8px;
padding-right: 8px;
}


.topnav {
font-size: 10px;
background: #111111 url(http://i43.tinypic.com/2yzlxm0.jpg) repeat-x !important;
height: 21px;
white-space: nowrap;
border: 0px solid #808080!important;
border-bottom: 1px solid #808080!important;
border-image: initial;
}


.row3 {
background-color: #333333!important;
padding: 4px;
}


.border {
border-bottom: 1px solid #808080!important;
}

/* QUOTE */
.quotetitle, .attachtitle {
margin: 10px 5px 0 5px;
padding: 4px;
border-width: 1px 1px 0 1px;
border-style: solid;
border-color: #808080!important;
color: #333;
background-color: #555!important;
font-size: 10px;
font-weight: bold;
}

.quotecontent, .attachcontent {
margin: 0 5px 10px 5px;
padding: 5px;
border-color: #808080!important;
border-width: 0 1px 1px 1px;
border-style: solid;
font-weight: normal;
font-size: 11px;
line-height: 125%;
line-height: 1.4em;
font-family: "Lucida Grande", "Trebuchet MS", Helvetica, Arial, sans-serif;
background-color: #222!important;
color: #444!important;
}


/* CODE */
.codetitle {
margin: 10px 5px 0 5px;
padding: 2px 4px;
border-width: 1px 1px 0 1px;
border-style: solid;
border-color: #808080!important;
color: #333!important;
background-color: #555!important;
font-family: "Lucida Grande", Verdana, Helvetica, Arial, sans-serif;
font-size: 10px;
}
.codecontent {
direction: ltr;
margin: 0 5px 10px 5px;
padding: 5px;
border-color: #D1D7DC!important;
border-width: 0 1px 1px 1px;
border-style: solid;
font-weight: normal;
color: #060!important;
font-size: 12px;
font-family: Courier,"Courier New",sans-serif;
background-color: #222!important;
}

/* ODPOVEDAT - PREDMET */
.post {
background-color: grey!important;
border-style: solid;
border-width: 1px;
}
.helpline {
background: #808080;
border-style: none;
border: 0 solid;
font-size: 10px;
border-image: initial;
}
.btnbbcode {
color: black;
font-weight: normal;
font-size: 11px;
font-family: "Lucida Grande", Verdana, Helvetica, sans-serif;
background-color: #808080!important;
border: 1px solid black;
border-image: initial;
}

input {
color: #333!important;
font-family: "Lucida Grande", Verdana, Helvetica, sans-serif;
font-size: 11px;
font-weight: normal;
padding: 1px;
border: 1px solid black;
background-color: grey;
border-image: initial;
}

select {
color: #333!important;
background-color: grey;
font-family: "Lucida Grande", Verdana, Helvetica, sans-serif;
font-size: 11px;
font-weight: normal;
border: 1px solid #222!important;
padding: 1px;
border-image: initial;
}

/* TEXT AREA */
textarea {
background-color: #222222!important;
color: #AAA!important;
font-family: "Lucida Grande", Verdana, Helvetica, Arial, sans-serif;
font-size: 11px;
line-height: 1.4em;
font-weight: normal;
border: 1px solid black;
padding: 2px;
border-image: initial;
}

