css:
@-moz-document domain(tumblr.com) {
#container, div.l-content, .content, .top, .bottom {
        background: transparent !important;
        box-shadow: none !important;
    }

    div.l-header {
        background: #222 !important;
        border-radius: 8px !important;
        border: 1px outset #444;
    }
    
    
    .notification, .answer_container, .token, .controls_section
    {
        background: rgba(0, 0, 0, 0.8) !important;
    }

    
    #previous_page_link, #next_page_link, .add_and_remove,
    .mass_editor, .MissingE_marklist_title, .customize,
    .recessed, .answer_avatar, .flat, .dashboard_options_form,
    #auto_pagination_loader
    {
        background: rgba(0, 0, 0, 0.5) !important;
    }    
    
    
    .notification, .answer_container, .answer_input, .dashboard_options_form,
    .flat
    {
        border: 1px solid black !important;
    }

    .notification .nipple {
        border-right: 6px solid rgba(0, 0, 0, 0.8) !important;
    }
    
    
    .avatar_frame {
        border: 1px solid black !important;
    }
    
    
    #dashboard_controls a, #dashboard_controls .control, 
    #footer, #footer a, .publish_on_time
    {
        color: white !important;
        text-shadow: 1px 1px rgba(0, 0, 0, 0.5) !important;
    }

    #search_query {
        background: rgba(0, 0, 0, 0.2) !important;
    }
    
    
    .tab_notice {
        background: #eee !important;
        color: #222 !important;
    }



    div.l-header {
        margin-top: -48px !important;
        position: fixed !important;

        -webkit-transition: 0.5s;
        -moz-transition: 0.5s;
        -o-transition: 0.5s;
    }

    div.l-header:hover {
        margin-top: -12px !important;
    }

    #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
        padding-top: 28px !important;
    }


    

    #dashboard_index, #dashboard_inbox, #dashboard_followers,
    #dashboard_drafts, #dashboard_post_queue, #main_doc_page,
    #dashboard_edit_post, #dashboard_likes, #account_forgot_password,
    #dashboard_create_tumblelog, #dashboard_block, #main_custom_themes,
    #main_themes, #dashboard_set_theme, #dashboard_directory,
    #dashboard_following, #main_tag, #main_explore, #dashboard_transcoding,
    #dashboard_submissions, #dashboard_tumblelog_settings, #main_edit_theme,
    #dashboard_upload_static_file, #main_theme_author_page
    {
        background: #214 url('http://img560.imageshack.us/img560/629/asdasdyb.png') 
repeat top left !important;
    }


    #dashboard_edit_post #container div.l-content, #main_doc_page #container div.l-content,
    #account_forgot_password #container div.l-content, #dashboard_create_tumblelog #container div.l-content, 
    #dashboard_block #container div.l-content, #main_custom_themes #container div.l-content,
    #main_themes #container div.l-content, #dashboard_set_theme #container div.l-content,
    #dashboard_directory #container div.l-content, #dashboard_following #container div.l-content,
    #main_explore #container div.l-content, .popup, #main_edit_theme #container div.l-content,
    #dashboard_upload_static_file #container div.l-content, #main_theme_author_page #container div.l-content,
    #content_bottom, #content_top
    {
        background: #fff !important;
    }

    body {
        background-image: none !important;
    }
    
    .controls_section .selected, #right_column #MissingE_sidebar_title {
        background: rgba(255,255,255,0.3) !important;
    }
    
    

    .post img, .reply_text, .text_field, #tag_editor,
    .source_url_questionmark_gradient, #post_one {
        border-radius: 8px !important;
    }
    
    .posts, .tag {
        border-radius: 4px 4px 0px 0px !important;
    }

    #post_two {
        border-radius: 0px 0px 8px 8px !important;
    }

    #content_top, #content_bottom, .inline_external_image, .source_url {
        border-radius: 0px !important;
        box-shadow: none !important;
    }

    .photo {
        border-radius: 8px !important;
        box-shadow: none !important;
    }
    
    .token {
        border: 1px solid #444 !important;
    }

    img[src*="http://assets.tumblr.com/images/content_bottom_edit_form.png"],
    img[src*="http://assets.tumblr.com/images/content_top_edit_form.png"]
    {
        height: 0px !important;
        padding-bottom: 20px;
    }

    #content_bottom {
        border-radius: 0 0 15px 15px !important;
    }

    .notification a {
        border-bottom: 1px solid rgba(0,0,0,0.2) !important;
    }
    
    .notification blockquote {
        border-left: 3px solid rgba(255, 255, 255, 0.5);
    }
    
    
    
    
    .new_post {
        background: #fff !important;
    }
    
    .new_post .short_label a {
        color: #777 !important;
        text-shadow: 1px 1px #aaa !important;
    }

    
    .camera {
        background: transparent !important;
        border-radius: 0px !important;
        opacity: 0.7 !important;
        padding-bottom: 4px;
    }

    
    .post {
        text-align: center;
    }

    
    .post p, .post blockquote, .post_info, .caption, .conversation_lines, .quote_source{
        text-align: left !important;
    }

    
    .tab_notice {
        top: 18px !important;
        margin-left: 8px !important;
        height: 10px !important;
        text-align: center !important;
        border-radius: 4px !important;
        box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.7) !important;
        vertical-align: middle !important;
        font: 10px "Arial", sans-serif !important;
        font-weight: bold !important;
    }

    
    #new_post_notice_container {
        right: -3px !important;
    }

    
    div.l-header {
        height: 60px !important;
        padding-bottom: 0px !important;
        margin-bottom: -10px !important;
        box-shadow: 0px 10px 8px -12px #000;
    }

    
    #container {
        box-shadow: 1px 1px 6px rgba(0, 0, 0, 0.6);
        padding-bottom: 20px !important;
    }

    
    #rescued_post *{
        color: #eee !important;
    }

    
    .count {
        text-shadow: none !important;
    }

    
    button.positive, button.negative, button.generic, input.generic {
    }

    
        #logo {
    	height: 0 !important;
    	width: 0 !important;
        margin-top: 11px !important;
        margin-left: 20px !important;
	    padding-left: 60px !important;
	    padding-top: 50px !important;
	    background: url(http://24.media.tumblr.com/tumblr_lvfodtrEJR1qcayx8o1_100.png) no-repeat !important;
        box-shadow: none !important;
    }


    
    .selection_nipple, .tab_notice_nipple {
        position: fixed;
        top: -2000px;
        left: -2000px;
    	height: 0 !important;
    	width: 0 !important;
        box-shadow: none !important;
    }

    
    .token a {
        color: #eee !important;
        text-shadow: 1px 1px #222;
    }
    
    
    .blog small {
          color: rgba(0, 0, 0, 0.6) !important;
    }
    
    
    #posts .post .permalink {
        height: 24px !important;
        background: #999 !important;
        border-radius: 16px !important;
        box-shadow: 2px 2px 6px rgba(0, 0, 0, 0.6);
        text-decoration: none !important;
        
        -webkit-transition: 0.5s;
        -moz-transition: 0.5s;
        -o-transition: 0.5s;
    }

    #posts .post .permalink:hover {
        background: #bbb !important;
        margin-top: -4px !important;
        box-shadow: 2px 2px 8px rgba(0, 0, 0, 0.5);
    }

    #posts .post .permalink:before {
        content: ">"
    }
    
    
    
    .following, #MissingE_sidebar_title a, .tag{
        color: rgba(255,255,255,0.8) !important;
        text-shadow: 1px 1px rgba(0,0,0,0.4) !important;
        border-radius: 4px 4px 0px 0px !important;
    }

    .likes, MissingE_askbox_loaded p {
        color: rgba(255,255,255,0.8) !important;
        text-shadow: 1px 1px rgba(0,0,0,0.4) !important;
        border-radius: 4px 4px 4px 4px !important;
    }
    
    .MissingE_askbox_loaded a {
        color: #aaa !important;
    }
    




   
    .post, .notification {
        margin-top: 0px !important;
        margin-bottom: 0px !important;
        border-radius: 0px !important;
    }
    
    #left_column .is_mine, #left_column .not_mine
    {
        padding-top: 26px !important;
        box-shadow: none !important;
        border-top: 2px dotted #ddd !important;
    }
    
    #left_column #new_post {
        box-shadow: none !important;
        border-radius: 0px 10px 0px 0px !important;
        border-top: 2px dotted #ddd !important;
        padding-top: 0px !important;
    }
    
    #left_column .without_commentary, #left_column .more_notes_link_container,
    #left_column .with_commentary{
        padding-top: 8px !important;
        padding-bottom: 8px !important;
        border: none !important;
    }
    
    .MissingE_bookmark_bar, .MissingE_bookmark_text, .MissingE_bookmark_line{
        margin: 0px !important;
        background: #eee !important;
        color: #444 !important;
        border: none !important;
    }
    
    .MissingE_bookmark_bar {
        border-top: 2px dotted #ddd !important;
        border-bottom: none !important;
    }

    .answer_container {
        border-radius: 0px !important;
        width: 470px !important;
    }
    
    .post .post_avatar {
        position: relative !important;
        background-color: transparent !important;
        box-shadow: none !important;
        border-radius: 6px 0px 0px 6px !important;
        background-repeat: no-repeat !important;
        border: none !important;
    }
    
    .post .avatar_and_i {
        top: -2px !important;
        left: -64px !important;
    }
    
    #left_column {
        position: relative !important;
        left: -21px !important;
    }



    .avatar_frame {
        float: left !important;
        position: relative !important;
        left: 0px !important;
        margin-right: 8px !important;
    }
    
    .nipple {
        position: fixed !important;
        left: -4000px !important;
    }
    
    .notification {
        padding-top: 5px !important;
        padding-bottom: 18px !important;
        width: 424px !important;
        margin-left: 20px !important;
        border-top: none !important;
        border-bottom: none !important;
    }
    
    .notification blockquote {
        position: relative !important;
        left: 35px !important;
        margin-top: 0px !important;
        margin-bottom: 0px !important;
        padding-left: 0px !important;
        text-overflow: ellipsis !important;
    }
}
