css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.com") {

.st-grid td:nth-child(5),
.st-bg-table td:nth-child(5) {
    border-right: 3px solid #ddd !important;
}

.st-grid td:nth-child(6),
.st-bg-table td:nth-child(6) {
    background: #f5f5f5 !important;
    border-left: 0 !important;
}

.st-bg-table td:nth-child(7),
.st-grid td:nth-child(7) {
    background: #f5f5f5 !important;
    border-left: 1px solid #ddd !important;
}

.st-bg-table td:nth-child(6).st-bg-today,
.st-grid td:nth-child(6).st-dtitle-today,
.st-bg-table td:nth-child(7).st-bg-today,
.st-grid td:nth-child(7).st-dtitle-today {
    border-right: 1px solid #666 !important;
    border-left: 1px solid #666 !important;
    background: #eaeaea !important;
}

.st-grid td.st-c,
.st-grid td.st-s {
    background: none !important;
}

}
