@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pokecheck.org") {

/* Global elements */
body {
background: #FFFFFF !important;
color: #000000 !important;
}

a:link {color: #3333FF !important;}
a:visited {color: #3333FF !important;}

input,select {-moz-appearance: none !important;}

input[type="text"],input[type="password"],
input[type="button"],input[type="submit"],
input[type="checkbox"],select {
background: #FFFFFF !important;
color: #000000 !important;
border: 1px solid #0000AA !important;
}

.warnmsg {color: #EF22EF !important;}

/* Pokémon Selector */
.red {background: #FF9999 !important; color: #000 !important;} /*DO NOT REMIX*/
.gold {background: #9999FF !important; color: #000 !important;} /*DO NOT REMIX*/
.emerald {background: #99FF99 !important; color: #000 !important;} /*DO NOT REMIX*/
.platinium {background: #888 !important; color: #000 !important;} /*DO NOT REMIX*/
.black {background: black !important; color: white !important;} /*DO NOT REMIX*/

/* Top section */
.mainheader {background: #EEE !important;
color: #111 !important;}

.dnsaddress {background: #F0AAFF !important;
border: 1px solid #0F00CC !important;}
.overflowbox {border: 1px solid #000088 !important;}

.disclaimer {color: #A500FF !important;}

/* Search/userinfo divider borders */
.mainheader,.linkbar {border-bottom: 1px solid blue !important;}

/* Shoutbox styles */
.shoutbox {border: 1px solid #000088 !important;}
.msg0 {background: #9999FF !important;}
.msg1 {background: #CCCCFF !important;}
.admmsg0 {background: #77AFFF !important;}
.admmsg1 {background: #AAAFFF !important;}
.privmsg {background: #FFA0A0 !important;}

/* img[src="i/refresh.png"] {
background: transparent url(http://i41.tinypic.com/vdzfkj.png) no-repeat top left !important;
height: 0 !important;
overflow: hidden !important;
} */

/* Fixed feedback bar */
.fixedbar {background: #AAAAFF !important;}

/* Sortable Pokémon tables/box info */
.sortable {border: 1px solid blue !important}
.pklisthdr {background: #CCCCFF !important;}
.row0 {background: #80AADD !important;}
.row1 {background: #8099CC !important;}
.row0:hover {background:#A0CCFF !important;}
.row1:hover {background:#A0BBEE !important;}

/* Pokémon stats table */
div .markings{background: #888 !important;}

.dhead {
background: #CCAAFF !important;
border: 1px solid #000099 !important;
}
.dblock {
background: #DDDDFF !important;
border: 1px solid #0000CC !important;
}

.stattable,.movetable {background: #8860CC !important;}
.statheader {background: #CCCCFF !important;}
.st0,.move0 {background: #80AADD !important;}
.st1,.move1 {background: #8099CC !important;}
.sthp {background: #C090EF !important;}
.cont {color: #111111 !important;}

.headless {
background: #80AADD !important;
color: #3333FF !important;
border: 1px solid blue !important;
}

/* Nature stat colors */
.up {color: #9F0033 !important;} /*DO NOT REMIX*/
.down {color: #00669F !important;} /*DO NOT REMIX*/

/* Legality analysis */
.betawarn {background: #BC99EE !important; border: 1px solid #330099 !important;}
.legaltable {background: #8860CC !important;}
.legalheader {background: #CCCCFF !important;}
.legal0 {background: #80AADD !important;}
.legal1 {background: #8099CC !important;}
.valid {color: #005540 !important;} /*DO NOT REMIX*/
.warning {color: #AA4040 !important;} /*DO NOT REMIX*/
.error {color: #CC0000 !important;} /*DO NOT REMIX*/

/* ZIP notification */
.zipnotice {
background: #8880DF !important;
color: #001111 !important;
border: 1px solid #0000CC !important;
}

/* Sprite page */
div.spritereport,div.iconsubmit,div.icontable{
background: #EEEEEE !important;
border: 1px solid blue !important;}

/* Directory display */
.list {background: #CCCCFF !important;
border: 1px solid blue !important;}

/* Frequently Asked Questions */
.faqentry {
background: #CCCCFF !important;
color: #3333FF !important;
border: 1px solid blue !important;
}

/* Public box display / account options */
.pubbox {background: #8860CC !important;}
.prefs {background: #EEEEEE !important;
color: #111111 !important;}
.pk0 {background: #80AADD !important; border: 1px solid #2F2F66 !important;}
.pk1 {background: #6099CC !important; border: 1px solid #1F1F88 !important;}
.pk0:hover {background: #A0CCFF !important;}
.pk1:hover {background: #A0BBEE !important;}
}

/* SPRITES SUBDOMAIN */
@-moz-document domain("sprites.pokecheck.org") {

body {background: #FFFFFF !important;
color: #000000 !important;}

a:link,a:visited {color: #3333FF !important;}

.header {background: #EEEEEE !important;
border-bottom: 1px solid blue !important;}
.tab {background: #CCCCFF !important;}
.active,.tab:hover {background: #9999CC !important;}

.fixbar {background: #AAAAFF !important;}
}
