css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("virtueverse.com") {

/* TO USE: http://virtueverse.com/index.php?title=Enerix&useskin=standard */

body {
width: 100% !important;
margin: 0 !important;
padding: 0 !important;
background: #111 !important;
}

/* --- Fix the quickbar --- */

#quickbar {
position: fixed !important;
left: 30px !important;
top: 10% !important;
border: 1px solid #fff !important;
background: #333 !important;
-moz-border-radius: 5px !important;
padding: 10px 5px !important;
width: 180px !important;
}

/* --- Quickbar links --- */

#quickbar > A:link, #quickbar > STRONG > A:link {
color: #fff !important;
font: bold 12px/20px "Corbel", "Tahoma", sans-serif !important;
padding: 1px 10px 1px 10px !important;
}

#quickbar > A:hover, #quickbar > STRONG > A:hover {
color: #fff !important;
font: bold 12px/20px "Corbel", "Tahoma", sans-serif !important;
background: #900 !important;
padding: 2px 10px 2px 10px !important;
border: #fff 1px solid !important;
-moz-border-radius: 5px !important;
}

#quickbar > A:active, #quickbar > STRONG > A:active {
color: #fff !important;
font: bold 12px/20px "Corbel", "Tahoma", sans-serif !important;
background: #222 !important;
padding: 2px 10px 2px 10px !important;
border: #444 1px solid !important;
-moz-border-radius: 5px !important;
}


#quickbar > A:visited, #quickbar > STRONG > A:visited {
color: #fff !important;
font: bold 12px/20px "Corbel", "Tahoma", sans-serif !important;
padding: 2px 10px 2px 10px !important;
}

/* --- Hide Logo --- */

#quickbar > A:first-child > IMG, #quickbar > A:first-child + HR, #quickbar > A:first-child {
display: none !important;
}

/* --- Fix Search Area --- */

#topbar > TABLE > TBODY > TR > TD:last-child > FORM {
padding: 2px 10px 2px 10px !important;
border: 1px solid #fff !important;
background: #333 !important;
-moz-border-radius: 5px !important;
position: fixed !important;
bottom: 10% !important;
left: 30px !important;
width: 180px !important;
padding: 10px 5px !important;
text-align: left !important;
}

#topbar > TABLE > TBODY > TR > TD:last-child > FORM > INPUT:first-child {
font: bold 10px "Corbel", "Tahoma", sans-serif !important;
border: #fff 1px solid !important;
background-color: #000 !important;
color: #ccc !important;
-moz-appearance: none !important;
-moz-border-radius: 3px !important;
padding: 2px !important;
width: 95px !important;
}

#topbar > TABLE > TBODY > TR > TD:last-child > FORM > INPUT:first-child + INPUT {
font: bold 10px "Corbel", "Tahoma", sans-serif !important;
-moz-appearance: none !important;
width: 25px !important;
border: #fff 1px solid !important;
-moz-border-radius: 3px !important;
background-color: #BB3300 !important;
opacity: 0.5 !important;
color: #ccc !important;
}

#topbar > TABLE > TBODY > TR > TD:last-child > FORM > INPUT:first-child + INPUT:hover {
font: bold 10px "Corbel", "Tahoma", sans-serif !important;
-moz-appearance: none !important;
width: 25px !important;
border: #fff 1px solid !important;
-moz-border-radius: 3px !important;
background-color: #BB3300 !important;
opacity: 1 !important;
color: #ccc !important;
}

#topbar > TABLE > TBODY > TR > TD:last-child > FORM > INPUT:last-child {
font: bold 10px "Corbel", "Tahoma", sans-serif !important;
-moz-appearance: none !important;
width: 45px !important;
border: #fff 1px solid !important;
-moz-border-radius: 3px !important;
background-color: #BB3300 !important;
opacity: 0.5 !important;
color: #ccc !important;
}

#topbar > TABLE > TBODY > TR > TD:last-child > FORM > INPUT:last-child:hover {
font: bold 10px "Corbel", "Tahoma", sans-serif !important;
-moz-appearance: none !important;
width: 45px !important;
border: #fff 1px solid !important;
-moz-border-radius: 3px !important;
background-color: #BB3300 !important;
opacity: 1 !important;
color: #ccc !important;
}


/* --- Fix Top Bar --- */

#topbar > TABLE > TBODY > TR > TD:first-child {
display: none !important;
}

#topbar {
border: 0 !important;
}

#topbar > TABLE {
margin-left: 152px !important;
width: 630px !important;
}

td.top > a:link, #topbar > TABLE > TBODY > TR > TD:first-child + TD > P > a:link {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #aaa !important;
}

td.top > a:hover, #topbar > TABLE > TBODY > TR > TD:first-child + TD > P > a:hover {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #fff !important;
}

td.top > a:active, #topbar > TABLE > TBODY > TR > TD:first-child + TD > P > a:active {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #fff !important;
}

td.top > a:visited, #topbar > TABLE > TBODY > TR > TD:first-child + TD > P > a:visited {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #aaa !important;
}

td.top, #topbar > TABLE > TBODY > TR > TD:first-child + TD > P {
color: #fff !important;
}

#content {
width: 800px !important;
background-color: #000 !important;
margin: 0px auto !important;
padding-top: 10px !important;
padding-right: 20px !important;
border-left: #fff 2px solid !important;
border-right: #fff 2px solid !important;
}

h1.pagetitle {
font: 30px "Cambria", "Serif" !important;
color: #fff !important;
}

#article > H1:first-child + P {
font: 15px "Cambria", "Serif" !important;
color: #bbb !important;
}

p.catlinks > a:link {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #aaa !important;
}

p.catlinks > a:hover {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #fff !important;
}

p.catlinks > a:active {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #fff !important;
}

p.catlinks > a:visited {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #aaa !important;
}

p.catlinks > span > a:link {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #990000 !important;
}

p.catlinks > span > a:hover {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #fff !important;
}

p.catlinks > span > a:active {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #fff !important;
}

p.catlinks > span > a:visited {
font: 11px "Corbel", "Tahoma", sans-serif !important;
color: #990000 !important;
}

/* --- ARTICLE --- */

#article > p,
#article > OL,
#article > UL,
#article {
font: 11pt "Cambria", serif !important;
color: #aaa !important;
text-align: justify !important;
}

#article > TABLE > TBODY > TR > TD > P, #article > FORM,
#uploadtext,
.noarticletext,
#nsselect,
LEGEND,
LABEL {
font: 10pt "Cambria", serif !important;
color: #aaa !important;
}

#powersearch {
background: #000 !important;
}

#editform > TEXTAREA,
TEXTAREA {
font: 10pt "Corbel", sans-serif !important;
color: #fff !important;
-moz-appearance: none !important;
border: #fff 1px solid !important;
width: 630px !important;
background: #000 !important;
padding: 5px !important;
}

#wpUploadDescription {
-moz-appearance: none !important;
border: #fff 1px solid !important;
width: 500px !important;
background: #000 !important;
padding: 5px !important;
}

TABLE[width="100%"][style="background-color: rgb(0, 43, 99); color: white; margin-bottom: 0pt;"],
TABLE[width="100%"][style="padding: 10px; background: transparent none repeat scroll 0% 0%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial; font-size: 85%; margin-bottom: 0pt;"] {
display: none !important;
}

#article > OL > LI, #article > UL > LI {
margin-left: 20px !important;
}

#article > blockquote {
font: 11pt "Cambria", serif !important;
color: #aaa !important;
text-align: justify !important;
border: #555 1px solid !important;
-moz-border-radius: 5px !important;
padding: 15px !important;
}

#article > p > a.new:link, a.new:link {
color: #555 !important;
}

#article > p > a.new:hover, a.new:hover {
color: #999 !important;
text-decoration: none !important;
}

#article > p > a.new:active, a.new:active {
color: #999 !important;
text-decoration: none !important;
}

#article > p > a.new:visited, a.new:visited {
color: #555 !important;
}

#article > p > a:link, a:link {
color: #DD3300 !important;
}

#article > p > a:hover, a:hover {
color: #FF6600 !important;
text-decoration: none !important;
}

#article > p > a:active, a:active {
color: #FF6600 !important;
text-decoration: none !important;
}

#article > p > a:visited, a:visited {
color: #DD3300 !important;
}

h1 > span.mw-headline, span.mw-headline > a {
font: 22pt "Cambria", serif !important;
color: #fff !important;
text-transform: uppercase !important;
text-align: left !important;
}

h2 > span.mw-headline, span.mw-headline > a {
font: 18pt "Cambria", serif !important;
color: #fff !important;
text-align: left !important;
}

h3 > span.mw-headline, span.mw-headline > a {
font: 14pt "Cambria", serif !important;
color: #fff !important;
text-transform: uppercase !important;
text-align: left !important;
}

h1 > span > a, h1 > span > a {
font: 8pt "Corbel", "Arial", sans-serif !important;
color: #fff !important;
text-transform: uppercase !important;
}

h2 > span > a, h2 > span > a {
font: 8pt "Corbel", "Arial", sans-serif !important;
color: #fff !important;
text-transform: uppercase !important;
}

h3 > span > a, h3 > span > a {
font: 8pt "Corbel", "Arial", sans-serif !important;
color: #fff !important;
text-transform: uppercase !important;
}

.sep {
border: none !important;
background-color: #fff !important;
color: #fff !important;
height: 1px !important;
width: 90% !important;
}

/* --- TOC --- */

#toc {
border: #fff 1px solid !important;
-moz-border-radius: 5px !important;
background: #333 !important;
}

#toctitle, #toctitle > span > a {
font: 10pt "Cambria", serif !important;
color: #fff !important;
text-align: justify !important;
}

#toc > TBODY > TR > TD > UL > LI > A,
#toc > TBODY > TR > TD > UL > LI > UL > LI > A ,
#toc > TBODY > TR > TD > UL > LI > UL > LI > UL > LI > A {
font: 10pt "Calibri", sans-serif !important;
color: #ccc !important;
text-align: justify !important;
}

#footer {
display: none !important;
}

/* --- THUMBNAILS --- */

div.thumb.tright {
margin: 0px 10px 0px 10px !important;
padding: 0px !important;
border: 0 !important;
}

div.thumb.tleft {
margin: 20px 20px 0px 0px !important;
padding: 0px !important;
border: 0 !important;
}

div.center {
margin: 0px !important;
padding: 0px !important;
border: 0 !important;
}

div.thumbinner {
margin: 0px !important;
padding: 10px 5px 5px 5px !important;
border: #fff 2px solid !important;
-moz-border-radius: 5px !important;
background: #333 !important;
width: 100% !important;
}

div.magnify {
display: none !important;
}

div.thumbcaption {
text-align: center !important;
color: #ccc !important;
font: italic 12px "Cambria", serif !important;
}
}
