css:
@-moz-document url-prefix("http://w3.my-fantasy.net")
{
table {
border-collapse:collapse;
}
input {
font-family:Verdana,Tahoma;
font-size:11px;
}
select {
font-family:Verdana,Tahoma;
font-size:11px;
}
textarea {
font-family:Verdana,Tahoma;
font-size:11px;
}
td {
color:#000000;
font-family:Verdana,Tahoma;
font-size:11px;
font-weight:normal;
}
a {
color:yellow;
/*edit z #D20000 na #000000 */
font-family:Verdana,Tahoma;
text-decoration:none;
}
a.white {
color:#FFFFFF;
}
a.black {
color:#000000;
}
a.gm {
color:#000000;
/*edit z #DF832F na #000000 */

}
a:hover {
text-decoration:underline;
}
li {
margin:0pt 5px;
text-align:left;
}
ul {
margin:0pt 10px;
}
ol {
margin:0pt 25px;
}
th {
background-color:#000000;
/*edit z #B2B3CA na #000000 */
border-bottom:1px solid #000000;
/*edit z #839ABE na #000000 */
font-family:Verdana,Tahoma;
font-size:11px;
font-weight:bold;
}
.f1 {
color:#000000;
font-family:Verdana,Tahoma;
font-size:10px;
font-weight:normal;
}
.f2 {
color:#000000;
font-family:Verdana,Tahoma;
font-size:11px;
font-weight:normal;
}
.f3 {
color:#000000;
font-family:Verdana,Tahoma;
font-size:12px;
font-weight:normal;
}
.td1 {
border:1px solid #000000;
}
.td_white {
border:1px solid #FFFFFF;
}
.td_white_l {
border-color:#FFFFFF rgb(255, 255, 255) rgb(255, 255, 255) rgb(0, 0, 0);
border-style:solid;
border-width:1px;
}
.td_white_r {
border-color:#FFFFFF rgb(0, 0, 0) rgb(255, 255, 255) rgb(255, 255, 255);
border-style:solid;
border-width:1px;
}
.td_num {
padding:1px;
text-align:right;
white-space:nowrap;
}
.td3 {
background-color:#000000;
/*edit z #E2E5EE na #000000 */
}
.th {
color:#000000;
/*edit z #336699 na #000000 */
font-family:Verdana,Tahoma;
font-size:11px;
font-weight:bold;
height:34px;
}
.container {
border:medium none;
height:100%;
padding:0pt;
width:100%;
}
.guzik {
background-color:#EEEEEE;
border:1px solid #000000;
text-align:center;
}
.text_btn {
background-color:#000000;
/*edit z #8283AA na #000000 */
border:1px solid #000000;
/*edit z #EAEAEA na #000000 */
color:#FFFFFF;
cursor:pointer;
padding:1px;
}
td#menu {
border-bottom:1px solid #000000;
/*edit z #8283AA na #000000 */
height:24px;
padding:0px;
}
a#menu {
display:block;
font-size:12px;
height:100%;
padding-left:3px;
width:100%;
}
a#menu2 {
display:block;
height:18px;
width:100%;
}
span#menu {
position:relative;
top:4px;
}
span#menu2 {
position:relative;
top:2px;
}
img {
border-style:solid;
border-width:0px;
}
td.activetab {
background-color:#000000;
/*edit z #C8CEDE na #000000 */
border-color:#FFFFFF rgb(255, 255, 255) rgb(200, 206, 222);
border-style:solid;
border-width:2px;
font-weight:bold;
text-align:center;
}
td.inactivetab {
border-color:-moz-use-text-color -moz-use-text-color #FFFFFF;
border-style:solid;
border-width:0px 0px 2px;
color:#EEEEEE;
padding:0px;
text-align:center;
}
td.active_menu {
background-color:#FF0000;
}
td.new_menu {
border:2px solid #000000;
/*edit z #DFFD11 na #000000 */
}
body {
color:#000000;
font-family:Verdana,Tahoma;
font-size:11px;
font-weight:normal;
}
a#menu:hover {
background-color:#9FB7C4;
/*edit z #9FB7C4 na #000000 */
}
a#menu2:hover {
background-color:#000000;
/*edit z #636392 na #000000 */
}

}
