css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www.reddit.com") {







.linklisting .odd {

background: rgba(0%,0%,0%,.04);

}



.organic-listing .link.odd {

background: #000;

}

.organic-listing .link.even {

background: rgba(0%,0%,0%,.04);

}



.linklisting .thing {

border-bottom: 1px solid #ddd !important;

margin: 0 !important;

}



.linklisting .title a, .organic-listing .title a { color: #333 !important; }



.linklisting .title a.title, .organic-listing .title a.title { font-weight: bold !important; font-family: 'Arial', lucida grande, helvetica, Verdana !important; font-size: 1em !important;}



.linklisting a, .organic-listing a { color: #555 !important; }



.linklisting .title a.title:visited {

 color: gray !important; 

}



a:hover, .title a:hover, organic-listing a:hover {

color: #f50 !important;

text-decoration: underline !important;

}



.linklisting .entry {

  padding: 1em !important; }



.linklisting .thing:hover, .linklisting .odd:hover, .organic-listing .thing:hover {

background: #d9f0fc !important;

}



.linklisting .last-clicked { border-top: none !important; border-right: none !important; border-left: 1px dashed gray !important; margin-left: -1px !important;}



.organic-listing .rank { display: none; }



.rank {

font-weight: bold;

font-size: 46px !important;

padding: 0 .1em 0 0 !important;

margin: .02em 0 0 0 !important;

letter-spacing: -1pt !important;

width: 3.1ex !important;

overflow: hidden;

white-space: nowrap;

}



.rank:before { content: "00"; margin-left: -1.05em;}



.linklisting .midcol {

  padding: .65em 0 !important; 

}



.organic-listing .midcol {

 margin-right: 10px !important;

}



.linklisting .comment {

   margin: 1em !important;

 }



.linklisting .comment.even:hover {

  background: #FFF !important;

}



.linklisting .comment.odd:hover {

  background: rgba(230, 230, 230, 0.3) !important;

  }



.linklisting .tagline { font-style: italic; }

.linklisting .tagline .score { font-weight: bold; font-style: normal;}

.linklisting .tagline .subreddit { font-style: normal; }

.organic-listing .buttons { padding-bottom: 3px !important; }



.linklisting .buttons .share, .organic-listing .buttons .share { display: none !important; }

.linklisting .even .buttons li, .organic-listing .even .buttons li { margin: 3px !important; padding: 2px !important; background: rgba(0%,0%,0%,.04); -moz-border-radius: 3px; border: 1px solid rgba(0%,0%,0%,.1) !important; }

.linklisting .odd .buttons li, .organic-listing .odd .buttons li { background: #fff; }

.linklisting .even:hover .buttons li, .organic-listing .even:hover .buttons li { background: #fff !important; }

.linklisting .buttons .first .comments:visited { color: gray !important; }

}
