.AutoPager_Related, .autoPagerS, .autopagerize_page_separator, .autopagerize_page_info {
    display: none !important;
}

@-moz-document domain("userstyles.org") {
.pagination { display: none !important; }
}

@-moz-document regexp("https?://www.google.*/search.*"), regexp("https?://www.google.*/") {
#nav { display: none !important; }
#navcnt { padding: 8px; }
}

@-moz-document domain("webupd8.org") {
.navigation, #blog-pager { display: none !important; }
}

@-moz-document domain("tumblr.com") {
#navigation {
    display: none !important;
}
}

@-moz-document domain("ebay.com") {
.pgbc {
    position: fixed !important;
    width: 100% !important;
    left: 0 !important;
    bottom: 0 !important;
    padding-top: 6px !important;
    padding-bottom: 6px !important;
    z-index: 20000 !important;
}

body {
    padding-bottom: 14px !important;
}

.pagn, .pagn-m, .pager { display: none !important; }

#cbBtmElem.ipp.cb {
    display: none !important;
}

hr {
    display: none !important;
}

