@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



















#dashboard_index,



#dashboard_inbox,



#dashboard_edit_post,



#dashboard_submissions,



#dashboard_drafts,



#dashboard_post_queue,



#dashboard_likes,



#dashboard_followers,



