background: #3F3F3D !important;

 position:absolute!important;content:""!important;left:10px !important;top: 0 !important;width:10px!important;height:16px!important;-moz-border-radius:50px 50px 0 0 !important;border-radius: 50px 50px 0 0 !important;-moz-transform:rotate(-45deg)!important;-moz-transform-origin:0 100%!important;-webkit-transform:rotate(-45deg)!important;-webkit-transform-origin:0 100%!important;margin-top:-9px!important;margin-left:-9px!important;}.selection_nipple.white:after,.selection_nipple:not([class="selection_nipple white"]):after{left:0!important;-moz-transform:rotate(45deg)!important;-moz-transform-origin:100% 100%!important;-webkit-transform:rotate(45deg)!important;-webkit-transform-origin:100% 100%!important;}.selection_nipple.white:before,.selection_nipple.white:after{margin-left:-11px!important;margin-top:-9px!important;}.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]) {background:none!important;width:0px!important;height:0px!important;margin-top:0px!important;border-bottom:solid 11px rgba(255,255,255,0.0)!important;border-right:11px solid transparent!important;border-left:11px solid transparent!important;}
