css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nexusmods.com") {

.upload-date { font-weight: bold !important; color: rgb(150, 150, 150) !important; }

.files-tab-files-list li .version { color: rgb(125, 125, 125) !important; }

.input_text { border: 1px solid rgb(125, 125, 125) !important; }

.files-list .file-image { float: none; width:auto; overflow: auto !important; }

.files-list .file-image img { float: none; height:auto; width:auto !important; }

.file-image-scroller .file-image-container li img { height:auto; width:auto !important; }

.desc { font-weight: bold !important; color: rgb(116, 116, 116) !important; }

#forum_table .maintitle .desc a { font-weight: bold !important; color: rgb(116, 116, 116) !important; }

p.edit { font-weight: bold !important; color: rgb(116, 116, 116) !important; }

p.posted_info strong.event { font-weight: bold !important; color: rgb(116, 116, 116) !important; }

.desc, p.posted_info, .warn_panel strong { font-weight: bold !important; color: rgb(116, 116, 116) !important; }

.comment-list .comment-posted { color: rgb(116, 116, 116) !important; }

.tab_bar li a { color: rgb(116, 116, 116) !important; text-decoration: none; }



.border-left{
	background:transparent url(http://forums.nexusmods.com/public/style_images/Dark/_custom/border_left.gif) no-repeat top left;
	padding-left:7px !important; }


.border-right{
	background:transparent url(http://forums.nexusmods.com/public/style_images/Dark/_custom/border_right.gif) no-repeat top right;
	padding-right:7px !important; }

*:focus{box-shadow:0 0 10px!important}

.files-filters { overflow: visible !important; padding: 0; height: 40px; }
}
