css:
/*=====================







    Important Stuff







=====================*/







@namespace url(http://www.w3.org/1999/xhtml);      @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 







 {















/*============================================













=============================================*/























/*===================================================================================================







===============================================Global=================================================







====================================================================================================*/















/*==========================================Body background==========================================*/















body 







	{background: url('http://i39.tinypic.com/4gruqq.png') center right #f5deb5 repeat fixed !important;}  







*  







	{text-shadow:none !important;}







	







/* ====================Solid content area in front of background, behind content bubbles=======================*/















#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#fbb974 !important;	







	opacity:.9;}







	







/*===============================Load More Posts Banner================================================*/















#auto_pagination_loader







	{background:rgba(56,161,207, 0.00)!important;}







div.return_to_top_icon{	background: none !important;







	width: 0px !important;







	height: 0px !important;







	margin-top: 0px !important;







	opacity: .5 !important;







	border-bottom: solid 30px black !important;







	border-right: 15px solid transparent !important;







	border-left: 15px solid transparent !important; }	







	







	







/*=======================================Selection Nipple ================================================*/















.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 







	background: none !important;







	width: 0px !important;







	height: 0px !important;







	margin-top: 0px !important;







	border-bottom: solid 11px white !important;







	border-right: 11px solid transparent !important;







	border-left: 11px solid transparent !important; }







.selection_nipple:not([class="selection_nipple white"]) {







	border-bottom: solid 11px #312423 !important;}	







	







/*=======================================Right Column Highligts================================================ */







	







.selected .following,







.selected .followers,







.selected .settings,







.selected .posts,







.selected .messages,







.selected .drafts,







.selected .queue, 







.selected .likes {







	color: rgba(255,255,255, 0.50) !important; 







	background: #abadae !important;}















/*========================================================================================================







===============================================Dashboard==================================================







=========================================================================================================*/	







	







/*======================================Dashboard Icon Map=================================================*/







ol#posts li.post.new_post 







	{background: url('http://30.media.tumblr.com/tumblr_ln9fd5xOjk1qgllfpo1_500.png') center center no-repeat;}















img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]







	{width:0;







    height:70px;







    padding-right:250px;







    opacity: 0;}







	







/*==============================Right Column Tags (Dashboard and Tagged Pages)==============================*/	







a.explore_more_tags,







a.add_and_remove,







a.customize,







a.mass_editor







	{background-color:#cf4511 !important;  







	background-image:none !important;







	border:0px !important}















div.hide_overflow







	{font-size:.9em;}







a.tag,







a.explore_more_tags,







a.add_and_remove







	{height:8px;}







a.tag







	{position:relative;top:-4px;}







.selected  .tag







	{height:14px;background:#000000 !important; 







	position:relative;top:0px;







	color:rgba(255,255,255, 0.50) !important;}







ul







	{-moz-box-shadow: 1px 1px 3px #000000 !important;}







	







/*===========================================Right Column Radar Image============================================*/















div.gradient,







div.dashboard_nav_item, 







a.permalink







	{background-color:#cf4511 !important;







	opacity:.9; 







	background-image:none !important;







	border:0px !important}







.content.is_media, 







.mask.top, 







.mask.bottom {







	background: none !important; }







.post.photo_post .photo, 







.post.photo_post .video_thumbnail {







	border-radius: 16px !important;}







.post.photo_post .photo, 







.post.photo_post .video_thumbnail 







	{-moz-box-shadow: 1px 1px 3px #000000 !important;}















/*========================================Blockquotes are crazy! ==============================================*/















blockquote







	{margin-left:-5px !important;}















/*===========================================================================================







=======================================Log In Page============================================







=============================================================================================*/















.login.sign_up,







A[href="/forgot_password"] {display: none !important;}	















/*===========================================================================================







===================================== Text Post Screen=========================================







=============================================================================================*/















.mceContentBody {







  background: none !important; }







  







/*==================================================







Notification Bubbles and other stuff I'm not messing with. :)







====================================================*/















ol#posts li.notification







	{background-color: #cf4511 !important;







	border-bottom: 2px solid #281c1c !important;







	color: #000000 !important;}







	







#right_column a#dashboard_switch_blog_menu_current:hover 







	{background:#cf4511;}







	







#nav .nav_item.active







	{background-color:#cf4511;}







	







#right_column







	{color:#701e71;}







	







#left_column #posts .post .permalink







	{background-color:#cf4511;}















#nav .nav_item.active, 







#nav .nav_item .nav_menu, 







#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel







	{background-color:#cf4511 !important;







	background-image:none !important;







	border:0px !important}















#dashboard_switch_blog_menu_current,







#dashboard_switch_blog_arrow_and_divider,







#dashboard_switch_blog_arrow_current







	{background-image:none !important}















.notification_type_icon







	{display:none !important}















#left_column #posts .notification 







	{background-color:#cf4511 !important;







	color:#666 !important;







	border-bottom-color: #eee !important}















#new_post_notice_container







	{color:#fff !important}















#new_post_notice_container a







	{color:#fff !important}







ol#posts .post.new_post .new_post_label 







	{display:none !important;}















/*=====================







    Important Stuff







=====================*/	







	







}
