css:
@namespace url(http://www.w3.org/1999/xhtml);

/* 
  dieses userstylesheet für mehr Kontrast und etwas größere Schrift im Fließtest 
  allerdings nicht auf der iGeL-Startseite

*/


@-moz-document domain("www.igel-monitor.de") {

body, .maincolumn {
    color: #000 !important;
    }

.maininner {
    width: 65em !important;
    }

a, a:visited {
    color: #32004b !important;
    }
a:hover {
    color: #d0005d !important;
    }


div.metanavi a, div.metanavi a:hover, div.metanavi a:visited {
    color: #fff !important;
    }
    
.maincolumn {
    font-size: 0.9em !important
    }    

/* Startseite */

ul.large_columns li {
    color: #000 !important;
    }

ul.box_column li .box_inner {
    color: #000 !important;
    }

ul.box_column li div.headline h2 {
    color: #32004B !important;
    }

}
