@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ogame.org"), domain("ogame.com.br"), domain("ogame.cz"), domain("ogame.de"), domain("ogame.dk"), domain("ogame.com.es"), domain("ogame.fr"), domain("ogame.gr"), domain("ogame.com.hr"), domain("ogame.hu"), domain("ogame.it"), domain("ogame.jp"), domain("ogame.lt"), domain("ogame.lv"), domain("ogame.nl"), domain("ogame.no"), domain("ogame.pl"), domain("ogame.com.pt"), domain("ogame.ro"), domain("ogame.rs"), domain("ogame.ru"), domain("ogame.se"), domain("ogame.si"), domain("ogame.sk"), domain("ogame.com.tr"), domain("ogame.tw"), domain("ogame.us") {

/* Redesign by mika77*/

body#overview,
body#imperium,
body#resources,
body#resourceSettings,
body#station,
body#station-moon,
body#trader,
body#research,
body#shipyard,
body#defense,
body#fleet1,
body#fleet2,
body#fleet3,
body#movement,
body#highscore,
body#galaxy,
body#messages,
body#premium,
body#payment,
body#ticket,
body#statistics,
body#preferences,
body#combatreport,
body#alliance,
body#networkkommunikation,
body#networkverwaltung,
body#techtree,
body#changelog,
body#networkbewerbungen,
body#tutorial {
background-image: url(http://s4.noelshack.com/old/up/redirect_2-bbb1142484.png) !important;
background-position: top !important;
background-attachment: fixed !important;
}

#star, #star1, #star2 {
display: none !important;
}

#helper a, #helper a.help_selected:hover {
background: none!important;
}

/*#####menu gauche#####*/
#links {
position: relative !important; 
left: -00px !important;
}

/*#####menu droite#####*/
#rechts {
right: -00px !important;
}

#message_alert_box, message_alert_box_default {
    background: url("http://s4.noelshack.com/old/up/cd1f1cbc59f2b59aa19c865697387c-6c50c1d012.png") no-repeat scroll 0 0 transparent !important;
}

a#message_alert_box.emptyMessage:hover, a#message_alert_box_default:hover, a#message_alert_box:hover {
    background: url("http://s4.noelshack.com/old/up/0c080ba528dab654af4ecad9664be5-705be33579.png") no-repeat scroll 0 0 transparent !important;
}



/* ######## Barre superieur ########## */

#info {
background-image: url("http://s4.noelshack.com/old/up/9c2d6e61df84c10082e6ca567971f6-244c86b932.png") !important;
}


#boxBG {
    background: none repeat-x scroll 0 0 transparent !important;
}


/* ##### Patch note ##### */
#changelog_link {
top: 105px !important;
left: 36px !important;
border: 0px #5F5F5F solid !important;
background: none !important;
z-index: 1000;
color: transparent !important;
}


#siteFooter {
background: none !important;
}

/* ##### Menu galaxie ##### */
#galaxyContent {
background-image: url(http://s4.noelshack.com/old/up/gala_con-d677e82614.png) !important;
}

#galaxy #galaxytable tr.row {
    background: none no-repeat scroll 0 0 transparent !important;
}

/* ######## Opacity ########## */

#overview #planet, #resources #planet, #resourceSettings #planet, #station #planet {
background-repeat: no-repeat; opacity : 0.9 !important;
}

#fleet1 .allornonewrap {
background: none repeat scroll 0 0 #13181D; opacity : 0.9 !important;
}

#links .leftmenu li {
background: none no-repeat scroll left top transparent; opacity : 0.93 !important;
}

.content-box-s {
background: none no-repeat scroll 0 0 transparent; opacity : 0.9 !important;
}

table#IFC_table {
background: none no-repeat scroll 0 0 transparent; opacity : 0.9 !important;
}

#eventHeader {
    background: none no-repeat scroll 0 0 transparent; opacity : 0.9 !important;
}

table#eventContent {
    background: none repeat-y scroll 5px 0 transparent; opacity : 0.9 !important;
}

#resources #buttonz {
    background-image: none; opacity : 0.9 !important;
}

#station #buttonz, #station-moon #buttonz {
    background-image: none; opacity : 0.9 !important;
}

#buttonz.wrapButtons {
