#resourceSettings #planet.shortHeader {
    background: none no-repeat scroll 0 0 transparent; opacity : 0.9 !important;
}

#fleet1 #warning, #fleet2 #warning, #movement #warning {
    background: none no-repeat scroll 0 0 transparent; opacity : 0.9 !important;
}

#fleet3 #sendfleet {
    background: none no-repeat scroll 0 0 transparent; opacity : 0.9 !important;
}

.fleetDetails {
    background: none repeat scroll 0 0 ; opacity : 0.9 !important;
}

span#newDivIFC {
    background: none no-repeat scroll 0 0 transparent; opacity : 0.9 !important;
}

#slPanel {
    background-image: none; opacity : 0.9 !important;
}

#highscore .contentbox div.header, #highscore .contentbox div.content, #highscore .contentbox div.footer {
    background-image: none; opacity : 0.9 !important;
}

#eventFooter {
    background: none no-repeat scroll 2px 0 transparent; opacity : 0.9 !important;
}

